schema:
{
 "description": "SiteSpec defines the desired state of Site",
 "properties": {
  "aclDefaultPolicy": {
   "enum": [
    "permit",
    "deny"
   ],
   "type": "string"
  },
  "publicAsn": {
   "maximum": 65534,
   "minimum": 0,
   "type": "integer"
  },
  "rohAsn": {
   "maximum": 65534,
   "minimum": 0,
   "type": "integer"
  },
  "rohRoutingProfile": {
   "enum": [
    "default",
    "default_agg",
    "full"
   ],
   "type": "string"
  },
  "siteMesh": {
   "enum": [
    "disabled",
    "hub",
    "spoke",
    "dspoke"
   ],
   "type": "string"
  },
  "vmAsn": {
   "maximum": 65534,
   "minimum": 0,
   "type": "integer"
  }
 },
 "required": [
  "aclDefaultPolicy",
  "publicAsn",
  "rohAsn",
  "rohRoutingProfile",
  "siteMesh",
  "vmAsn"
 ],
 "title": "Site",
 "type": "object"
}
