schema:
{
 "description": "Switch is the Schema for the switches API",
 "properties": {
  "spec": {
   "description": "SwitchSpec defines the desired state of Switch",
   "properties": {
    "asn": {
     "type": "integer"
    },
    "description": {
     "type": "string"
    },
    "macAddress": {
     "pattern": "^([0-9A-Fa-f]{2}[:-]){5}([0-9A-Fa-f]{2})$",
     "type": "string"
    },
    "mainIp": {
     "pattern": "^(([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])\\.){3}([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])$",
     "type": "string"
    },
    "mgmtIp": {
     "pattern": "^(([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])\\.){3}([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])$",
     "type": "string"
    },
    "nos": {
     "enum": [
      "cumulus_linux",
      "ubuntu_switch_dev",
      "sonic"
     ],
     "type": "string"
    },
    "portsCount": {
     "enum": [
      16,
      32,
      48,
      54,
      56
     ],
     "type": "integer"
    },
    "profile": {
     "type": "string"
    },
    "site": {
     "type": "string"
    },
    "tenant": {
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "Switch",
 "type": "object"
}
