schema:
{
 "description": "NgrokSpec defines the desired state of Ngrok",
 "properties": {
  "auth": {
   "type": "string"
  },
  "authtoken": {
   "type": "string"
  },
  "authtoken_type": {
   "default": "plain",
   "enum": [
    "plain",
    "secret"
   ],
   "type": "string"
  },
  "bind_tls": {
   "enum": [
    "true",
    "false",
    "both"
   ],
   "type": "string"
  },
  "host_header": {
   "type": "string"
  },
  "hostname": {
   "type": "string"
  },
  "inspect": {
   "default": false,
   "enum": [
    true,
    false
   ],
   "type": "boolean"
  },
  "podSpec": {
   "default": {
    "image": "zufardhiyaulhaq/ngrok"
   },
   "properties": {
    "image": {
     "type": "string"
    }
   },
   "type": "object"
  },
  "port": {
   "format": "int32",
   "maximum": 65535,
   "minimum": 1,
   "type": "integer"
  },
  "protocol": {
   "default": "http",
   "enum": [
    "http",
    "tcp",
    "tls"
   ],
   "type": "string"
  },
  "region": {
   "enum": [
    "us",
    "eu",
    "ap",
    "au",
    "sa",
    "jp",
    "in"
   ],
   "type": "string"
  },
  "remote_addr": {
   "type": "string"
  },
  "service": {
   "type": "string"
  }
 },
 "required": [
  "port",
  "service"
 ],
 "title": "Ngrok",
 "type": "object"
}
