schema:
{
 "description": "ClusterFederatedTrustDomainSpec defines the desired state of ClusterFederatedTrustDomain",
 "properties": {
  "bundleEndpointProfile": {
   "description": "BundleEndpointProfile is the profile for the bundle endpoint.",
   "properties": {
    "endpointSPIFFEID": {
     "description": "EndpointSPIFFEID is the SPIFFE ID of the bundle endpoint. It is required for the \"https_spiffe\" profile.",
     "type": "string"
    },
    "type": {
     "description": "Type is the type of the bundle endpoint profile.",
     "enum": [
      "https_spiffe",
      "https_web"
     ],
     "type": "string"
    }
   },
   "required": [
    "type"
   ],
   "type": "object"
  },
  "bundleEndpointURL": {
   "description": "BundleEndpointURL is the URL of the bundle endpoint. It must be an HTTPS URL and cannot contain userinfo (i.e. username/password).",
   "type": "string"
  },
  "trustDomain": {
   "description": "TrustDomain is the name of the trust domain to federate with (e.g. example.org)",
   "pattern": "[a-z0-9._-]{1,255}",
   "type": "string"
  },
  "trustDomainBundle": {
   "description": "TrustDomainBundle is the contents of the bundle for the referenced trust domain. This field is optional when the resource is created.",
   "type": "string"
  }
 },
 "required": [
  "bundleEndpointProfile",
  "bundleEndpointURL",
  "trustDomain"
 ],
 "title": "Cluster Federated Trust Domain",
 "type": "object"
}
