schema:
{
 "description": "ClusterSPIFFEIDSpec defines the desired state of ClusterSPIFFEID",
 "properties": {
  "admin": {
   "description": "Admin indicates whether or not the SVID can be used to access the SPIRE administrative APIs. Extra care should be taken to only apply this SPIFFE ID to admin workloads.",
   "type": "boolean"
  },
  "dnsNameTemplates": {
   "description": "DNSNameTemplate represents templates for extra DNS names that are applicable to SVIDs minted for this ClusterSPIFFEID. The node and pod spec are made available to the template under .NodeSpec, .PodSpec respectively.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "federatesWith": {
   "description": "FederatesWith is a list of trust domain names that workloads that obtain this SPIFFE ID will federate with.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "namespaceSelector": {
   "description": "NamespaceSelector selects the namespaces that are targetted by this CRD.",
   "properties": {
    "matchExpressions": {
     "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
     "items": {
      "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
      "properties": {
       "key": {
        "description": "key is the label key that the selector applies to.",
        "type": "string"
       },
       "operator": {
        "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
        "type": "string"
       },
       "values": {
        "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "key",
       "operator"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "matchLabels": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
     "type": "object"
    }
   },
   "type": "object"
  },
  "podSelector": {
   "description": "PodSelector selects the pods that are targetted by this CRD.",
   "properties": {
    "matchExpressions": {
     "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
     "items": {
      "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
      "properties": {
       "key": {
        "description": "key is the label key that the selector applies to.",
        "type": "string"
       },
       "operator": {
        "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
        "type": "string"
       },
       "values": {
        "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "key",
       "operator"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "matchLabels": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
     "type": "object"
    }
   },
   "type": "object"
  },
  "spiffeIDTemplate": {
   "description": "SPIFFEID is the SPIFFE ID template. The node and pod spec are made available to the template under .NodeSpec, .PodSpec respectively.",
   "type": "string"
  },
  "ttl": {
   "description": "TTL indicates an upper-bound time-to-live for SVIDs minted for this ClusterSPIFFEID. If unset, a default will be chosen.",
   "type": "string"
  },
  "workloadSelectorTemplates": {
   "description": "WorkloadSelectorTemplates are templates to produce arbitrary workload selectors that apply to a given workload before it will receive this SPIFFE ID. The rendered value is interpreted by SPIRE and are of the form type:value, where the value may, and often does, contain semicolons, .e.g., k8s:container-image:docker/hello-world The node and pod spec are made available to the template under .NodeSpec, .PodSpec respectively.",
   "items": {
    "type": "string"
   },
   "type": "array"
  }
 },
 "required": [
  "spiffeIDTemplate"
 ],
 "title": "Cluster SPIFFEID",
 "type": "object"
}
