schema:
{
 "description": "PulsarConnectionSpec defines the desired state of PulsarConnection",
 "properties": {
  "adminServiceURL": {
   "description": "AdminServiceURL is the admin service url of the pulsar cluster",
   "pattern": "^https?://.+$",
   "type": "string"
  },
  "authentication": {
   "description": "Authentication defines authentication configurations",
   "properties": {
    "oauth2": {
     "description": "PulsarAuthenticationOAuth2 indicates the parameters which are need by pulsar OAuth2",
     "properties": {
      "audience": {
       "type": "string"
      },
      "clientID": {
       "type": "string"
      },
      "issuerEndpoint": {
       "type": "string"
      },
      "key": {
       "description": "ValueOrSecretRef is a string or a secret reference of the authentication",
       "properties": {
        "secretRef": {
         "description": "SecretKeyRef indicates a secret name and key",
         "properties": {
          "key": {
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "required": [
          "key",
          "name"
         ],
         "type": "object"
        },
        "value": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "audience",
      "clientID",
      "issuerEndpoint",
      "key"
     ],
     "type": "object"
    },
    "token": {
     "description": "ValueOrSecretRef is a string or a secret reference of the authentication",
     "properties": {
      "secretRef": {
       "description": "SecretKeyRef indicates a secret name and key",
       "properties": {
        "key": {
         "type": "string"
        },
        "name": {
         "type": "string"
        }
       },
       "required": [
        "key",
        "name"
       ],
       "type": "object"
      },
      "value": {
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "required": [
  "adminServiceURL"
 ],
 "title": "Pulsar Connection",
 "type": "object"
}
