schema:
{
 "description": "SealedSecretSpec defines the desired state of SealedSecret",
 "properties": {
  "data": {
   "additionalProperties": {
    "format": "byte",
    "type": "string"
   },
   "description": "Data contains the secret data. Each key must consist of alphanumeric characters, '-', '_' or '.'. The serialized form of the secret data is a base64 encoded string, representing the arbitrary (possibly non-string) data value here. Described in https://tools.ietf.org/html/rfc4648#section-4",
   "type": "object"
  },
  "encryptedData": {
   "additionalProperties": {
    "description": "Envelopes array of Envelopes, which allows support for encrypting a single entry with multiple providers. During decryption, the controller will stop on the first successful provider and use the decrypted value it returns.",
    "items": {
     "description": "Envelope contains encrypted payload as well as key material and metadata needed to decrypt that payload. \n As of now, we banned any public key cryptograhy (PKI) support, as all available PKI schemes are not quantum-safe.",
     "properties": {
      "awsKms": {
       "description": "AwsKms provider.",
       "properties": {
        "encryptionAlgorithm": {
         "description": "EncryptionAlgorithm specifies the encryption algorithm that will be used to decrypt. https://docs.aws.amazon.com/kms/latest/APIReference/API_Decrypt.html",
         "type": "string"
        },
        "nonce": {
         "description": "Nonce of stream cipher",
         "format": "byte",
         "type": "string"
        },
        "version": {
         "description": "Version of the provider spec",
         "format": "int32",
         "type": "integer"
        }
       },
       "type": "object"
      },
      "cipherText": {
       "description": "CipherText holds the encrypted payload, encrypted by the symmetric DataKey.",
       "format": "byte",
       "type": "string"
      },
      "dataEncryptionKey": {
       "description": "DataEncryptionKey holds the encrypted symmetric data-key, needed to decrypt the CipherText. The data-key is either encrypted using envelope encryption provided by the Provider.",
       "format": "byte",
       "type": "string"
      },
      "keyEncryptionKeyId": {
       "description": "KeyEncryptionKeyId is the provider specific unique ID of the Key Encryption Key (KEK) use to encrypt/decrypt the Data Encryption Key (DEK). \n This ID must uniquely identify the KEK and provider and is used in authorization rules to decide which namespaces can access which KEKs.",
       "type": "string"
      }
     },
     "type": "object"
    },
    "type": "array"
   },
   "description": "EncryptedData contains the secret envelope encrypted secret data. Each key must consist of alphanumeric characters, '-', '_' or '.'. The values are arrays of Envelopes, which allows support for encrypting a single entry with multiple providers. During decryption, the controller will stop on the first successful provider and use the decrypted value it returns.",
   "type": "object"
  },
  "immutable": {
   "description": "Immutable, if set to true, ensures that data stored in the Secret cannot be updated (only object metadata can be modified). If not set to true, the field can be modified at any time. Defaulted to nil. This is a beta field enabled by ImmutableEphemeralVolumes feature gate.",
   "type": "boolean"
  },
  "metadata": {
   "description": "Metadata to use to create the Secret. For example to create it in with a different name or to add labes and annotations. \n Note, any namespace defined here will be ignored, an the corresponding secret will always be created in the namespace of the SealedSecret.",
   "type": "object",
   "x-kubernetes-preserve-unknown-fields": true
  },
  "stringData": {
   "additionalProperties": {
    "type": "string"
   },
   "description": "StringData allows specifying non-binary secret data in string form. It is provided as a write-only convenience method. All keys and values are merged into the data field on write, overwriting any existing values. It is never output when reading from the API.",
   "type": "object"
  },
  "type": {
   "description": "Used to facilitate programmatic handling of secret data.",
   "type": "string"
  }
 },
 "title": "Sealed Secret",
 "type": "object"
}
