schema:
{
 "description": "Desired state of the CertificateRequest resource.",
 "properties": {
  "duration": {
   "description": "The requested 'duration' (i.e. lifetime) of the Certificate. This option may be ignored/overridden by some issuer types.",
   "type": "string"
  },
  "extra": {
   "additionalProperties": {
    "items": {
     "type": "string"
    },
    "type": "array"
   },
   "description": "Extra contains extra attributes of the user that created the CertificateRequest. Populated by the cert-manager webhook on creation and immutable.",
   "type": "object"
  },
  "groups": {
   "description": "Groups contains group membership of the user that created the CertificateRequest. Populated by the cert-manager webhook on creation and immutable.",
   "items": {
    "type": "string"
   },
   "type": "array",
   "x-kubernetes-list-type": "atomic"
  },
  "isCA": {
   "description": "IsCA will request to mark the certificate as valid for certificate signing when submitting to the issuer. This will automatically add the `cert sign` usage to the list of `usages`.",
   "type": "boolean"
  },
  "issuerRef": {
   "description": "IssuerRef is a reference to the issuer for this CertificateRequest.  If the `kind` field is not set, or set to `Issuer`, an Issuer resource with the given name in the same namespace as the CertificateRequest will be used.  If the `kind` field is set to `ClusterIssuer`, a ClusterIssuer with the provided name will be used. The `name` field in this stanza is required at all times. The group field refers to the API group of the issuer which defaults to `cert-manager.io` if empty.",
   "properties": {
    "group": {
     "description": "Group of the resource being referred to.",
     "type": "string"
    },
    "kind": {
     "description": "Kind of the resource being referred to.",
     "type": "string"
    },
    "name": {
     "description": "Name of the resource being referred to.",
     "type": "string"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  },
  "request": {
   "description": "The PEM-encoded x509 certificate signing request to be submitted to the CA for signing.",
   "format": "byte",
   "type": "string"
  },
  "uid": {
   "description": "UID contains the uid of the user that created the CertificateRequest. Populated by the cert-manager webhook on creation and immutable.",
   "type": "string"
  },
  "usages": {
   "description": "Usages is the set of x509 usages that are requested for the certificate. If usages are set they SHOULD be encoded inside the CSR spec Defaults to `digital signature` and `key encipherment` if not specified.",
   "items": {
    "description": "KeyUsage specifies valid usage contexts for keys. See: https://tools.ietf.org/html/rfc5280#section-4.2.1.3 https://tools.ietf.org/html/rfc5280#section-4.2.1.12 \n Valid KeyUsage values are as follows: \"signing\", \"digital signature\", \"content commitment\", \"key encipherment\", \"key agreement\", \"data encipherment\", \"cert sign\", \"crl sign\", \"encipher only\", \"decipher only\", \"any\", \"server auth\", \"client auth\", \"code signing\", \"email protection\", \"s/mime\", \"ipsec end system\", \"ipsec tunnel\", \"ipsec user\", \"timestamping\", \"ocsp signing\", \"microsoft sgc\", \"netscape sgc\"",
    "enum": [
     "signing",
     "digital signature",
     "content commitment",
     "key encipherment",
     "key agreement",
     "data encipherment",
     "cert sign",
     "crl sign",
     "encipher only",
     "decipher only",
     "any",
     "server auth",
     "client auth",
     "code signing",
     "email protection",
     "s/mime",
     "ipsec end system",
     "ipsec tunnel",
     "ipsec user",
     "timestamping",
     "ocsp signing",
     "microsoft sgc",
     "netscape sgc"
    ],
    "type": "string"
   },
   "type": "array"
  },
  "username": {
   "description": "Username contains the name of the user that created the CertificateRequest. Populated by the cert-manager webhook on creation and immutable.",
   "type": "string"
  }
 },
 "required": [
  "issuerRef",
  "request"
 ],
 "title": "Certificate Request",
 "type": "object"
}
