schema:
{
 "description": "SonarPermissionTemplateSpec defines the desired state of SonarPermissionTemplate.",
 "properties": {
  "description": {
   "description": "Description of sonar permission template.",
   "type": "string"
  },
  "groupPermissions": {
   "description": "GroupPermissions adds a group to a permission template.",
   "items": {
    "description": "GroupPermission represents the group and its permissions.",
    "properties": {
     "groupName": {
      "description": "Group name or 'anyone' (case insensitive). Example value sonar-administrators.",
      "type": "string"
     },
     "permissions": {
      "description": "Permissions is a list of permissions. Possible values: admin, codeviewer, issueadmin, securityhotspotadmin, scan, user.",
      "items": {
       "type": "string"
      },
      "type": "array"
     }
    },
    "required": [
     "groupName",
     "permissions"
    ],
    "type": "object"
   },
   "nullable": true,
   "type": "array"
  },
  "name": {
   "description": "Name is a group name.",
   "type": "string"
  },
  "projectKeyPattern": {
   "description": "ProjectKeyPattern is key pattern. Must be a valid Java regular expression.",
   "type": "string"
  },
  "sonarOwner": {
   "description": "SonarOwner is a name of root sonar custom resource.",
   "type": "string"
  }
 },
 "required": [
  "name",
  "projectKeyPattern",
  "sonarOwner"
 ],
 "title": "Sonar Permission Template",
 "type": "object"
}
