schema:
{
 "properties": {
  "cpu": {
   "description": "CPU(s) (cores) to use for every instance of a SGCluster. Please note that every StackGres pod contains not only the Patroni+Postgres container, but several other sidecar containers. While the majority of the resources are devoted to the main Postgres container, some CPU is needed for the sidecars.\n\nThe specified number of cores will be split between all the containers.\n\nA minimum of 2 cores is recommended.\n",
   "pattern": "^[1-9][0-9]*[m]?$",
   "type": "string"
  },
  "memory": {
   "description": "RAM allocated to every instance of a SGCluster. Use the suffix `Mi` or `Gi` to specify Mebibytes or Gibibytes, respectively. Please note that every StackGres pod contains not only the Patroni+Postgres container, but several other sidecar containers. While the majority of the resources are devoted to the main Postgres container, some RAM is needed for the sidecars.\n\nThe specified amount of RAM will be split between all the containers.\n\nA minimum of 2-4Gi is recommended.\n",
   "pattern": "^[0-9]+(\\.[0-9]+)?(Mi|Gi)$",
   "type": "string"
  }
 },
 "required": [
  "cpu",
  "memory"
 ],
 "title": "SG Instance Profile",
 "type": "object"
}
