schema:
{
 "description": "QuoteAttestationSpec defines the desired state of QuoteAttestation",
 "properties": {
  "publicKey": {
   "description": "PublicKey for encrypting the secret, hash is part of the quote data, base-64 encoded.",
   "format": "byte",
   "type": "string"
  },
  "quote": {
   "description": "Quote to be verified, base64-encoded.",
   "format": "byte",
   "type": "string"
  },
  "quoteVersion": {
   "description": "QuoteVersion used to for generated quote, default is ECDSA quote \"3\"",
   "type": "string"
  },
  "secretName": {
   "description": "SecretName is name of the Secret object (in the same namespace) to keep the wrapped on secrets (only needed for KeyProvisioning request type) which is an opaque type. The secret data must contain two map elements `tls.key` and `tls.cert` and the values are the base64 encoded encrypted CA key and base64 encoded x509(PEM encoded) certificate. This must be added only after a successful quote validation and before updating the status condition.",
   "type": "string"
  },
  "serviceId": {
   "description": "ServiceID holds the unique identifier(name?) that represents service which is requesting for the secret. To be decided whether this should be SPIFFE trust domain!",
   "type": "string"
  },
  "signerName": {
   "description": "SignerName refers to the Kubernetes CSR signer name used by this request.",
   "type": "string"
  },
  "type": {
   "description": "Type represents the type of the request, one of \"QuoteAttestation\", \"KeyProvisioning\".",
   "enum": [
    "QuoteAttestation",
    "KeyProvisioning"
   ],
   "type": "string"
  }
 },
 "required": [
  "publicKey",
  "quote",
  "serviceId",
  "signerName",
  "type"
 ],
 "title": "Quote Attestation",
 "type": "object"
}
