schema:
{
 "description": "AlertSpec defines an alerting rule for events involving a list of objects",
 "properties": {
  "eventSeverity": {
   "default": "info",
   "description": "Filter events based on severity, defaults to ('info'). If set to 'info' no events will be filtered.",
   "enum": [
    "info",
    "error"
   ],
   "type": "string"
  },
  "eventSources": {
   "description": "Filter events based on the involved objects.",
   "items": {
    "description": "CrossNamespaceObjectReference contains enough information to let you locate the typed referenced object at cluster level",
    "properties": {
     "apiVersion": {
      "description": "API version of the referent",
      "type": "string"
     },
     "kind": {
      "description": "Kind of the referent",
      "enum": [
       "Bucket",
       "GitRepository",
       "Kustomization",
       "HelmRelease",
       "HelmChart",
       "HelmRepository",
       "ImageRepository",
       "ImagePolicy",
       "ImageUpdateAutomation",
       "OCIRepository"
      ],
      "type": "string"
     },
     "matchLabels": {
      "additionalProperties": {
       "type": "string"
      },
      "description": "MatchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
      "type": "object"
     },
     "name": {
      "description": "Name of the referent",
      "maxLength": 53,
      "minLength": 1,
      "type": "string"
     },
     "namespace": {
      "description": "Namespace of the referent",
      "maxLength": 53,
      "minLength": 1,
      "type": "string"
     }
    },
    "required": [
     "name"
    ],
    "type": "object"
   },
   "type": "array"
  },
  "exclusionList": {
   "description": "A list of Golang regular expressions to be used for excluding messages.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "providerRef": {
   "description": "Send events using this provider.",
   "properties": {
    "name": {
     "description": "Name of the referent.",
     "type": "string"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  },
  "summary": {
   "description": "Short description of the impact and affected cluster.",
   "type": "string"
  },
  "suspend": {
   "description": "This flag tells the controller to suspend subsequent events dispatching. Defaults to false.",
   "type": "boolean"
  }
 },
 "required": [
  "eventSources",
  "providerRef"
 ],
 "title": "Alert",
 "type": "object"
}
