schema:
{
 "description": "BucketSpec defines the desired state of an S3 compatible bucket",
 "properties": {
  "accessFrom": {
   "description": "AccessFrom defines an Access Control List for allowing cross-namespace references to this object.",
   "properties": {
    "namespaceSelectors": {
     "description": "NamespaceSelectors is the list of namespace selectors to which this ACL applies. Items in this list are evaluated using a logical OR operation.",
     "items": {
      "description": "NamespaceSelector selects the namespaces to which this ACL applies. An empty map of MatchLabels matches all namespaces in a cluster.",
      "properties": {
       "matchLabels": {
        "additionalProperties": {
         "type": "string"
        },
        "description": "MatchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "namespaceSelectors"
   ],
   "type": "object"
  },
  "bucketName": {
   "description": "The bucket name.",
   "type": "string"
  },
  "endpoint": {
   "description": "The bucket endpoint address.",
   "type": "string"
  },
  "ignore": {
   "description": "Ignore overrides the set of excluded patterns in the .sourceignore format (which is the same as .gitignore). If not provided, a default will be used, consult the documentation for your version to find out what those are.",
   "type": "string"
  },
  "insecure": {
   "description": "Insecure allows connecting to a non-TLS S3 HTTP endpoint.",
   "type": "boolean"
  },
  "interval": {
   "description": "The interval at which to check for bucket updates.",
   "type": "string"
  },
  "provider": {
   "default": "generic",
   "description": "The S3 compatible storage provider name, default ('generic').",
   "enum": [
    "generic",
    "aws",
    "gcp"
   ],
   "type": "string"
  },
  "region": {
   "description": "The bucket region.",
   "type": "string"
  },
  "secretRef": {
   "description": "The name of the secret containing authentication credentials for the Bucket.",
   "properties": {
    "name": {
     "description": "Name of the referent.",
     "type": "string"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  },
  "suspend": {
   "description": "This flag tells the controller to suspend the reconciliation of this source.",
   "type": "boolean"
  },
  "timeout": {
   "default": "60s",
   "description": "The timeout for download operations, defaults to 60s.",
   "type": "string"
  }
 },
 "required": [
  "bucketName",
  "endpoint",
  "interval"
 ],
 "title": "Bucket",
 "type": "object"
}
