schema:
{
 "description": "MetricGroup is the Schema for the metricgroups API",
 "properties": {
  "spec": {
   "description": "MetricGroupSpec defines the desired state of MetricGroup",
   "properties": {
    "clusters": {
     "description": "Specifies the name of clusters this metric group will apply. Empty means for all clusters",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "export": {
     "description": "Indicate whether the export function is enabled",
     "type": "boolean"
    },
    "metricType": {
     "description": "Specifies the type of metric this group contains",
     "enum": [
      "VROUTER",
      "CONTROLLER"
     ],
     "type": "string"
    },
    "metrics": {
     "description": "Specifies the name of metrics",
     "items": {
      "type": "string"
     },
     "minItems": 1,
     "type": "array"
    }
   },
   "type": "object"
  }
 },
 "title": "Metric Group",
 "type": "object"
}
