component.schema:
{
 "description": "IAMKey is the API for creating IAM Object Storage Keys on exoscale.com.",
 "properties": {
  "spec": {
   "description": "IAMKeySpec defines the desired state of an IAMKey.",
   "properties": {
    "deletionPolicy": {
     "default": "Delete",
     "description": "DeletionPolicy specifies what will happen to the underlying external\nwhen this managed resource is deleted - either \"Delete\" or \"Orphan\" the\nexternal resource.\nThis field is planned to be deprecated in favor of the ManagementPolicies\nfield in a future release. Currently, both could be set independently and\nnon-default values would be honored if the feature flag is enabled.\nSee the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223",
     "enum": [
      "Orphan",
      "Delete"
     ],
     "type": "string"
    },
    "forProvider": {
     "description": "IAMKeyParameters are the configurable fields of IAMKey.",
     "properties": {
      "keyName": {
       "description": "KeyName is the name of the Key as presented in the exoscale.com UI.\nIf empty, the value of `.metadata.annotations.\"crossplane.io/external-name\"` is used.\nThere can be multiple keys that have the same key name in exoscale.com, but they will have different key IDs.",
       "type": "string"
      },
      "services": {
       "description": "Services is the exoscale service to which IAMKey gets access to.\nOnly object storage (sos) service is supported thus the IAMKey will be restricted to access only sos.",
       "properties": {
        "sos": {
         "description": "SOSSpec is the Object Storage Service in exoscale.",
         "properties": {
          "buckets": {
           "description": "Buckets is a list of buckets to which IAMKey has access to.",
           "items": {
            "type": "string"
           },
           "type": "array"
          }
         },
         "required": [
          "buckets"
         ],
         "type": "object"
        }
       },
       "required": [
        "sos"
       ],
       "type": "object"
      },
      "zone": {
       "description": "Zone is the name of the zone where the IAM key is created.\nThe zone must be available in the S3 endpoint.\nCannot be changed after IAMKey is created.",
       "enum": [
        "ch-gva-2",
        "ch-dk-2",
        "de-fra-1",
        "de-muc-1",
        "at-vie-1",
        "bg-sof-1"
       ],
       "type": "string"
      }
     },
     "required": [
      "services",
      "zone"
     ],
     "type": "object"
    },
    "managementPolicies": {
     "default": [
      "*"
     ],
     "description": "THIS IS A BETA FIELD. It is on by default but can be opted out\nthrough a Crossplane feature flag.\nManagementPolicies specify the array of actions Crossplane is allowed to\ntake on the managed and external resources.\nThis field is planned to replace the DeletionPolicy field in a future\nrelease. Currently, both could be set independently and non-default\nvalues would be honored if the feature flag is enabled. If both are\ncustom, the DeletionPolicy field will be ignored.\nSee the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223\nand this one: https://github.com/crossplane/crossplane/blob/444267e84783136daa93568b364a5f01228cacbe/design/one-pager-ignore-changes.md",
     "items": {
      "description": "A ManagementAction represents an action that the Crossplane controllers\ncan take on an external resource.",
      "enum": [
       "Observe",
       "Create",
       "Update",
       "Delete",
       "LateInitialize",
       "*"
      ],
      "type": "string"
     },
     "type": "array"
    },
    "providerConfigRef": {
     "default": {
      "name": "default"
     },
     "description": "ProviderConfigReference specifies how the provider that will be used to\ncreate, observe, update, and delete this managed resource should be\nconfigured.",
     "properties": {
      "name": {
       "description": "Name of the referenced object.",
       "type": "string"
      },
      "policy": {
       "description": "Policies for referencing.",
       "properties": {
        "resolution": {
         "default": "Required",
         "description": "Resolution specifies whether resolution of this reference is required.\nThe default is 'Required', which means the reconcile will fail if the\nreference cannot be resolved. 'Optional' means this reference will be\na no-op if it cannot be resolved.",
         "enum": [
          "Required",
          "Optional"
         ],
         "type": "string"
        },
        "resolve": {
         "description": "Resolve specifies when this reference should be resolved. The default\nis 'IfNotPresent', which will attempt to resolve the reference only when\nthe corresponding field is not present. Use 'Always' to resolve the\nreference on every reconcile.",
         "enum": [
          "Always",
          "IfNotPresent"
         ],
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "publishConnectionDetailsTo": {
     "description": "PublishConnectionDetailsTo specifies the connection secret config which\ncontains a name, metadata and a reference to secret store config to\nwhich any connection details for this managed resource should be written.\nConnection details frequently include the endpoint, username,\nand password required to connect to the managed resource.",
     "properties": {
      "configRef": {
       "default": {
        "name": "default"
       },
       "description": "SecretStoreConfigRef specifies which secret store config should be used\nfor this ConnectionSecret.",
       "properties": {
        "name": {
         "description": "Name of the referenced object.",
         "type": "string"
        },
        "policy": {
         "description": "Policies for referencing.",
         "properties": {
          "resolution": {
           "default": "Required",
           "description": "Resolution specifies whether resolution of this reference is required.\nThe default is 'Required', which means the reconcile will fail if the\nreference cannot be resolved. 'Optional' means this reference will be\na no-op if it cannot be resolved.",
           "enum": [
            "Required",
            "Optional"
           ],
           "type": "string"
          },
          "resolve": {
           "description": "Resolve specifies when this reference should be resolved. The default\nis 'IfNotPresent', which will attempt to resolve the reference only when\nthe corresponding field is not present. Use 'Always' to resolve the\nreference on every reconcile.",
           "enum": [
            "Always",
            "IfNotPresent"
           ],
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "required": [
        "name"
       ],
       "type": "object"
      },
      "metadata": {
       "description": "Metadata is the metadata for connection secret.",
       "properties": {
        "annotations": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Annotations are the annotations to be added to connection secret.\n- For Kubernetes secrets, this will be used as \"metadata.annotations\".\n- It is up to Secret Store implementation for others store types.",
         "type": "object"
        },
        "labels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Labels are the labels/tags to be added to connection secret.\n- For Kubernetes secrets, this will be used as \"metadata.labels\".\n- It is up to Secret Store implementation for others store types.",
         "type": "object"
        },
        "type": {
         "description": "Type is the SecretType for the connection secret.\n- Only valid for Kubernetes Secret Stores.",
         "type": "string"
        }
       },
       "type": "object"
      },
      "name": {
       "description": "Name is the name of the connection secret.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "writeConnectionSecretToRef": {
     "description": "WriteConnectionSecretToReference specifies the namespace and name of a\nSecret to which any connection details for this managed resource should\nbe written. Connection details frequently include the endpoint, username,\nand password required to connect to the managed resource.\nThis field is planned to be replaced in a future release in favor of\nPublishConnectionDetailsTo. Currently, both could be set independently\nand connection details would be published to both without affecting\neach other.",
     "properties": {
      "name": {
       "description": "Name of the secret.",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the secret.",
       "type": "string"
      }
     },
     "required": [
      "name",
      "namespace"
     ],
     "type": "object"
    }
   },
   "required": [
    "forProvider"
   ],
   "type": "object"
  }
 },
 "required": [
  "spec"
 ],
 "title": "IAM Key",
 "type": "object"
}
