component.schema:
{
 "description": "IkePolicyV2 is the Schema for the IkePolicyV2s API. Manages a V2 Neutron IKE policy resource within OpenStack.",
 "properties": {
  "spec": {
   "description": "IkePolicyV2Spec defines the desired state of IkePolicyV2",
   "properties": {
    "deletionPolicy": {
     "default": "Delete",
     "description": "DeletionPolicy specifies what will happen to the underlying external when this managed resource is deleted - either \"Delete\" or \"Orphan\" the external resource. This field is planned to be deprecated in favor of the ManagementPolicies field in a future release. Currently, both could be set independently and non-default values would be honored if the feature flag is enabled. See the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223",
     "enum": [
      "Orphan",
      "Delete"
     ],
     "type": "string"
    },
    "forProvider": {
     "properties": {
      "authAlgorithm": {
       "description": "The authentication hash algorithm. Valid values are sha1, sha256, sha384, sha512. Default is sha1. Changing this updates the algorithm of the existing policy.",
       "type": "string"
      },
      "description": {
       "description": "The human-readable description for the policy. Changing this updates the description of the existing policy.",
       "type": "string"
      },
      "encryptionAlgorithm": {
       "description": "The encryption algorithm. Valid values are 3des, aes-128, aes-192 and so on. The default value is aes-128. Changing this updates the existing policy.",
       "type": "string"
      },
      "ikeVersion": {
       "description": "The IKE mode. A valid value is v1 or v2. Default is v1. Changing this updates the existing policy.",
       "type": "string"
      },
      "lifetime": {
       "description": "The lifetime of the security association. Consists of Unit and Value.",
       "items": {
        "properties": {
         "units": {
          "type": "string"
         },
         "value": {
          "description": "The value for the lifetime of the security association. Must be a positive integer. Default is 3600.",
          "type": "number"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "name": {
       "description": "The name of the policy. Changing this updates the name of the existing policy.",
       "type": "string"
      },
      "pfs": {
       "description": "The perfect forward secrecy mode. Valid values are Group2, Group5 and Group14. Default is Group5. Changing this updates the existing policy.",
       "type": "string"
      },
      "phase1NegotiationMode": {
       "description": "The IKE mode. A valid value is main, which is the default. Changing this updates the existing policy.",
       "type": "string"
      },
      "region": {
       "description": "The region in which to obtain the V2 Networking client. A Networking client is needed to create a VPN service. If omitted, the region argument of the provider is used. Changing this creates a new service.",
       "type": "string"
      },
      "tenantId": {
       "description": "The owner of the policy. Required if admin wants to create a service for another policy. Changing this creates a new policy.",
       "type": "string"
      },
      "valueSpecs": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Map of additional options.",
       "type": "object"
      }
     },
     "type": "object"
    },
    "initProvider": {
     "description": "THIS IS A BETA FIELD. It will be honored unless the Management Policies feature flag is disabled. InitProvider holds the same fields as ForProvider, with the exception of Identifier and other resource reference fields. The fields that are in InitProvider are merged into ForProvider when the resource is created. The same fields are also added to the terraform ignore_changes hook, to avoid updating them after creation. This is useful for fields that are required on creation, but we do not desire to update them after creation, for example because of an external controller is managing them, like an autoscaler.",
     "properties": {
      "authAlgorithm": {
       "description": "The authentication hash algorithm. Valid values are sha1, sha256, sha384, sha512. Default is sha1. Changing this updates the algorithm of the existing policy.",
       "type": "string"
      },
      "description": {
       "description": "The human-readable description for the policy. Changing this updates the description of the existing policy.",
       "type": "string"
      },
      "encryptionAlgorithm": {
       "description": "The encryption algorithm. Valid values are 3des, aes-128, aes-192 and so on. The default value is aes-128. Changing this updates the existing policy.",
       "type": "string"
      },
      "ikeVersion": {
       "description": "The IKE mode. A valid value is v1 or v2. Default is v1. Changing this updates the existing policy.",
       "type": "string"
      },
      "lifetime": {
       "description": "The lifetime of the security association. Consists of Unit and Value.",
       "items": {
        "properties": {
         "units": {
          "type": "string"
         },
         "value": {
          "description": "The value for the lifetime of the security association. Must be a positive integer. Default is 3600.",
          "type": "number"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "name": {
       "description": "The name of the policy. Changing this updates the name of the existing policy.",
       "type": "string"
      },
      "pfs": {
       "description": "The perfect forward secrecy mode. Valid values are Group2, Group5 and Group14. Default is Group5. Changing this updates the existing policy.",
       "type": "string"
      },
      "phase1NegotiationMode": {
       "description": "The IKE mode. A valid value is main, which is the default. Changing this updates the existing policy.",
       "type": "string"
      },
      "region": {
       "description": "The region in which to obtain the V2 Networking client. A Networking client is needed to create a VPN service. If omitted, the region argument of the provider is used. Changing this creates a new service.",
       "type": "string"
      },
      "tenantId": {
       "description": "The owner of the policy. Required if admin wants to create a service for another policy. Changing this creates a new policy.",
       "type": "string"
      },
      "valueSpecs": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Map of additional options.",
       "type": "object"
      }
     },
     "type": "object"
    },
    "managementPolicies": {
     "default": [
      "*"
     ],
     "description": "THIS IS A BETA FIELD. It is on by default but can be opted out through a Crossplane feature flag. ManagementPolicies specify the array of actions Crossplane is allowed to take on the managed and external resources. This field is planned to replace the DeletionPolicy field in a future release. Currently, both could be set independently and non-default values would be honored if the feature flag is enabled. If both are custom, the DeletionPolicy field will be ignored. See the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223 and this one: https://github.com/crossplane/crossplane/blob/444267e84783136daa93568b364a5f01228cacbe/design/one-pager-ignore-changes.md",
     "items": {
      "description": "A ManagementAction represents an action that the Crossplane controllers can take on an external resource.",
      "enum": [
       "Observe",
       "Create",
       "Update",
       "Delete",
       "LateInitialize",
       "*"
      ],
      "type": "string"
     },
     "type": "array"
    },
    "providerConfigRef": {
     "default": {
      "name": "default"
     },
     "description": "ProviderConfigReference specifies how the provider that will be used to create, observe, update, and delete this managed resource should be configured.",
     "properties": {
      "name": {
       "description": "Name of the referenced object.",
       "type": "string"
      },
      "policy": {
       "description": "Policies for referencing.",
       "properties": {
        "resolution": {
         "default": "Required",
         "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
         "enum": [
          "Required",
          "Optional"
         ],
         "type": "string"
        },
        "resolve": {
         "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
         "enum": [
          "Always",
          "IfNotPresent"
         ],
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "publishConnectionDetailsTo": {
     "description": "PublishConnectionDetailsTo specifies the connection secret config which contains a name, metadata and a reference to secret store config to which any connection details for this managed resource should be written. Connection details frequently include the endpoint, username, and password required to connect to the managed resource.",
     "properties": {
      "configRef": {
       "default": {
        "name": "default"
       },
       "description": "SecretStoreConfigRef specifies which secret store config should be used for this ConnectionSecret.",
       "properties": {
        "name": {
         "description": "Name of the referenced object.",
         "type": "string"
        },
        "policy": {
         "description": "Policies for referencing.",
         "properties": {
          "resolution": {
           "default": "Required",
           "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
           "enum": [
            "Required",
            "Optional"
           ],
           "type": "string"
          },
          "resolve": {
           "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
           "enum": [
            "Always",
            "IfNotPresent"
           ],
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "required": [
        "name"
       ],
       "type": "object"
      },
      "metadata": {
       "description": "Metadata is the metadata for connection secret.",
       "properties": {
        "annotations": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Annotations are the annotations to be added to connection secret. - For Kubernetes secrets, this will be used as \"metadata.annotations\". - It is up to Secret Store implementation for others store types.",
         "type": "object"
        },
        "labels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Labels are the labels/tags to be added to connection secret. - For Kubernetes secrets, this will be used as \"metadata.labels\". - It is up to Secret Store implementation for others store types.",
         "type": "object"
        },
        "type": {
         "description": "Type is the SecretType for the connection secret. - Only valid for Kubernetes Secret Stores.",
         "type": "string"
        }
       },
       "type": "object"
      },
      "name": {
       "description": "Name is the name of the connection secret.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "writeConnectionSecretToRef": {
     "description": "WriteConnectionSecretToReference specifies the namespace and name of a Secret to which any connection details for this managed resource should be written. Connection details frequently include the endpoint, username, and password required to connect to the managed resource. This field is planned to be replaced in a future release in favor of PublishConnectionDetailsTo. Currently, both could be set independently and connection details would be published to both without affecting each other.",
     "properties": {
      "name": {
       "description": "Name of the secret.",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the secret.",
       "type": "string"
      }
     },
     "required": [
      "name",
      "namespace"
     ],
     "type": "object"
    }
   },
   "required": [
    "forProvider"
   ],
   "type": "object"
  }
 },
 "required": [
  "spec"
 ],
 "title": "Ike Policy V2",
 "type": "object"
}
