{
 "description": "The `PodMonitor` custom resource definition (CRD) defines how `Prometheus` and `PrometheusAgent` can scrape metrics from a group of pods.\nAmong other things, it allows to specify:\n* The pods to scrape via label selectors.\n* The container ports to scrape.\n* Authentication credentials to use.\n* Target and metric relabeling.\n\n`Prometheus` and `PrometheusAgent` objects select `PodMonitor` objects using label and namespace selectors.",
 "properties": {
  "spec": {
   "description": "spec defines the specification of desired Pod selection for target discovery by Prometheus.",
   "properties": {
    "attachMetadata": {
     "description": "attachMetadata defines additional metadata which is added to the\ndiscovered targets.\n\nIt requires Prometheus \u003e= v2.35.0.",
     "properties": {
      "node": {
       "description": "node when set to true, Prometheus attaches node metadata to the discovered\ntargets.\n\nThe Prometheus service account must have the `list` and `watch`\npermissions on the `Nodes` objects.",
       "type": "boolean"
      }
     },
     "type": "object"
    },
    "bodySizeLimit": {
     "description": "bodySizeLimit when defined specifies a job level limit on the size\nof uncompressed response body that will be accepted by Prometheus.\n\nIt requires Prometheus \u003e= v2.28.0.",
     "pattern": "(^0|([0-9]*[.])?[0-9]+((K|M|G|T|E|P)i?)?B)$",
     "type": "string"
    },
    "convertClassicHistogramsToNHCB": {
     "description": "convertClassicHistogramsToNHCB defines whether to convert all scraped classic histograms into a native histogram with custom buckets.\nIt requires Prometheus \u003e= v3.0.0.",
     "type": "boolean"
    },
    "fallbackScrapeProtocol": {
     "description": "fallbackScrapeProtocol defines the protocol to use if a scrape returns blank, unparseable, or otherwise invalid Content-Type.\n\nIt requires Prometheus \u003e= v3.0.0.",
     "enum": [
      "PrometheusProto",
      "OpenMetricsText0.0.1",
      "OpenMetricsText1.0.0",
      "PrometheusText0.0.4",
      "PrometheusText1.0.0"
     ],
     "type": "string"
    },
    "jobLabel": {
     "description": "jobLabel defines the label to use to retrieve the job name from.\n`jobLabel` selects the label from the associated Kubernetes `Pod`\nobject which will be used as the `job` label for all metrics.\n\nFor example if `jobLabel` is set to `foo` and the Kubernetes `Pod`\nobject is labeled with `foo: bar`, then Prometheus adds the `job=\"bar\"`\nlabel to all ingested metrics.\n\nIf the value of this field is empty, the `job` label of the metrics\ndefaults to the namespace and name of the PodMonitor object (e.g. `\u003cnamespace\u003e/\u003cname\u003e`).",
     "type": "string"
    },
    "keepDroppedTargets": {
     "description": "keepDroppedTargets defines the per-scrape limit on the number of targets dropped by relabeling\nthat will be kept in memory. 0 means no limit.\n\nIt requires Prometheus \u003e= v2.47.0.",
     "format": "int64",
     "type": "integer"
    },
    "labelLimit": {
     "description": "labelLimit defines the per-scrape limit on number of labels that will be accepted for a sample.\n\nIt requires Prometheus \u003e= v2.27.0.",
     "format": "int64",
     "type": "integer"
    },
    "labelNameLengthLimit": {
     "description": "labelNameLengthLimit defines the per-scrape limit on length of labels name that will be accepted for a sample.\n\nIt requires Prometheus \u003e= v2.27.0.",
     "format": "int64",
     "type": "integer"
    },
    "labelValueLengthLimit": {
     "description": "labelValueLengthLimit defines the per-scrape limit on length of labels value that will be accepted for a sample.\n\nIt requires Prometheus \u003e= v2.27.0.",
     "format": "int64",
     "type": "integer"
    },
    "namespaceSelector": {
     "description": "namespaceSelector defines in which namespace(s) Prometheus should discover the pods.\nBy default, the pods are discovered in the same namespace as the `PodMonitor` object but it is possible to select pods across different/all namespaces.",
     "properties": {
      "any": {
       "description": "any defines the boolean describing whether all namespaces are selected in contrast to a\nlist restricting them.",
       "type": "boolean"
      },
      "matchNames": {
       "description": "matchNames defines the list of namespace names to select from.",
       "items": {
        "type": "string"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "nativeHistogramBucketLimit": {
     "description": "nativeHistogramBucketLimit defines ff there are more than this many buckets in a native histogram,\nbuckets will be merged to stay within the limit.\nIt requires Prometheus \u003e= v2.45.0.",
     "format": "int64",
     "type": "integer"
    },
    "nativeHistogramMinBucketFactor": {
     "anyOf": [
      {
       "type": "integer"
      },
      {
       "type": "string"
      }
     ],
     "description": "nativeHistogramMinBucketFactor defines if the growth factor of one bucket to the next is smaller than this,\nbuckets will be merged to increase the factor sufficiently.\nIt requires Prometheus \u003e= v2.50.0.",
     "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
