schema:
{
 "description": "Component describes an Dapr component type.",
 "properties": {
  "auth": {
   "description": "Auth represents authentication details for the component.",
   "properties": {
    "secretStore": {
     "type": "string"
    }
   },
   "required": [
    "secretStore"
   ],
   "type": "object"
  },
  "scopes": {
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "spec": {
   "description": "ComponentSpec is the spec for a component.",
   "properties": {
    "ignoreErrors": {
     "type": "boolean"
    },
    "initTimeout": {
     "type": "string"
    },
    "metadata": {
     "items": {
      "description": "NameValuePair is a name/value pair.",
      "properties": {
       "envRef": {
        "description": "EnvRef is the name of an environmental variable to read the value from.",
        "type": "string"
       },
       "name": {
        "description": "Name of the property.",
        "type": "string"
       },
       "secretKeyRef": {
        "description": "SecretKeyRef is the reference of a value in a secret store component.",
        "properties": {
         "key": {
          "description": "Field in the secret.",
          "type": "string"
         },
         "name": {
          "description": "Secret name.",
          "type": "string"
         }
        },
        "required": [
         "name"
        ],
        "type": "object"
       },
       "value": {
        "description": "Value of the property, in plaintext.",
        "format": "textarea",
        "type": "string"
       }
      },
      "required": [
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "type": {
     "type": "string"
    },
    "version": {
     "type": "string"
    }
   },
   "required": [
    "metadata",
    "type",
    "version"
   ],
   "type": "object"
  }
 },
 "title": "Component",
 "type": "object"
}
