           "attributes": {
            "additionalProperties": {
             "type": "string"
            },
            "description": "Optional map of free-form additional command attributes",
            "type": "object"
           },
           "commandLine": {
            "description": "The actual command-line string \n Special variables that can be used: \n  - `$PROJECTS_ROOT`: A path where projects sources are mounted \n  - `$PROJECT_SOURCE`: A path to a project source ($PROJECTS_ROOT/\u003cproject-name\u003e). If there are multiple projects, this will point to the directory of the first one.",
            "type": "string"
           },
           "component": {
            "description": "Describes component to which given action relates",
            "type": "string"
           },
           "env": {
            "description": "Optional list of environment variables that have to be set before running the command",
            "items": {
             "properties": {
              "name": {
               "type": "string"
              },
              "value": {
               "type": "string"
              }
             },
             "required": [
              "name",
              "value"
             ],
             "type": "object"
            },
            "type": "array"
           },
           "group": {
            "description": "Defines the group this command is part of",
            "properties": {
             "isDefault": {
              "description": "Identifies the default command for a given group kind",
              "type": "boolean"
             },
             "kind": {
              "description": "Kind of group the command is part of",
              "enum": [
               "build",
               "run",
               "test",
               "debug"
              ],
              "type": "string"
             }
            },
            "required": [
             "kind"
            ],
            "type": "object"
           },
           "hotReloadCapable": {
            "description": "Specify whether the command is restarted or not when the source code changes. If set to `true` the command won't be restarted. A *hotReloadCapable* `run` or `debug` command is expected to handle file changes on its own and won't be restarted. A *hotReloadCapable* `build` command is expected to be executed only once and won't be executed again. This field is taken into account only for commands `build`, `run` and `debug` with `isDefault` set to `true`. \n Default value is `false`",
            "type": "boolean"
           },
           "id": {
            "description": "Mandatory identifier that allows referencing this command in composite commands, from a parent, or in events.",
            "type": "string"
           },
           "label": {
            "description": "Optional label that provides a label for this command to be used in Editor UI menus for example",
            "type": "string"
           },
           "workingDir": {
            "description": "Working directory where the command should be executed \n Special variables that can be used: \n  - `${PROJECTS_ROOT}`: A path where projects sources are mounted \n  - `${PROJECT_SOURCE}`: A path to a project source (${PROJECTS_ROOT}/\u003cproject-name\u003e). If there are multiple projects, this will point to the directory of the first one.",
            "type": "string"
           }
          },
          "required": [
           "id"
          ],
          "type": "object"
         },
         "vscodeLaunch": {
          "description": "Command providing the definition of a VsCode launch action \n Deprecated; removed in v1alpha2",
          "oneOf": [
           {
            "required": [
             "uri"
            ]
           },
           {
            "required": [
             "inlined"
            ]
           }
          ],
          "properties": {
           "attributes": {
            "additionalProperties": {
             "type": "string"
            },
            "description": "Optional map of free-form additional command attributes",
            "type": "object"
           },
           "group": {
            "description": "Defines the group this command is part of",
            "properties": {
             "isDefault": {
              "description": "Identifies the default command for a given group kind",
              "type": "boolean"
             },
             "kind": {
              "description": "Kind of group the command is part of",
              "enum": [
               "build",
               "run",
               "test",
               "debug"
              ],
              "type": "string"
             }
