schema:
{
 "description": "CodebaseBranch is the Schema for the CodebaseBranches API.",
 "properties": {
  "spec": {
   "description": "CodebaseBranchSpec defines the desired state of CodebaseBranch.",
   "properties": {
    "branchName": {
     "description": "Name of a branch.",
     "type": "string"
    },
    "codebaseName": {
     "description": "Name of Codebase associated with.",
     "type": "string"
    },
    "fromCommit": {
     "description": "The new branch will be created starting from the selected commit hash.",
     "type": "string"
    },
    "release": {
     "description": "Flag if branch is used as \"release\" branch.",
     "type": "boolean"
    },
    "releaseJobParams": {
     "additionalProperties": {
      "type": "string"
     },
     "nullable": true,
     "type": "object"
    },
    "version": {
     "nullable": true,
     "type": "string"
    }
   },
   "required": [
    "branchName",
    "codebaseName",
    "fromCommit",
    "release"
   ],
   "type": "object"
  }
 },
 "title": "Codebase Branch",
 "type": "object"
}
