schema:
{
 "description": "Jenkins is the Schema for the jenkins API.",
 "properties": {
  "spec": {
   "description": "JenkinsSpec defines the desired state of Jenkins.",
   "properties": {
    "basePath": {
     "type": "string"
    },
    "edpSpec": {
     "properties": {
      "dnsWildcard": {
       "type": "string"
      }
     },
     "required": [
      "dnsWildcard"
     ],
     "type": "object"
    },
    "externalURL": {
     "description": "ExternalURL jenkins full external url for keycloak or other integrations",
     "type": "string"
    },
    "keycloakSpec": {
     "properties": {
      "enabled": {
       "type": "boolean"
      },
      "isPrivate": {
       "type": "boolean"
      },
      "realm": {
       "type": "string"
      },
      "secretName": {
       "type": "string"
      }
     },
     "required": [
      "enabled"
     ],
     "type": "object"
    },
    "restAPIUrl": {
     "description": "RestAPIUrl jenkins full rest api url",
     "type": "string"
    },
    "sharedLibraries": {
     "items": {
      "properties": {
       "name": {
        "type": "string"
       },
       "secret": {
        "nullable": true,
        "type": "string"
       },
       "tag": {
        "type": "string"
       },
       "type": {
        "nullable": true,
        "type": "string"
       },
       "url": {
        "type": "string"
       }
      },
      "required": [
       "name",
       "tag",
       "url"
      ],
      "type": "object"
     },
     "nullable": true,
     "type": "array"
    }
   },
   "required": [
    "keycloakSpec"
   ],
   "type": "object"
  }
 },
 "title": "Jenkins",
 "type": "object"
}
