schema:
{
 "description": "The MongoDBOpsManager resource allows you to deploy Ops Manager within your Kubernetes cluster",
 "properties": {
  "spec": {
   "properties": {
    "adminCredentials": {
     "description": "AdminSecret is the secret for the first admin user to create has the fields: \"Username\", \"Password\", \"FirstName\", \"LastName\"",
     "type": "string"
    },
    "applicationDatabase": {
     "properties": {
      "additionalMongodConfig": {
       "description": "AdditionalMongodConfig are additional configurations that can be passed to each data-bearing mongod at runtime. Uses the same structure as the mongod configuration file: https://docs.mongodb.com/manual/reference/configuration-options/",
       "format": "textarea",
       "type": "string"
      },
      "agent": {
       "description": "specify configuration like startup flags and automation config settings for the AutomationAgent and MonitoringAgent",
       "properties": {
        "logLevel": {
         "type": "string"
        },
        "logRotate": {
         "description": "LogRotate if enabled, will enable LogRotate for all processes.",
         "properties": {
          "includeAuditLogsWithMongoDBLogs": {
           "description": "set to 'true' to have the Automation Agent rotate the audit files along with mongodb log files",
           "type": "boolean"
          },
          "numTotal": {
           "description": "maximum number of log files to have total",
           "type": "integer"
          },
          "numUncompressed": {
           "description": "maximum number of log files to leave uncompressed",
           "type": "integer"
          },
          "percentOfDiskspace": {
           "description": "Maximum percentage of the total disk space these log files should take up. The string needs to be able to be converted to float64",
           "type": "string"
          },
          "sizeThresholdMB": {
           "description": "Maximum size for an individual log file before rotation. The string needs to be able to be converted to float64. Fractional values of MB are supported.",
           "type": "string"
          },
          "timeThresholdHrs": {
           "description": "maximum hours for an individual log file before rotation",
           "type": "integer"
          }
         },
         "required": [
          "sizeThresholdMB",
          "timeThresholdHrs"
         ],
         "type": "object"
        },
        "maxLogFileDurationHours": {
         "type": "integer"
        },
        "startupOptions": {
         "additionalProperties": {
          "type": "string"
         },
         "type": "object"
        },
        "systemLog": {
         "description": "SystemLog configures system log of mongod",
         "properties": {
          "destination": {
           "type": "string"
          },
          "logAppend": {
           "type": "boolean"
          },
          "path": {
           "type": "string"
          }
         },
         "required": [
          "destination",
          "logAppend",
          "path"
         ],
         "type": "object"
        }
       },
       "type": "object"
      },
      "automationConfig": {
       "description": "AutomationConfigOverride holds any fields that will be merged on top of the Automation Config that the operator creates for the AppDB. Currently only the process.disabled and logRotate field is recognized.",
       "properties": {
        "processes": {
         "items": {
          "description": "OverrideProcess contains fields that we can override on the AutomationConfig processes.",
          "properties": {
           "disabled": {
            "type": "boolean"
           },
           "logRotate": {
            "description": "CrdLogRotate is the crd definition of LogRotate including fields in strings while the agent supports them as float64",
            "properties": {
             "includeAuditLogsWithMongoDBLogs": {
              "description": "set to 'true' to have the Automation Agent rotate the audit files along with mongodb log files",
              "type": "boolean"
             },
             "numTotal": {
              "description": "maximum number of log files to have total",
              "type": "integer"
             },
             "numUncompressed": {
              "description": "maximum number of log files to leave uncompressed",
              "type": "integer"
             },
             "percentOfDiskspace": {
              "description": "Maximum percentage of the total disk space these log files should take up. The string needs to be able to be converted to float64",
              "type": "string"
             },
             "sizeThresholdMB": {
              "description": "Maximum size for an individual log file before rotation. The string needs to be able to be converted to float64. Fractional values of MB are supported.",
              "type": "string"
             },
             "timeThresholdHrs": {
              "description": "maximum hours for an individual log file before rotation",
              "type": "integer"
             }
            },
            "required": [
             "sizeThresholdMB",
             "timeThresholdHrs"
            ],
            "type": "object"
           },
           "name": {
            "type": "string"
           }
          },
          "required": [
           "disabled",
           "name"
          ],
          "type": "object"
         },
         "type": "array"
        }
       },
       "required": [
        "processes"
       ],
       "type": "object"
      },
      "cloudManager": {
       "properties": {
        "configMapRef": {
         "properties": {
          "name": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "clusterDomain": {
       "type": "string"
      },
      "clusterSpecList": {
       "items": {
        "description": "ClusterSpecItem is the mongodb multi-cluster spec that is specific to a particular Kubernetes cluster, this maps to the statefulset created in each cluster",
        "properties": {
         "clusterName": {
          "description": "ClusterName is name of the cluster where the MongoDB Statefulset will be scheduled, the name should have a one on one mapping with the service-account created in the central cluster to talk to the workload clusters.",
          "type": "string"
         },
         "externalAccess": {
          "description": "ExternalAccessConfiguration provides external access configuration for Multi-Cluster.",
          "properties": {
           "externalDomain": {
            "description": "An external domain that is used for exposing MongoDB to the outside world.",
            "type": "string"
           },
           "externalService": {
            "description": "Provides a way to override the default (NodePort) Service",
            "properties": {
             "annotations": {
              "additionalProperties": {
               "type": "string"
              },
              "description": "A map of annotations that shall be added to the externally available Service.",
              "type": "object"
             },
             "spec": {
              "description": "A wrapper for the Service spec object.",
              "format": "textarea",
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         },
         "memberConfig": {
          "description": "MemberConfig",
          "format": "textarea",
          "items": {
           "properties": {
            "priority": {
             "type": "string"
            },
            "tags": {
             "additionalProperties": {
              "type": "string"
             },
             "type": "object"
            },
            "votes": {
             "type": "integer"
            }
           },
           "type": "object"
          },
          "type": "string"
         },
         "members": {
          "description": "Amount of members for this MongoDB Replica Set",
          "type": "integer"
         },
         "service": {
          "description": "this is an optional service, it will get the name \"\u003crsName\u003e-service\" in case not provided",
          "type": "string"
         },
         "statefulSet": {
          "description": "StatefulSetConfiguration holds the optional custom StatefulSet that should be merged into the operator created one.",
          "properties": {
           "metadata": {
            "description": "StatefulSetMetadataWrapper is a wrapper around Labels and Annotations",
            "properties": {
             "annotations": {
              "additionalProperties": {
               "type": "string"
              },
              "type": "object"
             },
             "labels": {
              "additionalProperties": {
               "type": "string"
              },
              "type": "object"
             }
            },
            "type": "object"
           },
           "spec": {
            "format": "textarea",
            "type": "string"
           }
          },
          "required": [
           "spec"
          ],
          "type": "object"
         }
        },
        "required": [
         "members"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "connectivity": {
       "properties": {
        "replicaSetHorizons": {
         "description": "ReplicaSetHorizons holds list of maps of horizons to be configured in each of MongoDB processes. Horizons map horizon names to the node addresses for each process in the replicaset, e.g.: [ { \"internal\": \"my-rs-0.my-internal-domain.com:31843\", \"external\": \"my-rs-0.my-external-domain.com:21467\" }, { \"internal\": \"my-rs-1.my-internal-domain.com:31843\", \"external\": \"my-rs-1.my-external-domain.com:21467\" }, ... ] The key of each item in the map is an arbitrary, user-chosen string that represents the name of the horizon. The value of the item is the host and, optionally, the port that this mongod node will be connected to from.",
         "items": {
          "additionalProperties": {
           "type": "string"
          },
          "type": "object"
         },
         "type": "array"
        }
       },
       "type": "object"
      },
      "credentials": {
       "description": "Name of the Secret holding credentials information",
       "type": "string"
      },
      "featureCompatibilityVersion": {
       "type": "string"
      },
      "logLevel": {
       "enum": [
        "DEBUG",
        "INFO",
        "WARN",
        "ERROR",
        "FATAL"
       ],
       "type": "string"
      },
      "memberConfig": {
       "description": "MemberConfig",
       "items": {
        "properties": {
         "priority": {
          "type": "string"
         },
         "tags": {
          "additionalProperties": {
           "type": "string"
          },
          "type": "object"
         },
         "votes": {
          "type": "integer"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "members": {
       "description": "Amount of members for this MongoDB Replica Set",
       "maximum": 50,
       "minimum": 3,
       "type": "integer"
      },
      "monitoringAgent": {
       "description": "Specify configuration like startup flags just for the MonitoringAgent. These take precedence over the flags set in AutomationAgent",
       "properties": {
        "logLevel": {
         "type": "string"
        },
        "maxLogFileDurationHours": {
         "type": "integer"
        },
        "startupOptions": {
         "additionalProperties": {
          "type": "string"
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "opsManager": {
       "properties": {
        "configMapRef": {
         "properties": {
          "name": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "passwordSecretKeyRef": {
       "description": "PasswordSecretKeyRef contains a reference to the secret which contains the password for the mongodb-ops-manager SCRAM-SHA user",
       "properties": {
        "key": {
         "type": "string"
        },
        "name": {
         "type": "string"
        }
       },
       "required": [
        "name"
       ],
       "type": "object"
      },
      "podSpec": {
       "properties": {
        "persistence": {
         "properties": {
          "multiple": {
           "properties": {
            "data": {
             "properties": {
              "labelSelector": {
               "format": "textarea",
               "type": "string"
              },
              "storage": {
               "type": "string"
              },
              "storageClass": {
               "type": "string"
              }
             },
             "type": "object"
            },
            "journal": {
             "properties": {
              "labelSelector": {
               "format": "textarea",
               "type": "string"
              },
              "storage": {
               "type": "string"
              },
              "storageClass": {
               "type": "string"
              }
             },
             "type": "object"
            },
            "logs": {
             "properties": {
              "labelSelector": {
               "format": "textarea",
               "type": "string"
              },
              "storage": {
               "type": "string"
              },
              "storageClass": {
               "type": "string"
              }
             },
             "type": "object"
            }
           },
           "type": "object"
          },
          "single": {
           "properties": {
            "labelSelector": {
             "format": "textarea",
             "type": "string"
            },
            "storage": {
             "type": "string"
            },
            "storageClass": {
             "type": "string"
            }
           },
           "type": "object"
          }
         },
         "type": "object"
        },
        "podTemplate": {
         "format": "textarea",
         "type": "string"
        }
       },
       "type": "object"
      },
      "prometheus": {
       "description": "Enables Prometheus integration on the AppDB.",
       "properties": {
        "metricsPath": {
         "description": "Indicates path to the metrics endpoint.",
         "pattern": "^\\/[a-z0-9]+$",
         "type": "string"
        },
        "passwordSecretRef": {
         "description": "Name of a Secret containing a HTTP Basic Auth Password.",
         "properties": {
          "key": {
           "description": "Key is the key in the secret storing this password. Defaults to \"password\"",
           "type": "string"
          },
          "name": {
           "description": "Name is the name of the secret storing this user's password",
           "type": "string"
          }
         },
         "required": [
          "name"
         ],
         "type": "object"
        },
        "port": {
         "description": "Port where metrics endpoint will bind to. Defaults to 9216.",
         "type": "integer"
        },
        "tlsSecretKeyRef": {
         "description": "Name of a Secret (type kubernetes.io/tls) holding the certificates to use in the Prometheus endpoint.",
         "properties": {
          "key": {
           "description": "Key is the key in the secret storing this password. Defaults to \"password\"",
           "type": "string"
          },
          "name": {
           "description": "Name is the name of the secret storing this user's password",
           "type": "string"
          }
         },
         "required": [
          "name"
         ],
         "type": "object"
        },
        "username": {
         "description": "HTTP Basic Auth Username for metrics endpoint.",
         "type": "string"
        }
       },
       "required": [
        "passwordSecretRef",
        "username"
       ],
       "type": "object"
      },
      "security": {
       "properties": {
        "authentication": {
         "description": "Authentication holds various authentication related settings that affect this MongoDB resource.",
         "properties": {
          "agents": {
           "description": "Agents contains authentication configuration properties for the agents",
           "properties": {
            "automationLdapGroupDN": {
             "type": "string"
            },
            "automationPasswordSecretRef": {
             "description": "SecretKeySelector selects a key of a Secret.",
             "properties": {
              "key": {
               "description": "The key of the secret to select from.  Must be a valid secret key.",
               "type": "string"
              },
              "name": {
               "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
               "type": "string"
              },
              "optional": {
               "description": "Specify whether the Secret or its key must be defined",
               "type": "boolean"
              }
             },
             "required": [
              "key"
             ],
             "type": "object",
             "x-kubernetes-map-type": "atomic"
            },
            "automationUserName": {
             "type": "string"
            },
            "clientCertificateSecretRef": {
             "format": "textarea",
             "type": "string"
            },
            "mode": {
             "description": "Mode is the desired Authentication mode that the agents will use",
             "type": "string"
            }
           },
           "required": [
            "mode"
           ],
           "type": "object"
          },
          "enabled": {
           "type": "boolean"
          },
          "ignoreUnknownUsers": {
           "description": "IgnoreUnknownUsers maps to the inverse of auth.authoritativeSet",
           "type": "boolean"
          },
          "internalCluster": {
           "type": "string"
          },
          "ldap": {
           "description": "LDAP Configuration",
           "properties": {
            "authzQueryTemplate": {
             "type": "string"
            },
            "bindQueryPasswordSecretRef": {
             "properties": {
              "name": {
               "type": "string"
              }
             },
             "required": [
              "name"
             ],
             "type": "object"
            },
            "bindQueryUser": {
             "type": "string"
            },
            "caConfigMapRef": {
             "description": "Allows to point at a ConfigMap/key with a CA file to mount on the Pod",
             "properties": {
              "key": {
               "description": "The key to select.",
               "type": "string"
              },
              "name": {
               "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
               "type": "string"
              },
              "optional": {
               "description": "Specify whether the ConfigMap or its key must be defined",
               "type": "boolean"
              }
             },
             "required": [
              "key"
             ],
             "type": "object",
             "x-kubernetes-map-type": "atomic"
            },
            "servers": {
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "timeoutMS": {
             "type": "integer"
            },
            "transportSecurity": {
             "enum": [
              "tls",
              "none"
             ],
             "type": "string"
            },
            "userCacheInvalidationInterval": {
             "type": "integer"
            },
            "userToDNMapping": {
             "type": "string"
            },
            "validateLDAPServerConfig": {
             "type": "boolean"
            }
           },
           "type": "object"
          },
          "modes": {
           "items": {
            "enum": [
             "X509",
             "SCRAM",
             "SCRAM-SHA-1",
             "MONGODB-CR",
             "SCRAM-SHA-256",
             "LDAP"
            ],
            "type": "string"
           },
           "type": "array"
          },
          "requireClientTLSAuthentication": {
           "description": "Clients should present valid TLS certificates",
           "type": "boolean"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "certsSecretPrefix": {
         "type": "string"
        },
        "roles": {
         "items": {
          "properties": {
           "authenticationRestrictions": {
            "items": {
             "properties": {
              "clientSource": {
               "items": {
                "type": "string"
               },
               "type": "array"
              },
              "serverAddress": {
               "items": {
                "type": "string"
               },
               "type": "array"
              }
             },
             "type": "object"
            },
            "type": "array"
           },
           "db": {
            "type": "string"
           },
           "privileges": {
            "items": {
             "properties": {
              "actions": {
               "items": {
                "type": "string"
               },
               "type": "array"
              },
              "resource": {
               "properties": {
                "cluster": {
                 "type": "boolean"
                },
                "collection": {
                 "type": "string"
                },
                "db": {
                 "type": "string"
                }
               },
               "type": "object"
              }
             },
             "required": [
              "actions",
              "resource"
             ],
             "type": "object"
            },
            "type": "array"
           },
           "role": {
            "type": "string"
           },
           "roles": {
            "items": {
             "properties": {
              "db": {
               "type": "string"
              },
              "role": {
               "type": "string"
              }
             },
             "required": [
              "db",
              "role"
             ],
             "type": "object"
            },
            "type": "array"
           }
          },
          "required": [
           "db",
           "role"
          ],
          "type": "object"
         },
         "type": "array"
        },
        "tls": {
         "properties": {
          "additionalCertificateDomains": {
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "ca": {
           "description": "CA corresponds to a ConfigMap containing an entry for the CA certificate (ca.pem) used to validate the certificates created already.",
           "type": "string"
          },
          "enabled": {
           "description": "DEPRECATED please enable TLS by setting `security.certsSecretPrefix` or `security.tls.secretRef.prefix`. Enables TLS for this resource. This will make the operator try to mount a Secret with a defined name (\u003cresource-name\u003e-cert). This is only used when enabling TLS on a MongoDB resource, and not on the AppDB, where TLS is configured by setting `secretRef.Name`.",
           "type": "boolean"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "service": {
       "description": "this is an optional service, it will get the name \"\u003crsName\u003e-svc\" in case not provided",
       "type": "string"
      },
      "topology": {
       "enum": [
        "SingleCluster",
        "MultiCluster"
       ],
       "type": "string"
      },
      "type": {
       "enum": [
        "Standalone",
        "ReplicaSet",
        "ShardedCluster"
       ],
       "type": "string"
      },
      "version": {
       "pattern": "^[0-9]+.[0-9]+.[0-9]+(-.+)?$|^$",
       "type": "string"
      }
     },
     "required": [
      "version"
     ],
     "type": "object"
    },
    "backup": {
     "description": "Backup",
     "properties": {
      "assignmentLabels": {
       "description": "Assignment Labels set in the Ops Manager",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "blockStores": {
       "items": {
        "description": "DataStoreConfig is the description of the config used to reference to database. Reused by Oplog and Block stores Optionally references the user if the Mongodb is configured with authentication",
        "properties": {
         "assignmentLabels": {
          "description": "Assignment Labels set in the Ops Manager",
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "mongodbResourceRef": {
          "properties": {
           "name": {
            "type": "string"
           },
           "namespace": {
            "type": "string"
           }
          },
          "required": [
           "name"
          ],
          "type": "object"
         },
         "mongodbUserRef": {
          "properties": {
           "name": {
            "type": "string"
           }
          },
          "required": [
           "name"
          ],
          "type": "object"
         },
         "name": {
          "type": "string"
         }
        },
        "required": [
         "mongodbResourceRef",
         "name"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "enabled": {
       "description": "Enabled indicates if Backups will be enabled for this Ops Manager.",
       "type": "boolean"
      },
      "encryption": {
       "description": "Encryption settings",
       "properties": {
        "kmip": {
         "description": "Kmip corresponds to the KMIP configuration assigned to the Ops Manager Project's configuration.",
         "properties": {
          "server": {
           "description": "KMIP Server configuration",
           "properties": {
            "ca": {
             "description": "CA corresponds to a ConfigMap containing an entry for the CA certificate (ca.pem) used for KMIP authentication",
             "type": "string"
            },
            "url": {
             "description": "KMIP Server url in the following format: hostname:port Valid examples are: 10.10.10.3:5696 my-kmip-server.mycorp.com:5696 kmip-svc.svc.cluster.local:5696",
             "pattern": "[^\\:]+:[0-9]{0,5}",
             "type": "string"
            }
           },
           "required": [
            "ca",
            "url"
           ],
           "type": "object"
          }
         },
         "required": [
          "server"
         ],
         "type": "object"
        }
       },
       "type": "object"
      },
      "externalServiceEnabled": {
       "type": "boolean"
      },
      "fileSystemStores": {
       "items": {
        "properties": {
         "name": {
          "type": "string"
         }
        },
        "required": [
         "name"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "headDB": {
       "description": "HeadDB specifies configuration options for the HeadDB",
       "properties": {
        "labelSelector": {
         "format": "textarea",
         "type": "string"
        },
        "storage": {
         "type": "string"
        },
        "storageClass": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "jvmParameters": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "members": {
       "description": "Members indicate the number of backup daemon pods to create.",
       "minimum": 1,
       "type": "integer"
