schema:
{
 "description": "OAuth2Client is the Schema for the oauth2clients API",
 "properties": {
  "spec": {
   "description": "OAuth2ClientSpec defines the desired state of OAuth2Client",
   "properties": {
    "allowedCorsOrigins": {
     "description": "AllowedCorsOrigins is an array of allowed CORS origins",
     "items": {
      "description": "RedirectURI represents a redirect URI for the client",
      "pattern": "\\w+:/?/?[^\\s]+",
      "type": "string"
     },
     "type": "array"
    },
    "audience": {
     "description": "Audience is a whitelist defining the audiences this client is allowed to request tokens for",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "backChannelLogoutSessionRequired": {
     "default": false,
     "description": "BackChannelLogoutSessionRequired Boolean value specifying whether the RP requires that a sid (session ID) Claim be included in the Logout Token to identify the RP session with the OP when the backchannel_logout_uri is used. If omitted, the default value is false.",
     "type": "boolean"
    },
    "backChannelLogoutURI": {
     "description": "BackChannelLogoutURI RP URL that will cause the RP to log itself out when sent a Logout Token by the OP",
     "pattern": "(^$|^https?://.*)",
     "type": "string"
    },
    "clientName": {
     "description": "ClientName is the human-readable string name of the client to be presented to the end-user during authorization.",
     "type": "string"
    },
    "frontChannelLogoutSessionRequired": {
     "default": false,
     "description": "FrontChannelLogoutSessionRequired Boolean value specifying whether the RP requires that iss (issuer) and sid (session ID) query parameters be included to identify the RP session with the OP when the frontchannel_logout_uri is used",
     "type": "boolean"
    },
    "frontChannelLogoutURI": {
     "description": "FrontChannelLogoutURI RP URL that will cause the RP to log itself out when rendered in an iframe by the OP. An iss (issuer) query parameter and a sid (session ID) query parameter MAY be included by the OP to enable the RP to validate the request and to determine which of the potentially multiple sessions is to be logged out; if either is included, both MUST be",
     "pattern": "(^$|^https?://.*)",
     "type": "string"
    },
    "grantTypes": {
     "description": "GrantTypes is an array of grant types the client is allowed to use.",
     "items": {
      "description": "GrantType represents an OAuth 2.0 grant type",
      "enum": [
       "client_credentials",
       "authorization_code",
       "implicit",
       "refresh_token"
      ],
      "type": "string"
     },
     "maxItems": 4,
     "minItems": 1,
     "type": "array"
    },
    "hydraAdmin": {
     "description": "HydraAdmin is the optional configuration to use for managing this client",
     "properties": {
      "endpoint": {
       "description": "Endpoint is the endpoint for the hydra instance on which to set up the client. This value will override the value provided to `--endpoint` (defaults to `\"/clients\"` in the application)",
       "pattern": "(^$|^/.*)",
       "type": "string"
      },
      "forwardedProto": {
       "description": "ForwardedProto overrides the `--forwarded-proto` flag. The value \"off\" will force this to be off even if `--forwarded-proto` is specified",
       "pattern": "(^$|https?|off)",
       "type": "string"
      },
      "port": {
       "description": "Port is the port for the hydra instance on which to set up the client. This value will override the value provided to `--hydra-port`",
       "maximum": 65535,
       "type": "integer"
      },
      "url": {
       "description": "URL is the URL for the hydra instance on which to set up the client. This value will override the value provided to `--hydra-url`",
       "maxLength": 64,
       "pattern": "(^$|^https?://.*)",
       "type": "string"
      }
     },
     "type": "object"
    },
    "jwksUri": {
     "description": "JwksUri Define the URL where the JSON Web Key Set should be fetched from when performing the private_key_jwt client authentication method.",
     "pattern": "(^$|^https?://.*)",
     "type": "string"
    },
    "metadata": {
     "description": "Metadata is arbitrary data",
     "format": "textarea",
     "nullable": true,
     "type": "string"
    },
    "postLogoutRedirectUris": {
     "description": "PostLogoutRedirectURIs is an array of the post logout redirect URIs allowed for the application",
     "items": {
      "description": "RedirectURI represents a redirect URI for the client",
      "pattern": "\\w+:/?/?[^\\s]+",
      "type": "string"
     },
     "type": "array"
    },
    "redirectUris": {
     "description": "RedirectURIs is an array of the redirect URIs allowed for the application",
     "items": {
      "description": "RedirectURI represents a redirect URI for the client",
      "pattern": "\\w+:/?/?[^\\s]+",
      "type": "string"
     },
     "type": "array"
    },
    "responseTypes": {
     "description": "ResponseTypes is an array of the OAuth 2.0 response type strings that the client can use at the authorization endpoint.",
     "items": {
      "description": "ResponseType represents an OAuth 2.0 response type strings",
      "enum": [
       "id_token",
       "code",
       "token",
       "code token",
       "code id_token",
       "id_token token",
       "code id_token token"
      ],
      "type": "string"
     },
     "maxItems": 3,
     "minItems": 1,
     "type": "array"
    },
    "scope": {
     "description": "Scope is a string containing a space-separated list of scope values (as described in Section 3.3 of OAuth 2.0 [RFC6749]) that the client can use when requesting access tokens.",
     "pattern": "([a-zA-Z0-9\\.\\*]+\\s?)+",
     "type": "string"
    },
    "secretName": {
     "description": "SecretName points to the K8s secret that contains this client's ID and password",
     "maxLength": 253,
     "minLength": 1,
     "pattern": "[a-z0-9]([-a-z0-9]*[a-z0-9])?(\\.[a-z0-9]([-a-z0-9]*[a-z0-9])?)*",
     "type": "string"
    },
    "skipConsent": {
     "default": false,
     "description": "SkipConsent skips the consent screen for this client.",
     "type": "boolean"
    },
    "tokenEndpointAuthMethod": {
     "allOf": [
      {
       "enum": [
        "client_secret_basic",
        "client_secret_post",
        "private_key_jwt",
        "none"
       ]
      },
      {
       "enum": [
        "client_secret_basic",
        "client_secret_post",
        "private_key_jwt",
        "none"
       ]
      }
     ],
     "description": "Indication which authentication method shoud be used for the token endpoint",
     "type": "string"
    }
   },
   "required": [
    "grantTypes",
    "scope",
    "secretName"
   ],
   "type": "object"
  }
 },
 "title": "O Auth2Client",
 "type": "object"
}
