       "versions": {
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "location": {
     "type": "string"
    },
    "match": {
     "properties": {
      "excludedNamespaces": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "kinds": {
       "items": {
        "description": "Kinds accepts a list of objects with apiGroups and kinds fields that list the groups/kinds of objects to which the mutation will apply. If multiple groups/kinds objects are specified, only one match is needed for the resource to be in scope.",
        "properties": {
         "apiGroups": {
          "description": "APIGroups is the API groups the resources belong to. '*' is all groups. If '*' is present, the length of the slice must be one. Required.",
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "kinds": {
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "labelSelector": {
       "description": "A label selector is a label query over a set of resources. The result of matchLabels and matchExpressions are ANDed. An empty label selector matches all objects. A null label selector matches no objects.",
       "properties": {
        "matchExpressions": {
         "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
         "items": {
          "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
          "properties": {
           "key": {
            "description": "key is the label key that the selector applies to.",
            "type": "string"
           },
           "operator": {
            "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
            "type": "string"
           },
           "values": {
            "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
            "items": {
             "type": "string"
            },
            "type": "array"
           }
          },
          "required": [
           "key",
           "operator"
          ],
          "type": "object"
         },
         "type": "array"
        },
        "matchLabels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
         "type": "object"
        }
       },
       "type": "object"
      },
      "namespaceSelector": {
       "description": "A label selector is a label query over a set of resources. The result of matchLabels and matchExpressions are ANDed. An empty label selector matches all objects. A null label selector matches no objects.",
       "properties": {
        "matchExpressions": {
         "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
