schema:
{
 "properties": {
  "spec": {
   "properties": {
    "persistentVolumeClaim": {
     "properties": {
      "claimName": {
       "description": "PersistentVolumeClaim name to backup",
       "type": "string"
      },
      "spec": {
       "description": "PersistentVolumeClaim spec to create and backup",
       "properties": {
        "accessModes": {
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "resources": {
         "properties": {
          "limits": {
           "additionalProperties": true,
           "properties": {
            "storageClassName": {
             "type": "string"
            }
           },
           "type": "object"
          },
          "requests": {
           "additionalProperties": true,
           "type": "object"
          }
         },
         "type": "object"
        },
        "selector": {
         "properties": {
          "matchExpressions": {
           "items": {
            "additionalProperties": true,
            "type": "object"
           },
           "type": "array"
          },
          "matchLabels": {
           "additionalProperties": true,
           "type": "object"
          }
         },
         "type": "object"
        },
        "storageClassName": {
         "type": "string"
        },
        "volumeName": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "schedule": {
     "items": {
      "properties": {
       "every": {
        "description": "Interval for creating new backups",
        "type": "string"
       },
       "keep": {
        "description": "Number of historical backups to keep",
        "type": "integer"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "template": {
     "properties": {
      "spec": {
       "description": "VolumeSnapshot spec",
       "properties": {
        "volumeSnapshotClassName": {
         "description": "VolumeSnapshotClassName is the name of the VolumeSnapshotClass requested by the VolumeSnapshot. VolumeSnapshotClassName may be left nil to indicate that the default SnapshotClass should be used. A given cluster may have multiple default Volume SnapshotClasses: one default per CSI Driver. If a VolumeSnapshot does not specify a SnapshotClass, VolumeSnapshotSource will be checked to figure out what the associated CSI Driver is, and the default VolumeSnapshotClass associated with that CSI Driver will be used. If more than one VolumeSnapshotClass exist for a given CSI Driver and more than one have been marked as default, CreateSnapshot will fail and generate an event. Empty string is not allowed for this field.",
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Snapshot Group",
 "type": "object"
}
