component.schema:
{
 "description": "ApiV4Definition is the Schema for the v4 apidefinitions API.",
 "properties": {
  "spec": {
   "description": "ApiV4DefinitionSpec defines the desired state of ApiDefinition.",
   "properties": {
    "analytics": {
     "description": "API Analytics",
     "properties": {
      "enabled": {
       "default": true,
       "description": "Analytics Enabled or not?",
       "type": "boolean"
      },
      "logging": {
       "description": "Analytics Logging",
       "properties": {
        "condition": {
         "description": "The logging condition. This field is evaluated for HTTP requests and supports EL expressions.",
         "type": "string"
        },
        "content": {
         "description": "Defines which component of the request should be included in the log payload.",
         "properties": {
          "headers": {
           "description": "Should HTTP headers be logged or not ?",
           "type": "boolean"
          },
          "messageHeaders": {
           "description": "Should message headers be logged or not ?",
           "type": "boolean"
          },
          "messageMetadata": {
           "description": "Should message metadata be logged or not ?",
           "type": "boolean"
          },
          "messagePayload": {
           "description": "Should message payloads be logged or not ?",
           "type": "boolean"
          },
          "payload": {
           "description": "Should HTTP payloads be logged or not ?",
           "type": "boolean"
          }
         },
         "required": [
          "headers",
          "messageHeaders",
          "messageMetadata",
          "messagePayload",
          "payload"
         ],
         "type": "object"
        },
        "messageCondition": {
         "description": "The logging message condition. This field is evaluated for messages and supports EL expressions.",
         "type": "string"
        },
        "mode": {
         "description": "The logging mode defines which \"hop\" of the request roundtrip\nshould be included in the log payload.\nThis can be either the inbound request to the gateway,\nthe request issued by the gateway to the upstream service, or both.",
         "properties": {
          "endpoint": {
           "description": "If true, the request to the upstream service will be included in the log payload",
           "type": "boolean"
          },
          "entrypoint": {
           "description": "If true, the inbound request to the gateway will be included in the log payload",
           "type": "boolean"
          }
         },
         "required": [
          "endpoint",
          "entrypoint"
         ],
         "type": "object"
        },
        "phase": {
         "description": "Defines which phase of the request roundtrip\nshould be included in the log payload.\nThis can be either the request phase, the response phase, or both.",
         "properties": {
          "request": {
           "description": "Should the request phase of the request roundtrip be included in the log payload or not ?",
           "type": "boolean"
          },
          "response": {
           "description": "Should the response phase of the request roundtrip be included in the log payload or not ?",
           "type": "boolean"
          }
         },
         "required": [
          "request",
          "response"
         ],
         "type": "object"
        }
       },
       "type": "object"
      },
      "sampling": {
       "description": "Analytics Sampling",
       "properties": {
        "type": {
         "description": "The sampling type to use",
         "type": "string"
        },
        "value": {
         "description": "Sampling Value",
         "type": "string"
        }
       },
       "required": [
        "type",
        "value"
       ],
       "type": "object"
      }
     },
     "required": [
      "enabled"
     ],
     "type": "object"
    },
    "categories": {
     "description": "The list of categories the API belongs to.\nCategories are reflected in APIM portal so that consumers can easily find the APIs they need.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "contextRef": {
     "properties": {
      "name": {
       "type": "string"
      },
      "namespace": {
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "crossId": {
     "description": "When promoting an API from one environment to the other,\nthis ID identifies the API across those different environments.\nSetting this ID also allows to take control over an existing API on an APIM instance\n(by setting the same value as defined in APIM).\nIf empty, a UUID will be generated based on the namespace and name of the resource.",
     "type": "string"
    },
    "definitionContext": {
     "description": "The API Definition context is used to identify the Kubernetes origin of the API,\nand define whether the API definition should be synchronized\nfrom an API instance or from a config map created in the cluster (which is the default)",
     "properties": {
      "origin": {
       "default": "KUBERNETES",
       "description": "The definition context origin where the API definition is managed.\nThe value is always `KUBERNETES` for an API managed by the operator.",
       "enum": [
        "KUBERNETES"
       ],
       "type": "string"
      },
      "syncFrom": {
       "default": "KUBERNETES",
       "description": "The syncFrom field defines where the gateways should source the API definition from.\nIf the value is `MANAGEMENT`, the API definition will be sourced from an APIM instance.\nThis means that the API definition *must* hold a context reference in that case.\nSetting the value to `MANAGEMENT` allows to make an API definition available on\ngateways deployed across multiple clusters / regions.\nIf the value is `KUBERNETES`, the API definition will be sourced from a config map.\nThis means that only gateways deployed in the same cluster will be able to sync the API definition.",
       "enum": [
        "KUBERNETES",
        "MANAGEMENT"
       ],
       "type": "string"
      }
     },
     "type": "object"
    },
    "definitionVersion": {
     "default": "V4",
     "description": "The definition version of the API.",
     "enum": [
      "V4"
     ],
     "type": "string"
    },
    "description": {
     "description": "API description",
     "type": "string"
    },
    "endpointGroups": {
     "description": "List of Endpoint groups",
     "items": {
      "properties": {
       "endpoints": {
        "description": "List of endpoint for the group",
        "items": {
         "properties": {
          "configuration": {
           "description": "Endpoint Configuration, arbitrary map of key-values",
           "format": "textarea",
           "type": "string"
          },
          "inheritConfiguration": {
           "description": "Should endpoint group configuration be inherited or not ?",
           "type": "boolean"
          },
          "name": {
           "description": "The endpoint name (this value should be unique across endpoints)",
           "type": "string"
          },
          "secondary": {
           "description": "Endpoint is secondary or not?",
           "type": "boolean"
          },
          "services": {
           "description": "Endpoint Services",
           "properties": {
            "healthCheck": {
             "description": "Health check service",
             "properties": {
              "configuration": {
               "description": "Service Configuration, a map of arbitrary key-values",
               "format": "textarea",
               "type": "string"
              },
              "enabled": {
               "description": "Is the service enabled or not ?",
               "type": "boolean"
              },
              "overrideConfiguration": {
               "description": "Service Override Configuration or not?",
               "type": "boolean"
              },
              "type": {
               "description": "Service Type",
               "type": "string"
              }
             },
             "required": [
              "enabled",
              "overrideConfiguration"
             ],
             "type": "object"
            }
           },
           "type": "object"
          },
          "sharedConfigurationOverride": {
           "description": "Endpoint Configuration Override, arbitrary map of key-values",
           "format": "textarea",
           "type": "string"
          },
          "tenants": {
           "description": "List of endpoint tenants",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "type": {
           "description": "Endpoint Type",
           "type": "string"
          },
          "weight": {
           "description": "Endpoint Weight",
           "type": "integer"
          }
         },
         "required": [
          "inheritConfiguration",
          "secondary"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "headers": {
        "additionalProperties": {
         "type": "string"
        },
        "description": "Endpoint group headers, arbitrary map of key-values",
        "type": "object"
       },
       "http": {
        "description": "Endpoint group http client options",
        "properties": {
         "clearTextUpgrade": {
          "default": true,
          "description": "Should HTTP/2 clear text upgrade be used or not ?",
          "type": "boolean"
         },
         "connectTimeout": {
          "description": "Connection timeout of the http connection",
          "format": "int64",
          "type": "integer"
         },
         "followRedirects": {
          "default": false,
          "description": "Should HTTP redirects be followed or not ?",
          "type": "boolean"
         },
         "idleTimeout": {
          "description": " Idle Timeout for the http connection",
          "format": "int64",
          "type": "integer"
         },
         "keepAlive": {
          "default": true,
          "type": "boolean"
         },
         "keepAliveTimeout": {
          "default": 30000,
          "description": "Should keep alive be used for the HTTP connection ?",
          "format": "int64",
          "type": "integer"
         },
         "maxConcurrentConnections": {
          "description": "HTTP max concurrent connections",
          "type": "integer"
         },
         "pipelining": {
          "default": false,
          "description": "Should HTTP/1.1 pipelining be used for the connection or not ?",
          "type": "boolean"
         },
         "propagateClientAcceptEncoding": {
          "default": false,
          "description": "Propagate Client Accept-Encoding header",
          "type": "boolean"
         },
         "readTimeout": {
          "description": "Read timeout",
          "format": "int64",
          "type": "integer"
         },
         "useCompression": {
          "default": false,
          "description": "Should compression be used or not ?",
          "type": "boolean"
         },
         "version": {
          "default": "HTTP_1_1",
          "description": "HTTP Protocol Version (Possible values Http1 or Http2)",
          "enum": [
           "HTTP_1_1",
           "HTTP_2"
          ],
          "type": "string"
         }
        },
        "required": [
         "followRedirects",
         "pipelining",
         "useCompression"
        ],
        "type": "object"
       },
       "loadBalancer": {
        "description": "Endpoint group load balancer",
        "properties": {
         "type": {
          "default": "ROUND_ROBIN",
          "enum": [
           "ROUND_ROBIN",
           "RANDOM",
           "WEIGHTED_ROUND_ROBIN",
           "WEIGHTED_RANDOM"
          ],
          "type": "string"
         }
        },
        "required": [
         "type"
        ],
        "type": "object"
       },
       "name": {
        "description": "Endpoint group name",
        "type": "string"
       },
       "services": {
        "description": "Endpoint group services",
        "properties": {
         "discovery": {
          "description": "Endpoint group discovery service",
          "properties": {
           "configuration": {
            "description": "Service Configuration, a map of arbitrary key-values",
            "format": "textarea",
            "type": "string"
           },
           "enabled": {
            "description": "Is the service enabled or not ?",
            "type": "boolean"
           },
           "overrideConfiguration": {
            "description": "Service Override Configuration or not?",
            "type": "boolean"
           },
           "type": {
            "description": "Service Type",
            "type": "string"
           }
          },
          "required": [
           "enabled",
           "overrideConfiguration"
          ],
          "type": "object"
         },
         "healthCheck": {
          "description": "Endpoint group health check service",
          "properties": {
           "configuration": {
            "description": "Service Configuration, a map of arbitrary key-values",
            "format": "textarea",
            "type": "string"
           },
           "enabled": {
            "description": "Is the service enabled or not ?",
            "type": "boolean"
           },
           "overrideConfiguration": {
            "description": "Service Override Configuration or not?",
            "type": "boolean"
           },
           "type": {
            "description": "Service Type",
            "type": "string"
           }
          },
          "required": [
           "enabled",
           "overrideConfiguration"
          ],
          "type": "object"
         }
        },
        "type": "object"
       },
       "sharedConfiguration": {
        "description": "Endpoint group shared configuration, arbitrary map of key-values",
        "format": "textarea",
        "type": "string"
       },
       "ssl": {
        "description": "Endpoint group http client SSL options",
        "properties": {
         "hostnameVerifier": {
          "default": true,
          "description": "Verify Hostname when establishing connection",
          "type": "boolean"
         },
         "keyStore": {
          "description": "KeyStore type (possible values PEM, PKCS12, JKS)",
          "properties": {
           "type": {
            "description": "The KeyStore type to use (possible values are PEM, PKCS12, JKS)",
            "type": "string"
           }
          },
          "type": "object"
         },
         "trustAll": {
          "default": false,
          "description": "Whether to trust all issuers or not",
          "type": "boolean"
         },
         "trustStore": {
          "description": "TrustStore type (possible values PEM, PKCS12, JKS)",
          "properties": {
           "type": {
            "description": "The TrustStore type to use (possible values are PEM, PKCS12, JKS)",
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "required": [
         "hostnameVerifier",
         "trustAll"
        ],
        "type": "object"
       },
       "type": {
        "description": "Endpoint group type",
        "type": "string"
       }
      },
      "required": [
       "name"
      ],
      "type": "object"
     },
     "minItems": 1,
     "type": "array"
    },
    "flowExecution": {
     "description": "API Flow Execution",
     "properties": {
      "matchRequired": {
       "description": "Is match required or not ? If set to true, a 404 status response will be returned if no matching flow was found.",
       "type": "boolean"
      },
      "mode": {
       "description": "The flow mode to use",
       "type": "string"
      }
     },
     "required": [
      "matchRequired"
     ],
     "type": "object"
    },
    "flows": {
     "default": [],
     "description": "List of flows for the API",
     "items": {
      "properties": {
       "enabled": {
        "default": true,
        "description": "Is flow enabled or not?",
        "type": "boolean"
       },
       "id": {
        "description": "The ID of the flow this field is mainly used for compatibility with\nAPIM exports and can be safely ignored.",
        "type": "string"
       },
       "name": {
        "description": "Flow name",
        "type": "string"
       },
       "publish": {
        "description": "List of Publish flow steps",
        "items": {
         "properties": {
          "condition": {
           "description": "FlowStep condition",
           "type": "string"
          },
          "configuration": {
           "description": "FlowStep configuration is a map of arbitrary key-values",
           "format": "textarea",
           "type": "string"
          },
          "description": {
           "description": "FlowStep description",
           "type": "string"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this FlowStep is enabled or not",
           "type": "boolean"
          },
          "messageCondition": {
           "description": "The message condition (supports EL expressions)",
           "type": "string"
          },
          "name": {
           "description": "FlowStep name",
           "type": "string"
          },
          "policy": {
           "description": "FlowStep policy",
           "type": "string"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "request": {
        "description": "List of Request flow steps",
        "items": {
         "properties": {
          "condition": {
           "description": "FlowStep condition",
           "type": "string"
          },
          "configuration": {
           "description": "FlowStep configuration is a map of arbitrary key-values",
           "format": "textarea",
           "type": "string"
          },
          "description": {
           "description": "FlowStep description",
           "type": "string"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this FlowStep is enabled or not",
           "type": "boolean"
          },
          "messageCondition": {
           "description": "The message condition (supports EL expressions)",
           "type": "string"
          },
          "name": {
           "description": "FlowStep name",
           "type": "string"
          },
          "policy": {
           "description": "FlowStep policy",
           "type": "string"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "response": {
        "description": "List of Response flow steps",
        "items": {
         "properties": {
          "condition": {
           "description": "FlowStep condition",
           "type": "string"
          },
          "configuration": {
           "description": "FlowStep configuration is a map of arbitrary key-values",
           "format": "textarea",
           "type": "string"
          },
          "description": {
           "description": "FlowStep description",
           "type": "string"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this FlowStep is enabled or not",
           "type": "boolean"
          },
          "messageCondition": {
           "description": "The message condition (supports EL expressions)",
           "type": "string"
          },
          "name": {
           "description": "FlowStep name",
           "type": "string"
          },
          "policy": {
           "description": "FlowStep policy",
           "type": "string"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "selectors": {
        "description": "List of Flow selectors",
        "items": {
         "format": "textarea",
         "type": "string"
        },
        "type": "array"
       },
       "subscribe": {
        "description": "List of Subscribe flow steps",
        "items": {
         "properties": {
          "condition": {
           "description": "FlowStep condition",
           "type": "string"
          },
          "configuration": {
           "description": "FlowStep configuration is a map of arbitrary key-values",
           "format": "textarea",
           "type": "string"
          },
          "description": {
           "description": "FlowStep description",
           "type": "string"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this FlowStep is enabled or not",
           "type": "boolean"
          },
          "messageCondition": {
           "description": "The message condition (supports EL expressions)",
           "type": "string"
          },
          "name": {
           "description": "FlowStep name",
           "type": "string"
          },
          "policy": {
           "description": "FlowStep policy",
           "type": "string"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "tags": {
        "description": "List of tags",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "enabled"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "groups": {
     "description": "List of groups associated with the API",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "id": {
     "description": "The API ID. If empty, this field will take the value of the `metadata.uid`\nfield of the resource.",
     "type": "string"
    },
    "labels": {
     "description": "List of labels of the API",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "lifecycleState": {
     "default": "UNPUBLISHED",
     "description": "API life cycle state can be one of the values PUBLISHED, UNPUBLISHED",
     "enum": [
      "PUBLISHED",
      "UNPUBLISHED"
     ],
     "type": "string"
    },
    "listeners": {
     "description": "List of listeners for this API",
     "items": {
      "format": "textarea",
      "type": "string"
     },
     "minItems": 1,
     "type": "array"
    },
    "members": {
     "description": "List of members associated with the API",
     "items": {
      "properties": {
       "displayName": {
        "description": "Member display name",
        "type": "string"
       },
       "role": {
        "default": "USER",
        "description": "The API role associated with this Member",
        "type": "string"
       },
       "source": {
        "description": "Member source",
        "example": "gravitee",
        "type": "string"
       },
       "sourceId": {
        "description": "Member source ID",
        "example": "user@email.com",
        "type": "string"
       }
      },
      "required": [
       "source",
       "sourceId"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "metadata": {
     "description": "List of API metadata entries",
     "items": {
      "properties": {
       "defaultValue": {
        "description": "Metadata Default value",
        "type": "string"
       },
       "format": {
        "description": "Metadata Format",
        "enum": [
         "STRING",
         "NUMERIC",
         "BOOLEAN",
         "DATE",
         "MAIL",
         "URL"
        ],
        "type": "string"
       },
       "key": {
        "description": "Metadata Key",
        "type": "string"
       },
       "name": {
        "description": "Metadata Name",
        "type": "string"
       },
       "value": {
        "description": "Metadata Value",
        "type": "string"
       }
      },
      "required": [
       "format",
       "key",
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "name": {
     "description": "API name",
     "type": "string"
    },
    "notifyMembers": {
     "description": "If true, new members added to the API spec will\nbe notified when the API is synced with APIM.",
     "type": "boolean"
    },
    "pages": {
     "additionalProperties": {
      "properties": {
       "api": {
        "description": "The API of the page. If empty, will be set automatically to the generated ID of the API.",
        "type": "string"
       },
       "configuration": {
        "additionalProperties": {
         "type": "string"
        },
        "description": "Custom page configuration (e.g. page rendering can be changed to use Redoc instead of Swagger ui)",
        "type": "object"
       },
       "content": {
        "description": "The content of the page, if any.",
        "type": "string"
       },
       "crossId": {
        "description": "CrossID is designed to identified a page across environments.\nIf not set, this ID will be generated in a predictable manner based on\nthe map key associated to this entry in the API.",
        "type": "string"
       },
       "homepage": {
        "default": false,
        "description": "If true, this page will be displayed as the homepage of your API documentation.",
        "type": "boolean"
       },
       "id": {
        "description": "The ID of the page. This field is mostly required when you are applying\nan API exported from APIM to make the operator take control over it.\nIf not set, this ID will be generated in a predictable manner based on\nthe map key associated to this entry in the API.",
        "type": "string"
       },
       "name": {
        "description": "This is the display name of the page in APIM and on the portal.\nThis field can be edited safely if you want to rename a page.",
        "type": "string"
       },
       "order": {
        "description": "The order used to display the page in APIM and on the portal.",
        "format": "int64",
        "type": "integer"
       },
       "parent": {
        "description": "If your page contains a folder, setting this field to the map key associated to the\nfolder entry will be reflected into APIM by making the page a child of this folder.",
        "type": "string"
       },
       "parentId": {
        "description": "The parent ID of the page. This field is mostly required when you are applying\nan API exported from APIM to make the operator take control over it. Use `Parent`\nin any other case.",
        "type": "string"
       },
       "published": {
        "default": false,
        "description": "If true, the page will be accessible from the portal (default is false)",
        "type": "boolean"
       },
       "source": {
        "description": "Source allow you to fetch pages from various external sources, overriding page content\neach time the source is fetched.",
        "properties": {
         "configuration": {
          "format": "textarea",
          "type": "string"
         },
         "type": {
          "type": "string"
         }
        },
        "required": [
         "configuration",
         "type"
        ],
        "type": "object"
       },
       "type": {
        "description": "The type of the documentation page or folder.",
        "enum": [
         "MARKDOWN",
         "SWAGGER",
         "ASYNCAPI",
         "ASCIIDOC",
         "FOLDER",
         "SYSTEM_FOLDER",
         "ROOT"
        ],
        "type": "string"
       },
       "visibility": {
        "default": "PUBLIC",
        "description": "The visibility of the page.",
        "enum": [
         "PUBLIC",
         "PRIVATE"
        ],
