component.schema:
{
 "description": "ApiDefinition is the Schema for the apidefinitions API.",
 "properties": {
  "spec": {
   "description": "The API definition is the main resource handled by the Kubernetes Operator\nMost of the configuration properties defined here are already documented\nin the APIM Console API Reference.\nSee https://docs.gravitee.io/apim/3.x/apim_installguide_rest_apis_documentation.html",
   "properties": {
    "categories": {
     "default": [],
     "description": "The list of categories the API belongs to.\nCategories are reflected in APIM portal so that consumers can easily find the APIs they need.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "contextRef": {
     "properties": {
      "kind": {
       "type": "string"
      },
      "name": {
       "type": "string"
      },
      "namespace": {
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "crossId": {
     "description": "When promoting an API from one environment to the other,\nthis ID identifies the API across those different environments.\nSetting this ID also allows to take control over an existing API on an APIM instance\n(by setting the same value as defined in APIM).\nIf empty, a UUID will be generated based on the namespace and name of the resource.",
     "type": "string"
    },
    "definition_context": {
     "description": "The definition context is used to inform a management API instance that this API definition\nis managed using a kubernetes operator",
     "properties": {
      "mode": {
       "default": "fully_managed",
       "type": "string"
      },
      "origin": {
       "default": "kubernetes",
       "type": "string"
      },
      "syncFrom": {
       "default": "kubernetes",
       "type": "string"
      }
     },
     "type": "object"
    },
    "deployedAt": {
     "description": "Shows the time that the API is deployed",
     "format": "int64",
     "type": "integer"
    },
    "description": {
     "description": "API description",
     "type": "string"
    },
    "execution_mode": {
     "default": "v4-emulation-engine",
     "description": "Execution mode that eventually runs the API in the gateway",
     "enum": [
      "v3",
      "v4-emulation-engine"
     ],
     "type": "string"
    },
    "flow_mode": {
     "default": "DEFAULT",
     "description": "The flow mode of the API. The value is either `DEFAULT` or `BEST_MATCH`.",
     "enum": [
      "DEFAULT",
      "BEST_MATCH"
     ],
     "type": "string"
    },
    "flows": {
     "default": [],
     "description": "The flow of the API",
     "items": {
      "properties": {
       "condition": {
        "description": "Flow condition",
        "type": "string"
       },
       "consumers": {
        "default": [],
        "description": "List of the consumers of this Flow",
        "items": {
         "properties": {
          "consumerId": {
           "description": "Consumer ID",
           "type": "string"
          },
          "consumerType": {
           "description": "Consumer type (possible values TAG)",
           "type": "integer"
          }
         },
         "type": "object"
        },
        "type": "array"
       },
       "enabled": {
        "default": true,
        "description": "Indicate if this flow is enabled or disabled",
        "type": "boolean"
       },
       "id": {
        "description": "Flow ID",
        "type": "string"
       },
       "methods": {
        "default": [],
        "description": "A list of methods  for this flow (GET;POST;PUT;PATCH;DELETE;OPTIONS;HEAD;CONNECT;TRACE;OTHER)",
        "items": {
         "enum": [
          "GET",
          "POST",
          "PUT",
          "PATCH",
          "DELETE",
          "OPTIONS",
          "HEAD",
          "CONNECT",
          "TRACE",
          "OTHER"
         ],
         "type": "string"
        },
        "type": "array"
       },
       "name": {
        "description": "Flow name",
        "type": "string"
       },
       "path-operator": {
        "description": "List of path operators",
        "properties": {
         "operator": {
          "default": "STARTS_WITH",
          "description": "Operator (possible values STARTS_WITH or EQUALS)",
          "enum": [
           "STARTS_WITH",
           "EQUALS"
          ],
          "type": "string"
         },
         "path": {
          "description": "Operator path",
          "type": "string"
         }
        },
        "type": "object"
       },
       "post": {
        "default": [],
        "description": "Flow post step",
        "items": {
         "properties": {
          "condition": {
           "description": "FlowStep condition",
           "type": "string"
          },
          "configuration": {
           "description": "FlowStep configuration is a map of arbitrary key-values",
           "format": "textarea",
           "type": "string"
          },
          "description": {
           "description": "FlowStep description",
           "type": "string"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this FlowStep is enabled or not",
           "type": "boolean"
          },
          "name": {
           "description": "FlowStep name",
           "type": "string"
          },
          "policy": {
           "description": "FlowStep policy",
           "type": "string"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "pre": {
        "default": [],
        "description": "Flow pre step",
        "items": {
         "properties": {
          "condition": {
           "description": "FlowStep condition",
           "type": "string"
          },
          "configuration": {
           "description": "FlowStep configuration is a map of arbitrary key-values",
           "format": "textarea",
           "type": "string"
          },
          "description": {
           "description": "FlowStep description",
           "type": "string"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this FlowStep is enabled or not",
           "type": "boolean"
          },
          "name": {
           "description": "FlowStep name",
           "type": "string"
          },
          "policy": {
           "description": "FlowStep policy",
           "type": "string"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       }
      },
      "required": [
       "enabled"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "gravitee": {
     "default": "2.0.0",
     "description": "The definition version of the API. For v1alpha1 resources, this field should always set to `2.0.0`.",
     "type": "string"
    },
    "groupRefs": {
     "default": [],
     "description": "List of group references associated with the API\nThis groups are references to Group custom resources created on the cluster.",
     "items": {
      "properties": {
       "kind": {
        "type": "string"
       },
       "name": {
        "type": "string"
       },
       "namespace": {
        "type": "string"
       }
      },
      "required": [
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "groups": {
     "default": [],
     "description": "List of groups associated with the API.\nThis groups are id or name references to existing groups in APIM.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "id": {
     "description": "The API ID. If empty, this field will take the value of the `metadata.uid`\nfield of the resource.",
     "type": "string"
    },
    "labels": {
     "default": [],
     "description": "List of labels of the API",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "lifecycle_state": {
     "default": "CREATED",
     "description": "API life cycle state can be one of the values CREATED, PUBLISHED, UNPUBLISHED, DEPRECATED, ARCHIVED",
     "enum": [
      "CREATED",
      "PUBLISHED",
      "UNPUBLISHED",
      "DEPRECATED",
      "ARCHIVED"
     ],
     "type": "string"
    },
    "local": {
     "default": false,
     "description": "local defines if the api is local or not.\n\n\nIf true, the Operator will create the ConfigMaps for the Gateway and pushes the API to the Management API\nbut without setting the update flag in the datastore.\n\n\nIf false, the Operator will not create the ConfigMaps for the Gateway.\nInstead, it pushes the API to the Management API and forces it to update the event in the datastore.\nThis will cause Gateways to fetch the APIs from the datastore",
     "type": "boolean"
    },
    "members": {
     "description": "List of members associated with the API",
     "items": {
      "properties": {
       "role": {
        "default": "USER",
        "description": "The API role associated with this Member",
        "type": "string"
       },
       "source": {
        "description": "Member source",
        "example": "gravitee",
        "type": "string"
       },
       "sourceId": {
        "description": "Member source ID",
        "example": "user@email.com",
        "type": "string"
       }
      },
      "required": [
       "source",
       "sourceId"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "metadata": {
     "default": [],
     "description": "List of API metadata entries",
     "items": {
      "properties": {
       "defaultValue": {
        "description": "Metadata Default value",
        "type": "string"
       },
       "format": {
        "description": "Metadata Format",
        "enum": [
         "STRING",
         "NUMERIC",
         "BOOLEAN",
         "DATE",
         "MAIL",
         "URL"
        ],
        "type": "string"
       },
       "key": {
        "description": "Metadata Key",
        "type": "string"
       },
       "name": {
        "description": "Metadata Name",
        "type": "string"
       },
       "value": {
        "description": "Metadata Value",
        "type": "string"
       }
      },
      "required": [
       "format",
       "key",
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "name": {
     "description": "API name",
     "type": "string"
    },
    "notifyMembers": {
     "default": true,
     "description": "If true, new members added to the API spec will\nbe notified when the API is synced with APIM.",
     "type": "boolean"
    },
    "pages": {
     "additionalProperties": {
      "properties": {
       "accessControls": {
        "default": [],
        "description": "If the page is private, defines a set of user groups with access",
        "items": {
         "properties": {
          "referenceId": {
           "description": "The ID denied or granted by the access control (currently only group names are supported)",
           "type": "string"
          },
          "referenceType": {
           "description": "The type of reference denied or granted by the access control\nCurrently only GROUP is supported",
           "enum": [
            "GROUP"
           ],
           "type": "string"
          }
         },
         "type": "object"
        },
        "type": "array"
       },
       "api": {
        "description": "The API of the page. If empty, will be set automatically to the generated ID of the API.",
        "type": "string"
       },
       "configuration": {
        "additionalProperties": {
         "type": "string"
        },
        "description": "Custom page configuration (e.g. page rendering can be changed to use Redoc instead of Swagger ui)",
        "type": "object"
       },
       "content": {
        "description": "The content of the page, if any.",
        "type": "string"
       },
       "crossId": {
        "description": "CrossID is designed to identified a page across environments.\nIf not set, this ID will be generated in a predictable manner based on\nthe map key associated to this entry in the API.",
        "type": "string"
       },
       "excludedAccessControls": {
        "description": "if true, the references defined in the accessControls list will be\ndenied access instead of being granted",
        "type": "boolean"
       },
       "homepage": {
        "default": false,
        "description": "If true, this page will be displayed as the homepage of your API documentation.",
        "type": "boolean"
       },
       "id": {
        "description": "The ID of the page. This field is mostly required when you are applying\nan API exported from APIM to make the operator take control over it.\nIf not set, this ID will be generated in a predictable manner based on\nthe map key associated to this entry in the API.",
        "type": "string"
       },
       "name": {
        "description": "This is the display name of the page in APIM and on the portal.\nThis field can be edited safely if you want to rename a page.",
        "type": "string"
       },
       "order": {
        "description": "The order used to display the page in APIM and on the portal.",
        "format": "int64",
        "type": "integer"
       },
       "parent": {
        "description": "If your page contains a folder, setting this field to the map key associated to the\nfolder entry will be reflected into APIM by making the page a child of this folder.",
        "type": "string"
       },
       "parentId": {
        "description": "The parent ID of the page. This field is mostly required when you are applying\nan API exported from APIM to make the operator take control over it. Use `Parent`\nin any other case.",
        "type": "string"
       },
       "published": {
        "default": false,
        "description": "If true, the page will be accessible from the portal (default is false)",
        "type": "boolean"
       },
       "source": {
        "description": "Source allow you to fetch pages from various external sources, overriding page content\neach time the source is fetched.",
        "properties": {
         "configuration": {
          "format": "textarea",
          "type": "string"
         },
         "type": {
          "type": "string"
         }
        },
        "required": [
         "configuration",
         "type"
        ],
        "type": "object"
       },
       "type": {
        "description": "The type of the documentation page or folder.",
        "enum": [
         "MARKDOWN",
         "SWAGGER",
         "ASYNCAPI",
         "ASCIIDOC",
         "FOLDER",
         "SYSTEM_FOLDER",
         "ROOT"
        ],
        "type": "string"
       },
       "visibility": {
        "default": "PUBLIC",
        "description": "The visibility of the page.",
        "enum": [
         "PUBLIC",
         "PRIVATE"
        ],
        "type": "string"
       }
      },
      "required": [
       "type"
      ],
      "type": "object"
     },
     "description": "A map of pages objects.\n\n\nKeys uniquely identify pages and are used to keep them in sync\nwith APIM when using a management context.\n\n\nRenaming a key is the equivalent of deleting the page and recreating\nit holding a new ID in APIM.",
     "type": "object"
    },
    "path_mappings": {
     "description": "API Path mapping",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "plans": {
     "default": [],
     "description": "API plans",
     "items": {
      "properties": {
       "api": {
        "description": "Specify the API associated with this plan",
        "type": "string"
       },
       "characteristics": {
        "description": "List of plan characteristics",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "comment_required": {
        "description": "Indicate of comment is required for this plan or not",
        "type": "boolean"
       },
       "crossId": {
        "description": "The plan Cross ID.\nThis field is used to identify plans defined for an API\nthat has been promoted between different environments.",
        "type": "string"
       },
       "description": {
        "description": "Plan Description",
        "type": "string"
       },
       "excluded_groups": {
        "default": [],
        "description": "List of excluded groups for this plan",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "flows": {
        "default": [],
        "description": "List of different flows for this Plan",
        "items": {
         "properties": {
          "condition": {
           "description": "Flow condition",
           "type": "string"
          },
          "consumers": {
           "default": [],
           "description": "List of the consumers of this Flow",
           "items": {
            "properties": {
             "consumerId": {
              "description": "Consumer ID",
              "type": "string"
             },
             "consumerType": {
              "description": "Consumer type (possible values TAG)",
              "type": "integer"
             }
            },
            "type": "object"
           },
           "type": "array"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this flow is enabled or disabled",
           "type": "boolean"
          },
          "id": {
           "description": "Flow ID",
           "type": "string"
          },
          "methods": {
           "default": [],
           "description": "A list of methods  for this flow (GET;POST;PUT;PATCH;DELETE;OPTIONS;HEAD;CONNECT;TRACE;OTHER)",
           "items": {
            "enum": [
             "GET",
             "POST",
             "PUT",
             "PATCH",
             "DELETE",
             "OPTIONS",
             "HEAD",
             "CONNECT",
             "TRACE",
             "OTHER"
            ],
            "type": "string"
           },
           "type": "array"
          },
          "name": {
           "description": "Flow name",
           "type": "string"
          },
          "path-operator": {
           "description": "List of path operators",
           "properties": {
            "operator": {
             "default": "STARTS_WITH",
             "description": "Operator (possible values STARTS_WITH or EQUALS)",
             "enum": [
              "STARTS_WITH",
              "EQUALS"
             ],
             "type": "string"
            },
            "path": {
             "description": "Operator path",
             "type": "string"
            }
           },
           "type": "object"
          },
          "post": {
           "default": [],
           "description": "Flow post step",
           "items": {
            "properties": {
             "condition": {
              "description": "FlowStep condition",
              "type": "string"
             },
             "configuration": {
              "description": "FlowStep configuration is a map of arbitrary key-values",
              "format": "textarea",
              "type": "string"
             },
             "description": {
              "description": "FlowStep description",
              "type": "string"
             },
             "enabled": {
              "default": true,
              "description": "Indicate if this FlowStep is enabled or not",
              "type": "boolean"
             },
             "name": {
              "description": "FlowStep name",
              "type": "string"
             },
             "policy": {
              "description": "FlowStep policy",
              "type": "string"
             }
            },
            "required": [
             "enabled"
            ],
            "type": "object"
           },
           "type": "array"
          },
          "pre": {
           "default": [],
           "description": "Flow pre step",
           "items": {
            "properties": {
             "condition": {
              "description": "FlowStep condition",
              "type": "string"
             },
             "configuration": {
              "description": "FlowStep configuration is a map of arbitrary key-values",
              "format": "textarea",
              "type": "string"
             },
             "description": {
              "description": "FlowStep description",
              "type": "string"
             },
             "enabled": {
              "default": true,
              "description": "Indicate if this FlowStep is enabled or not",
              "type": "boolean"
             },
             "name": {
              "description": "FlowStep name",
              "type": "string"
             },
             "policy": {
              "description": "FlowStep policy",
              "type": "string"
             }
            },
            "required": [
             "enabled"
            ],
            "type": "object"
           },
           "type": "array"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "id": {
        "description": "Plan ID",
        "type": "string"
       },
       "name": {
        "description": "Plan name",
        "type": "string"
       },
       "order": {
        "description": "Plan order",
        "type": "integer"
       },
       "paths": {
        "additionalProperties": {
         "items": {
          "properties": {
           "description": {
            "description": "Rule description",
            "type": "string"
           },
           "enabled": {
            "description": "Indicate if the Rule is enabled or not",
            "type": "boolean"
           },
           "methods": {
            "description": "List of http methods for this Rule (GET;POST;PUT;PATCH;DELETE;OPTIONS;HEAD;CONNECT;TRACE;OTHER)",
            "items": {
             "enum": [
              "GET",
              "POST",
              "PUT",
              "PATCH",
              "DELETE",
              "OPTIONS",
              "HEAD",
              "CONNECT",
              "TRACE",
              "OTHER"
             ],
             "type": "string"
            },
            "type": "array"
           },
           "policy": {
            "description": "Rule policy",
            "properties": {
             "configuration": {
              "description": "Policy configuration is a map of arbitrary key-values",
              "format": "textarea",
              "type": "string"
             },
             "name": {
              "description": "Policy name",
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         },
         "type": "array"
        },
        "description": "A map of different paths (alongside their Rules) for this Plan",
        "type": "object"
       },
       "security": {
        "description": "Plan Security",
        "type": "string"
       },
       "securityDefinition": {
        "description": "Plan Security definition",
        "type": "string"
       },
       "selection_rule": {
        "description": "Plan selection rule",
        "type": "string"
       },
       "status": {
        "default": "PUBLISHED",
        "description": "The plan status",
        "enum": [
         "PUBLISHED",
         "DEPRECATED",
         "STAGING"
        ],
        "type": "string"
       },
       "tags": {
        "default": [],
        "description": "List of plan tags",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "type": {
        "default": "API",
        "description": "Plan type",
        "enum": [
         "API",
         "CATALOG"
        ],
        "type": "string"
       },
       "validation": {
        "default": "AUTO",
        "description": "Plan validation strategy",
        "enum": [
         "AUTO",
         "MANUAL"
        ],
        "type": "string"
       }
      },
      "required": [
       "description",
       "name",
       "security"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "properties": {
     "default": [],
     "description": "List of Properties for the API",
     "items": {
      "properties": {
       "dynamic": {
        "description": "Property is dynamic or not?",
        "type": "boolean"
       },
       "encrypted": {
        "description": "Property Encrypted or not?",
        "type": "boolean"
       },
       "key": {
        "description": "Property Key",
        "type": "string"
       },
       "value": {
        "description": "Property Value",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "proxy": {
     "description": "The proxy of the API that specifies its VirtualHosts and Groups.",
     "properties": {
      "cors": {
       "description": "Proxy Cors",
       "properties": {
        "allowCredentials": {
         "description": "Access Control - Allow credentials or not",
         "type": "boolean"
        },
        "allowHeaders": {
         "default": [],
         "description": "Access Control - List of allowed headers",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "allowMethods": {
         "default": [],
         "description": "Access Control - List of allowed methods",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "allowOrigin": {
         "default": [],
         "description": "Access Control -  List of Allowed origins",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "enabled": {
         "description": "Indicate if the cors enabled or not",
         "type": "boolean"
        },
        "exposeHeaders": {
         "default": [],
         "description": "Access Control - List of Exposed Headers",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "maxAge": {
         "description": "Access Control -  Max age",
         "type": "integer"
        },
        "runPolicies": {
         "default": false,
         "description": "Run policies or not",
         "type": "boolean"
        }
       },
       "required": [
        "allowCredentials",
        "enabled",
        "maxAge"
       ],
       "type": "object"
      },
      "failover": {
       "description": "Proxy Failover",
       "properties": {
        "cases": {
         "description": "List of Failover cases",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
