schema:
{
 "properties": {
  "spec": {
   "description": "Representation of a Kubernetes cluster that has been registered.",
   "properties": {
    "clusterDomain": {
     "description": "name local DNS suffix used by the cluster.\nused for building FQDNs for in-cluster services\ndefaults to 'cluster.local'",
     "type": "string"
    },
    "providerInfo": {
     "description": "Metadata for clusters provisioned from cloud providers.",
     "oneOf": [
      {
       "not": {
        "anyOf": [
         {
          "required": [
           "eks"
          ]
         }
        ]
       }
      },
      {
       "required": [
        "eks"
       ]
      }
     ],
     "properties": {
      "eks": {
       "description": "Provider info for an AWS EKS provisioned cluster.",
       "properties": {
        "accountId": {
         "description": "AWS 12 digit account ID.",
         "type": "string"
        },
        "arn": {
         "description": "AWS ARN.",
         "type": "string"
        },
        "name": {
         "description": "EKS resource name.",
         "type": "string"
        },
        "region": {
         "description": "AWS region.",
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "secretName": {
     "description": "name of the secret which contains the kubeconfig with information to connect to the remote cluster.",
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "Kubernetes Cluster",
 "type": "object"
}
