schema:
{
 "properties": {
  "spec": {
   "description": "Describes a workload controlled by a discovered service mesh.",
   "oneOf": [
    {
     "not": {
      "anyOf": [
       {
        "required": [
         "kubernetes"
        ]
       }
      ]
     }
    },
    {
     "required": [
      "kubernetes"
     ]
    }
   ],
   "properties": {
    "appMesh": {
     "description": "Metadata specific to an App Mesh controlled workload.",
     "properties": {
      "ports": {
       "description": "Ports exposed by this workload. Needed for declaring App Mesh VirtualNode listeners.",
       "items": {
        "properties": {
         "port": {
          "maximum": 4294967295,
          "minimum": 0,
          "type": "integer"
         },
         "protocol": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "virtualNodeName": {
       "description": "The value of the env var APPMESH_VIRTUAL_NODE_NAME on the App Mesh envoy proxy container.",
       "type": "string"
      }
     },
     "type": "object"
    },
    "kubernetes": {
     "description": "Information describing workloads backed by Kubernetes Pods.",
     "properties": {
      "controller": {
       "description": "Resource reference to the Kubernetes Pod controller (i.e. Deployment, ReplicaSet, DaemonSet) for this Workload..",
       "properties": {
        "clusterName": {
         "description": "name of the cluster in which the resource exists",
         "type": "string"
        },
        "name": {
         "description": "name of the resource being referenced",
         "type": "string"
        },
        "namespace": {
         "description": "namespace of the resource being referenced",
         "type": "string"
        }
       },
       "type": "object"
      },
      "podLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Labels on the Pod itself (read from `metadata.labels`), which are used to determine which Services front this workload.",
       "type": "object"
      },
      "serviceAccountName": {
       "description": "Service account associated with the Pods owned by this controller.",
       "type": "string"
      }
     },
     "type": "object"
    },
    "mesh": {
     "description": "The Mesh with which this Workload is associated.",
     "properties": {
      "name": {
       "description": "name of the resource being referenced",
       "type": "string"
      },
      "namespace": {
       "description": "namespace of the resource being referenced",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Workload",
 "type": "object"
}
