schema:
{
 "description": "Replication is the Schema for the replications API",
 "properties": {
  "spec": {
   "description": "ReplicationSpec defines the desired state of Replication",
   "properties": {
    "creator": {
     "type": "string"
    },
    "description": {
     "type": "string"
    },
    "destNamespace": {
     "description": "The destination namespace If left empty, the resource will be but in the same namespace as the source",
     "type": "string"
    },
    "destRegistry": {
     "description": "Destination Registry Reference to a registry cr",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    },
    "enabled": {
     "description": "Whether the policy is enabled or not",
     "type": "boolean"
    },
    "filters": {
     "description": "The replication policy filter array",
     "items": {
      "description": "ReplicationFilter holds the specifications of a replication filter",
      "properties": {
       "type": {
        "description": "The replication policy filter type.",
        "type": "string"
       },
       "value": {
        "description": "The value of replication policy filter.",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "name": {
     "description": "The name of the replication",
     "type": "string"
    },
    "override": {
     "description": "Whether to override the resources on the destination registry or not",
     "type": "boolean"
    },
    "parentInstance": {
     "description": "ParentInstance is a LocalObjectReference to the name of the harbor instance the replication policy gets created for",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    },
    "replicateDeletion": {
     "description": "Whether to replicate the deletion operation",
     "type": "boolean"
    },
    "srcRegistry": {
     "description": "Source Registry Reference to a registry cr",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    },
    "trigger": {
     "description": "The replication policy trigger type",
     "properties": {
      "triggerSettings": {
       "description": "TriggerSettings holds the settings of a trigger",
       "properties": {
        "cron": {
         "type": "string"
        }
       },
       "required": [
        "cron"
       ],
       "type": "object"
      },
      "type": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "triggerAfterCreation": {
     "type": "boolean"
    }
   },
   "required": [
    "name",
    "override",
    "parentInstance"
   ],
   "type": "object"
  }
 },
 "title": "Replication",
 "type": "object"
}
