component.schema:
{
 "description": "VaultTransitKey is the Schema for the vaulttransitengines API.",
 "properties": {
  "spec": {
   "description": "VaultTransitKeySpec defines the desired state of VaultTransitKey.",
   "properties": {
    "allowPlaintextBackup": {
     "description": "AllowPlaintextBackup enables taking backups of named key in the plaintext format. Once set, this cannot be disabled.",
     "type": "boolean"
    },
    "deleteProtection": {
     "description": "DeleteProtection configures that the secret should not be able to be deleted. Defaults to false.",
     "type": "boolean"
    },
    "engine": {
     "description": "Engine configures the used transit engine.",
     "type": "string"
    },
    "exportable": {
     "description": "Exportable enables keys to be exportable. This allows for all the valid keys in the key ring to be exported. Once set, this cannot be disabled.",
     "type": "boolean"
    },
    "minimumDecryptionVersion": {
     "description": "MinimumDecryptionVersion specifies the minimum version of the key that can be used to decrypt the ciphertext. Adjusting this as part of a key rotation policy can prevent old copies of ciphertext from being decrypted, should they fall into the wrong hands. For signatures, this value controls the minimum version of signature that can be verified against. For HMACs, this controls the minimum version of a key allowed to be used as the key for verification.",
     "type": "integer"
    },
    "minimumEncryptionVersion": {
     "description": "MinimumEncryptionVersion Specifies the minimum version of the key that can be used to encrypt plaintext, sign payloads, or generate HMACs. Must be 0 (which will use the latest version) or a value greater or equal to min_decryption_version.",
     "type": "integer"
    },
    "type": {
     "description": "Type configures the transit key type. Must be a vault supported key type. Additional information: https://www.vaultproject.io/api/secret/transit#type.",
     "enum": [
      "aes128-gcm96",
      "aes256-gcm96",
      "chacha20-poly1305",
      "ed25519",
      "ecdsa-p256",
      "ecdsa-p384",
      "ecdsa-p521",
      "rsa-2048",
      "rsa-3072",
      "rsa-4096"
     ],
     "type": "string"
    }
   },
   "required": [
    "engine",
    "type"
   ],
   "type": "object"
  }
 },
 "title": "Vault Transit Key",
 "type": "object"
}
