schema:
{
 "description": "VaultKVSecret is the Schema for the vaultkvsecrets API.",
 "properties": {
  "spec": {
   "description": "VaultKVSecretSpec defines the desired secret's fields and the secret's config.",
   "properties": {
    "deleteProtection": {
     "description": "DeleteProtection configures that the secret should not be able to be deleted. Defaults to false.",
     "type": "boolean"
    },
    "engine": {
     "description": "Engine configures the secret storage engine in which the secret should be stored.",
     "type": "string"
    },
    "fields": {
     "additionalProperties": {
      "description": "VaultKVSecretField defines the desired state of a field in a VaultKVSecret.",
      "properties": {
       "autoGenerated": {
        "description": "AutoGenerated configures that the secret should have an autogenerated value. Must be set to false when using a custom stringValue or custom cipherText. Defaults to true.",
        "type": "boolean"
       },
       "autoGeneratedLength": {
        "description": "AutoGeneratedLength can be used in combination with AutoGenerated. It optionally configures the length of the autogenerated secret, the default is 64 character.",
        "type": "integer"
       },
       "ciphertext": {
        "description": "CipherText represents a value which has been encrypted by Heists managed Transit Engine.",
        "pattern": "^vault:([a-z0-9]+):(.+)$",
        "type": "string"
       }
      },
      "type": "object"
     },
     "description": "Fields is a map of fields stored in the Secret.",
     "type": "object"
    },
    "path": {
     "description": "Path configures the relative path of the Secret inside its secret engine.",
     "type": "string"
    }
   },
   "required": [
    "engine"
   ],
   "type": "object"
  }
 },
 "title": "Vault KV Secret",
 "type": "object"
}
