schema:
{
 "description": "VaultSyncSecret is the Schema for the vaultsyncsecrets API.",
 "properties": {
  "spec": {
   "properties": {
    "certificateTemplates": {
     "description": "CertificateTemplates configures settings for certificates which may be issued.",
     "items": {
      "properties": {
       "alias": {
        "description": "Alias is the name of this certificate template.",
        "type": "string"
       },
       "certificateRole": {
        "description": "CertificateRole is the name of the VaultCertificateRole to be used for issuing this certificate.",
        "type": "string"
       },
       "commonName": {
        "description": "CommonName is the CN (common name) of the issued certificate.",
        "type": "string"
       },
       "dnsSans": {
        "description": "DNSSans is a list of DNS subject alternative names requested for this certificate.",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "excludeCNFromSans": {
        "description": "ExcludeCNFromSans toggles if the common name should be excluded from the subject alternative names of the certificate.",
        "type": "boolean"
       },
       "ipSans": {
        "description": "IPSans is a list of IP subject alternative names requested for this certificate.",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "otherSans": {
        "description": "OtherSans is a list of custom OID/UTF-8 subject alternative names requested for this certificate. Expected Format: `\u003coid\u003e;\u003ctype\u003e:\u003cvalue\u003e`",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "ttl": {
        "description": "TTL is the Time-To-Live requested for this certificate.",
        "type": "string"
       },
       "uriSans": {
        "description": "AlternativeNames is a list of URI subject alternative names requested for this certificate.",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "certificateRole"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "data": {
     "additionalProperties": {
      "properties": {
       "certificate": {
        "description": "Certificate configures a VaultCertificateRole from which a field should be synced.",
        "properties": {
         "field": {
          "description": "Field is the field of the certificate which should be synced.",
          "enum": [
           "certificate",
           "private_key",
           "cert_chain",
           "full_cert_chain"
          ],
          "type": "string"
         },
         "name": {
          "description": "Name is the name of the certificate template used to issue the certificate which should be synced.",
          "minLength": 1,
          "type": "string"
         }
        },
        "type": "object"
       },
       "certificateAuthority": {
        "description": "CertificateAuthority configures a VaultCertificateAuthority from which a field should be synced.",
        "properties": {
         "field": {
          "description": "Field is the field of the certificate authority which should be synced.",
          "enum": [
           "certificate",
           "private_key",
           "cert_chain",
           "full_cert_chain"
          ],
          "type": "string"
         },
         "name": {
          "description": "Name is the name of the VaultCertificateAuthority which should be synced.",
          "minLength": 1,
          "type": "string"
         }
        },
        "type": "object"
       },
       "cipherText": {
        "description": "CipherText represents a value which has been encrypted by Heists managed Transit Engine.",
        "pattern": "^vault:([a-z0-9]+):(.+)$",
        "type": "string"
       },
       "kvSecret": {
        "description": "KVSecret configures a VaultKVSecret from which a field should be synced",
        "properties": {
         "field": {
          "description": "Field specifies a single field of the VaultKVSecret which should be synced.",
          "minLength": 1,
          "type": "string"
         },
         "name": {
          "description": "Name is the name of the VaultKVSecret which should be synced.",
          "minLength": 1,
          "type": "string"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "description": "Data is a map of values which should be synced to the Target Kubernetes Secret.",
     "type": "object"
    },
    "target": {
     "description": "Target configures the secret you want to sync values to.",
     "properties": {
      "additionalAnnotations": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "AdditionalAnnotations is a map of annotations added to the secret.",
       "type": "object"
      },
      "additionalLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "AdditionalLabels is a map of labels added to the secret.",
       "type": "object"
      },
      "name": {
       "description": "Name is the name of the secret resource you want to create.",
       "maxLength": 253,
       "type": "string"
      },
      "namespace": {
       "description": "Namespace is the namespace the secret should be created in.",
       "maxLength": 63,
       "type": "string"
      },
      "type": {
       "description": "Type is the type of secret which should be created.",
       "enum": [
        "Opaque",
        "kubernetes.io/dockercfg",
        "kubernetes.io/dockerconfigjson",
        "kubernetes.io/basic-auth",
        "kubernetes.io/ssh-auth",
        "kubernetes.io/tls"
       ],
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Vault Sync Secret",
 "type": "object"
}
