schema:
{
 "properties": {
  "spec": {
   "description": "IssuerSpec defines the desired state of Issuer",
   "properties": {
    "authSecretName": {
     "description": "A reference to a Secret in the same namespace as the referent. If the referent is a ClusterIssuer, the reference instead refers to the resource with the given name in the configured 'cluster resource namespace', which is set as a flag on the controller component (and defaults to the namespace that the controller runs in).",
     "type": "string"
    },
    "caBundle": {
     "description": "CaBundle contains the CA bundle required to trust the Horizon endpoint certificate",
     "type": "string"
    },
    "labels": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "Labels is a map of labels that will override labels set at the Certificate or Ingress levels.",
     "type": "object"
    },
    "owner": {
     "description": "Owner will override the owner value set at the Certificate or Ingress levels.",
     "type": "string"
    },
    "profile": {
     "description": "The Horizon Profile that will be used to enroll certificates. Your authenticated principal should have rights over this Profile.",
     "type": "string"
    },
    "revokeCertificates": {
     "default": false,
     "description": "RevokeCertificates controls whether this issuer should revoke certificates that have been issued through it when their Kubernetes object is deleted.",
     "type": "boolean"
    },
    "skipTLSVerify": {
     "default": false,
     "description": "SkipTLSVerify indicates if untrusted certificates should be allowed when connecting to the Horizon instance.",
     "type": "boolean"
    },
    "team": {
     "description": "Team will override the team value set at the Certificate or Ingress levels.",
     "type": "string"
    },
    "url": {
     "description": "URL is the base URL of your Horizon instance, for instance: \"https://horizon.yourcompany.com\".",
     "type": "string"
    }
   },
   "required": [
    "authSecretName",
    "profile",
    "url"
   ],
   "type": "object"
  }
 },
 "title": "Issuer",
 "type": "object"
}
