schema:
{
 "description": "SnapshotGroupClass specifies parameters that a underlying storage system uses when creating a volumegroup snapshot. A specific SnapshotGroupClass is used by specifying its name in a VolumeGroupSnapshot object. SnapshotGroupClasses are non-namespaced",
 "properties": {
  "deletionPolicy": {
   "description": "deletionPolicy determines whether a SnapshotGroupContent created through the SnapshotGroupClass should be deleted when its bound SnapshotGroup is deleted. Supported values are \"Retain\" and \"Delete\". \"Retain\" means that the SnapshotGroupContent and its physical snapshotGroup on underlying storage system are kept. \"Delete\" means that the SnapshotGroupContent and its physical snapshotGroup on underlying storage system are deleted. Required.",
   "enum": [
    "Delete",
    "Retain"
   ],
   "type": "string"
  },
  "parameters": {
   "additionalProperties": {
    "type": "string"
   },
   "description": "parameters is a key-value map with storage driver specific parameters for creating snapshotGroups. These values are opaque to Kubernetes.",
   "type": "object"
  },
  "snapshotter": {
   "description": "snapshotter is the name of the storage driver that handles this SnapshotGroupClass. Required.",
   "type": "string"
  }
 },
 "required": [
  "deletionPolicy",
  "snapshotter"
 ],
 "title": "Snapshot Group Class",
 "type": "object"
}
