schema:
{
 "description": "VolumeGroupClass specifies parameters that a underlying storage system uses when creating a volumegroup. A specific VolumeGroupClass is used by specifying its name in a VolumeGroup object. VolumeGroupClasses are non-namespaced",
 "properties": {
  "deletionPolicy": {
   "description": "deletionPolicy determines whether a VolumeGroupContent created through the VolumeGroupClass should be deleted when its bound VolumeGroup is deleted. Supported values are \"Retain\" and \"Delete\". \"Retain\" means that the VolumeGroupContent and its physical volumeGroup on underlying storage system are kept. \"Delete\" means that the VolumeGroupContent and its physical volumeGroup on underlying storage system are deleted. Required.",
   "enum": [
    "Delete",
    "Retain"
   ],
   "type": "string"
  },
  "parameters": {
   "additionalProperties": {
    "type": "string"
   },
   "description": "parameters is a key-value map with storage driver specific parameters for creating volumeGroups. These values are opaque to Kubernetes.",
   "type": "object"
  },
  "provisioner": {
   "description": "provisioner is the name of the storage driver that handles this VolumeGroupClass. Required.",
   "type": "string"
  }
 },
 "required": [
  "deletionPolicy",
  "provisioner"
 ],
 "title": "Volume Group Class",
 "type": "object"
}
