        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "key",
       "operator"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "matchLabels": {
     "additionalProperties": {
      "type": "string"
     },
