component.schema:
{
 "description": "MonitoringDashboard is the Schema for the monitoring API",
 "properties": {
  "spec": {
   "properties": {
    "columnLayout": {
     "description": "The content is divided into equally spaced columns and the widgets are arranged vertically.",
     "properties": {
      "columns": {
       "description": "The columns of content to display.",
       "items": {
        "properties": {
         "weight": {
          "description": "The relative weight of this column. The column weight is used to adjust the width of columns on the screen (relative to peers). Greater the weight, greater the width of the column on the screen. If omitted, a value of 1 is used while rendering.",
          "format": "int64",
          "type": "integer"
         },
         "widgets": {
          "description": "The display widgets arranged vertically in this column.",
          "items": {
           "properties": {
            "blank": {
             "description": "A blank space.",
             "type": "object"
            },
            "logsPanel": {
             "description": "A widget that shows a stream of logs.",
             "properties": {
              "filter": {
               "description": "A filter that chooses which log entries to return.  See [Advanced Logs Queries](https://cloud.google.com/logging/docs/view/advanced-queries). Only log entries that match the filter are returned.  An empty filter matches all log entries.",
               "type": "string"
              },
              "resourceNames": {
               "description": "The names of logging resources to collect logs for. Currently only projects are supported. If empty, the widget will default to the host project.",
               "items": {
                "oneOf": [
                 {
                  "not": {
                   "required": [
                    "external"
                   ]
                  },
                  "required": [
                   "name",
                   "kind"
                  ]
                 },
                 {
                  "not": {
                   "anyOf": [
                    {
                     "required": [
                      "name"
                     ]
                    },
                    {
                     "required": [
                      "namespace"
                     ]
                    },
                    {
                     "required": [
                      "kind"
                     ]
                    }
                   ]
                  },
                  "required": [
                   "external"
                  ]
                 }
                ],
                "properties": {
                 "external": {
                  "description": "The external name of the referenced resource",
                  "type": "string"
                 },
                 "kind": {
                  "description": "Kind of the referent.",
                  "type": "string"
                 },
                 "name": {
                  "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
                  "type": "string"
                 },
                 "namespace": {
                  "description": "Namespace of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/namespaces/",
                  "type": "string"
                 }
                },
                "type": "object"
               },
               "type": "array"
              }
             },
             "type": "object"
            },
            "scorecard": {
             "description": "A scorecard summarizing time series data.",
             "properties": {
              "gaugeView": {
               "description": "Will cause the scorecard to show a gauge chart.",
               "properties": {
                "lowerBound": {
                 "description": "The lower bound for this gauge chart. The value of the chart should always be greater than or equal to this.",
                 "format": "double",
                 "type": "number"
                },
                "upperBound": {
                 "description": "The upper bound for this gauge chart. The value of the chart should always be less than or equal to this.",
                 "format": "double",
                 "type": "number"
                }
               },
               "type": "object"
              },
              "sparkChartView": {
               "description": "Will cause the scorecard to show a spark chart.",
               "properties": {
                "minAlignmentPeriod": {
                 "description": "The lower bound on data point frequency in the chart implemented by specifying the minimum alignment period to use in a time series query. For example, if the data is published once every 10 minutes it would not make sense to fetch and align data at one minute intervals. This field is optional and exists only as a hint.",
                 "type": "string"
                },
                "sparkChartType": {
                 "description": "Required. The type of sparkchart to show in this chartView.",
                 "type": "string"
                }
               },
               "required": [
                "sparkChartType"
               ],
               "type": "object"
              },
              "thresholds": {
               "description": "The thresholds used to determine the state of the scorecard given the\n time series' current value. For an actual value x, the scorecard is in a\n danger state if x is less than or equal to a danger threshold that triggers\n below, or greater than or equal to a danger threshold that triggers above.\n Similarly, if x is above/below a warning threshold that triggers\n above/below, then the scorecard is in a warning state - unless x also puts\n it in a danger state. (Danger trumps warning.)\n\n\n As an example, consider a scorecard with the following four thresholds:\n\n\n ```\n {\n   value: 90,\n   category: 'DANGER',\n   trigger: 'ABOVE',\n },\n {\n   value: 70,\n   category: 'WARNING',\n   trigger: 'ABOVE',\n },\n {\n   value: 10,\n   category: 'DANGER',\n   trigger: 'BELOW',\n },\n {\n   value: 20,\n   category: 'WARNING',\n   trigger: 'BELOW',\n }\n ```\n\n\n Then: values less than or equal to 10 would put the scorecard in a DANGER\n state, values greater than 10 but less than or equal to 20 a WARNING state,\n values strictly between 20 and 70 an OK state, values greater than or equal\n to 70 but less than 90 a WARNING state, and values greater than or equal to\n 90 a DANGER state.",
               "items": {
                "properties": {
                 "color": {
                  "description": "The state color for this threshold. Color is not allowed in a XyChart.",
                  "type": "string"
                 },
                 "direction": {
                  "description": "The direction for the current threshold. Direction is not allowed in a XyChart.",
                  "type": "string"
                 },
                 "label": {
                  "description": "A label for the threshold.",
                  "type": "string"
                 },
                 "value": {
                  "description": "The value of the threshold. The value should be defined in the native scale of the metric.",
                  "format": "double",
                  "type": "number"
                 }
                },
                "type": "object"
               },
               "type": "array"
              },
              "timeSeriesQuery": {
               "description": "Required. Fields for querying time series data from the Stackdriver metrics API.",
               "properties": {
                "timeSeriesFilter": {
                 "description": "Filter parameters to fetch time series.",
                 "properties": {
                  "aggregation": {
                   "description": "By default, the raw time series data is returned. Use this field to combine multiple time series for different views of the data.",
                   "properties": {
                    "alignmentPeriod": {
                     "description": "The `alignment_period` specifies a time interval, in seconds, that is used\n to divide the data in all the\n [time series][google.monitoring.v3.TimeSeries] into consistent blocks of\n time. This will be done before the per-series aligner can be applied to\n the data.\n\n\n The value must be at least 60 seconds. If a per-series aligner other than\n `ALIGN_NONE` is specified, this field is required or an error is returned.\n If no per-series aligner is specified, or the aligner `ALIGN_NONE` is\n specified, then this field is ignored.\n\n\n The maximum value of the `alignment_period` is 2 years, or 104 weeks.",
                     "type": "string"
                    },
                    "crossSeriesReducer": {
                     "description": "The reduction operation to be used to combine time series into a single\n time series, where the value of each data point in the resulting series is\n a function of all the already aligned values in the input time series.\n\n\n Not all reducer operations can be applied to all time series. The valid\n choices depend on the `metric_kind` and the `value_type` of the original\n time series. Reduction can yield a time series with a different\n `metric_kind` or `value_type` than the input time series.\n\n\n Time series data must first be aligned (see `per_series_aligner`) in order\n to perform cross-time series reduction. If `cross_series_reducer` is\n specified, then `per_series_aligner` must be specified, and must not be\n `ALIGN_NONE`. An `alignment_period` must also be specified; otherwise, an\n error is returned.",
                     "type": "string"
                    },
                    "groupByFields": {
                     "description": "The set of fields to preserve when `cross_series_reducer` is specified. The `group_by_fields` determine how the time series are partitioned into subsets prior to applying the aggregation operation. Each subset contains time series that have the same value for each of the grouping fields. Each individual time series is a member of exactly one subset. The `cross_series_reducer` is applied to each subset of time series. It is not possible to reduce across different resource types, so this field implicitly contains `resource.type`.  Fields not specified in `group_by_fields` are aggregated away.  If `group_by_fields` is not specified and all the time series have the same resource type, then the time series are aggregated into a single output time series. If `cross_series_reducer` is not defined, this field is ignored.",
                     "items": {
                      "type": "string"
                     },
                     "type": "array"
                    },
                    "perSeriesAligner": {
                     "description": "An `Aligner` describes how to bring the data points in a single\n time series into temporal alignment. Except for `ALIGN_NONE`, all\n alignments cause all the data points in an `alignment_period` to be\n mathematically grouped together, resulting in a single data point for\n each `alignment_period` with end timestamp at the end of the period.\n\n\n Not all alignment operations may be applied to all time series. The valid\n choices depend on the `metric_kind` and `value_type` of the original time\n series. Alignment can change the `metric_kind` or the `value_type` of\n the time series.\n\n\n Time series data must be aligned in order to perform cross-time\n series reduction. If `cross_series_reducer` is specified, then\n `per_series_aligner` must be specified and not equal to `ALIGN_NONE`\n and `alignment_period` must be specified; otherwise, an error is\n returned.",
                     "type": "string"
                    }
                   },
                   "type": "object"
                  },
                  "filter": {
                   "description": "Required. The [monitoring filter](https://cloud.google.com/monitoring/api/v3/filters) that identifies the metric types, resources, and projects to query.",
                   "type": "string"
                  },
                  "pickTimeSeriesFilter": {
                   "description": "Ranking based time series filter.",
                   "properties": {
                    "direction": {
                     "description": "How to use the ranking to select time series that pass through the filter.",
                     "type": "string"
                    },
                    "numTimeSeries": {
                     "description": "How many time series to allow to pass through the filter.",
                     "format": "int32",
                     "type": "integer"
                    },
                    "rankingMethod": {
                     "description": "`ranking_method` is applied to each time series independently to produce the value which will be used to compare the time series to other time series.",
                     "type": "string"
                    }
                   },
                   "type": "object"
                  },
                  "secondaryAggregation": {
                   "description": "Apply a second aggregation after `aggregation` is applied.",
                   "properties": {
                    "alignmentPeriod": {
                     "description": "The `alignment_period` specifies a time interval, in seconds, that is used\n to divide the data in all the\n [time series][google.monitoring.v3.TimeSeries] into consistent blocks of\n time. This will be done before the per-series aligner can be applied to\n the data.\n\n\n The value must be at least 60 seconds. If a per-series aligner other than\n `ALIGN_NONE` is specified, this field is required or an error is returned.\n If no per-series aligner is specified, or the aligner `ALIGN_NONE` is\n specified, then this field is ignored.\n\n\n The maximum value of the `alignment_period` is 2 years, or 104 weeks.",
