schema:
{
 "description": "MongoDBDatabase is the Schema for the mongodbs API",
 "properties": {
  "spec": {
   "description": "MongoDBDatabaseSpec defines the desired state of MongoDBDatabase",
   "properties": {
    "address": {
     "description": "The connect URI",
     "type": "string"
    },
    "atlasGroupId": {
     "type": "string"
    },
    "databaseName": {
     "description": "DatabaseName is by default the same as metata.name",
     "type": "string"
    },
    "rootSecret": {
     "description": "Contains a credentials set of a user with enough permission to manage databases and user accounts",
     "properties": {
      "name": {
       "description": "Name referrs to the name of the secret, must be located whithin the same namespace",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace, by default the same namespace is used.",
       "type": "string"
      },
      "passwordField": {
       "default": "password",
       "type": "string"
      },
      "userField": {
       "default": "username",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    }
   },
   "required": [
    "rootSecret"
   ],
   "type": "object"
  }
 },
 "title": "Mongo DB Database",
 "type": "object"
}
