component.schema:
{
 "description": "Stargate is the Schema for the stargates API",
 "properties": {
  "spec": {
   "description": "Specification of the desired behavior of this Stargate resource.",
   "properties": {
    "affinity": {
     "description": "Affinity is the affinity to apply to all the Stargate pods.\nLeave nil to let the controller reuse the same affinity rules used for data pods in this datacenter, if any.\nSee https://kubernetes.io/docs/concepts/scheduling-eviction/assign-pod-node/#affinity-and-anti-affinity",
