schema:
{
 "description": "K8ssandraTask is the Schema for the k8ssandratasks API",
 "properties": {
  "spec": {
   "description": "K8ssandraTaskSpec defines the desired state of K8ssandraTask",
   "properties": {
    "cluster": {
     "description": "Which K8ssandraCluster this task is operating on.",
     "properties": {
      "apiVersion": {
       "description": "API version of the referent.",
       "type": "string"
      },
      "fieldPath": {
       "description": "If referring to a piece of an object instead of an entire object, this string should contain a valid JSON/Go field access statement, such as desiredState.manifest.containers[2]. For example, if the object reference is to a container within a pod, this would take on a value like: \"spec.containers{name}\" (where \"name\" refers to the name of the container that triggered the event) or if no container name is specified \"spec.containers[2]\" (container with index 2 in this pod). This syntax is chosen only to have some well-defined way of referencing a part of an object. TODO: this design is not final and this field is subject to change in the future.",
       "type": "string"
      },
      "kind": {
       "description": "Kind of the referent. More info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#types-kinds",
       "type": "string"
      },
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/namespaces/",
       "type": "string"
      },
      "resourceVersion": {
       "description": "Specific resourceVersion to which this reference is made, if any. More info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#concurrency-control-and-consistency",
       "type": "string"
      },
      "uid": {
       "description": "UID of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#uids",
       "type": "string"
      }
     },
     "type": "object",
     "x-kubernetes-map-type": "atomic"
    },
    "datacenters": {
     "description": "The names of the targeted datacenters. If omitted, will default to all DCs in spec order.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "dcConcurrencyPolicy": {
     "description": "How to handle concurrency across DCs. Valid values are: - \"Forbid\" (default): sequential processing. The K8ssandraTask only spawns one CassandraTask at a time, which must run to completion before the CassandraTask for the next DC is created. If any CassandraTask fails, the K8ssandraTask is marked as failed, and the remaining CassandraTasks are cancelled (i.e. never created). - \"Allow\": parallel processing. The K8ssandraTask spawns all CassandraTasks at once. If any CassandraTask fails, the K8ssandraTask is marked as failed, but the remaining CassandraTasks finish running.",
     "type": "string"
    },
    "template": {
     "description": "The characteristics of the CassandraTask that will get created for each DC.",
     "properties": {
      "concurrencyPolicy": {
       "description": "Specifics if this task can be run concurrently with other active tasks. Valid values are: - \"Allow\": allows multiple Tasks to run concurrently on Cassandra cluster - \"Forbid\" (default): only a single task is executed at once The \"Allow\" property is only valid if all the other active Tasks have \"Allow\" as well.",
       "type": "string"
      },
      "jobs": {
       "description": "Jobs defines the jobs this task will execute (and their order)",
       "items": {
        "properties": {
         "args": {
          "description": "Arguments are additional parameters for the command",
          "properties": {
           "keyspace_name": {
            "type": "string"
           },
           "new_tokens": {
            "additionalProperties": {
             "type": "string"
            },
            "description": "NewTokens is a map of pod names to their newly-assigned tokens. Required for the move command, ignored otherwise. Pods referenced in this map must exist; any existing pod not referenced in this map will not be moved.",
            "type": "object"
           },
           "pod_name": {
            "type": "string"
           },
           "rack": {
            "type": "string"
           },
           "source_datacenter": {
            "type": "string"
           }
          },
          "type": "object"
         },
         "command": {
          "description": "Command defines what is run against Cassandra pods",
          "type": "string"
         },
         "name": {
          "type": "string"
         }
        },
        "required": [
         "command",
         "name"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "restartPolicy": {
       "description": "RestartPolicy indicates the behavior n case of failure. Default is Never.",
       "type": "string"
      },
      "scheduledTime": {
       "description": "ScheduledTime indicates the earliest possible time this task is executed. This does not necessarily equal to the time it is actually executed (if other tasks are blocking for example). If not set, the task will be executed immediately.",
       "format": "date-time",
       "type": "string"
      },
      "ttlSecondsAfterFinished": {
       "description": "TTLSecondsAfterFinished defines how long the completed job will kept before being cleaned up. If set to 0 the task will not be cleaned up by the cass-operator. If unset, the default time (86400s) is used.",
       "format": "int32",
       "type": "integer"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "K8ssandra Task",
 "type": "object"
}
