schema:
{
 "description": "ReplicatedSecret is the Schema for the replicatedsecrets API",
 "properties": {
  "spec": {
   "description": "ReplicatedSecretSpec defines the desired state of ReplicatedSecret",
   "properties": {
    "replicationTargets": {
     "description": "TargetContexts indicates the target clusters to which the secrets are replicated to. If empty, no clusters are targeted",
     "items": {
      "properties": {
       "k8sContextName": {
        "description": "K8sContextName defines the target cluster name as set in the ClientConfig. If left empty, current cluster is assumed",
        "type": "string"
       },
       "namespace": {
        "description": "TODO Implement at some point Namespace to replicate the data to in the target cluster. If left empty, current namespace is used.",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "selector": {
     "description": "Selector defines which secrets are replicated. If left empty, all the secrets are replicated",
     "properties": {
      "matchExpressions": {
       "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
       "items": {
        "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
        "properties": {
         "key": {
          "description": "key is the label key that the selector applies to.",
          "type": "string"
         },
         "operator": {
          "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
          "type": "string"
         },
         "values": {
          "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "required": [
         "key",
         "operator"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "matchLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
       "type": "object"
      }
     },
     "type": "object",
     "x-kubernetes-map-type": "atomic"
    }
   },
   "type": "object"
  }
 },
 "title": "Replicated Secret",
 "type": "object"
}
