component.schema:
{
 "description": "K8ssandraCluster is the Schema for the k8ssandraclusters API. The K8ssandraCluster CRD name is also the name of the\nCassandra cluster (which corresponds to cluster_name in cassandra.yaml).",
 "properties": {
  "spec": {
   "description": "K8ssandraClusterSpec defines the desired state of K8ssandraCluster",
   "properties": {
    "auth": {
     "default": true,
     "description": "Whether to enable authentication in this cluster. The default is true; it is highly recommended to always leave\nauthentication turned on. When enabled, authentication will be enforced not only on Cassandra nodes, but also on\nReaper, Medusa and Stargate nodes, if any.",
     "type": "boolean"
    },
    "cassandra": {
     "description": "Cassandra is a specification of the Cassandra cluster. This includes everything from\nthe number of datacenters, the k8s cluster where each DC should be deployed, node\naffinity (via racks), individual C* node settings, JVM settings, and more.",
     "properties": {
      "additionalSeeds": {
       "description": "AdditionalSeeds specifies Cassandra node IPs for an existing datacenter. This is\nprimarily intended for migrations from an existing Cassandra cluster that is not\nmanaged by k8ssandra-operator. Note that this property should NOT be used to set\nseeds for a DC that is or will be managed by k8ssandra-operator. k8ssandra-operator\nalready manages seeds for DCs that it manages. If you have DNS set up such that you\ncan resolve hostnames for the remote Cassandra cluster, then you can specify hostnames\nhere; otherwise, use IP addresses.",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "cdc": {
       "description": "CDC defines the desired state for CDC integrations. It can be used to feed mutation events from Cassandra into an Apache Pulsar cluster,\nfrom where they can be expored to external systems.",
       "properties": {
        "cdcConcurrentProcessors": {
         "type": "integer"
        },
        "cdcPollIntervalM": {
         "type": "integer"
        },
        "cdcWorkingDir": {
         "type": "string"
        },
        "errorCommitLogReprocessEnabled": {
         "type": "boolean"
        },
        "pulsarAuthParams": {
         "type": "string"
        },
        "pulsarAuthPluginClassName": {
         "type": "string"
        },
        "pulsarBatchDelayInMs": {
         "type": "integer"
        },
        "pulsarKeyBasedBatcher": {
         "type": "boolean"
        },
        "pulsarMaxPendingMessages": {
         "type": "integer"
        },
        "pulsarMaxPendingMessagesAcrossPartitions": {
         "type": "integer"
        },
        "pulsarServiceUrl": {
         "minLength": 1,
         "type": "string"
        },
        "sslAllowInsecureConnection": {
         "type": "string"
        },
        "sslCipherSuites": {
         "type": "string"
        },
        "sslEnabledProtocols": {
         "type": "string"
        },
        "sslHostnameVerificationEnable": {
         "type": "string"
        },
        "sslKeystorePassword": {
         "type": "string"
        },
        "sslKeystorePath": {
         "type": "string"
        },
        "sslProvider": {
         "type": "string"
        },
        "sslTruststorePassword": {
         "type": "string"
        },
        "sslTruststorePath": {
         "type": "string"
        },
        "sslTruststoreType": {
         "type": "string"
        },
        "topicPrefix": {
         "type": "string"
        }
       },
       "required": [
        "pulsarServiceUrl"
       ],
       "type": "object"
      },
      "clientEncryptionStores": {
       "description": "Client encryption stores which are used by Cassandra and Reaper.",
       "properties": {
        "keystorePasswordSecretRef": {
         "description": "ref to the secret that contains the keystore password if password stored in different secret than keystoreSecretRef\nif key isn't specified explicitly, \"keystore-password\" entry will be used",
         "properties": {
          "key": {
           "description": "The key of the entry in the Secret resource's `data` field to be used.",
           "type": "string"
          },
          "name": {
           "description": "Name of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names\nTODO: Add other useful fields. apiVersion, kind, uid?",
           "type": "string"
          }
         },
         "type": "object",
         "x-kubernetes-map-type": "atomic"
        },
        "keystoreSecretRef": {
         "description": "ref to the secret that contains the keystore and optionally its password (which can also be specified through\nthe keystorePasswordSecretRef field)\nif keys are not specified, \"keystore\" entry and a \"keystore-password\" entry will be used",
         "properties": {
          "key": {
           "description": "The key of the entry in the Secret resource's `data` field to be used.",
           "type": "string"
          },
          "name": {
           "description": "Name of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names\nTODO: Add other useful fields. apiVersion, kind, uid?",
           "type": "string"
          }
         },
         "type": "object",
         "x-kubernetes-map-type": "atomic"
        },
        "truststorePasswordSecretRef": {
         "description": "ref to the secret that contains the truststore password if password stored in different secret than keystoreSecretRef\nif key isn't specified  explicitly, \"truststore-password\" entry will be used",
         "properties": {
          "key": {
           "description": "The key of the entry in the Secret resource's `data` field to be used.",
           "type": "string"
          },
          "name": {
           "description": "Name of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names\nTODO: Add other useful fields. apiVersion, kind, uid?",
           "type": "string"
          }
         },
         "type": "object",
         "x-kubernetes-map-type": "atomic"
        },
        "truststoreSecretRef": {
         "description": "ref to the secret that contains the truststore and optionally its password (which can also be specified through\nthe truststorePasswordSecretRef field)\nif keys are not specified explicitly, \"keystore\" entry and a \"keystore-password\" entry will be used",
         "properties": {
          "key": {
           "description": "The key of the entry in the Secret resource's `data` field to be used.",
           "type": "string"
          },
          "name": {
           "description": "Name of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names\nTODO: Add other useful fields. apiVersion, kind, uid?",
           "type": "string"
          }
         },
         "type": "object",
         "x-kubernetes-map-type": "atomic"
        }
       },
       "required": [
        "keystoreSecretRef",
        "truststoreSecretRef"
       ],
       "type": "object"
      },
      "clusterName": {
       "description": "Override the Cassandra cluster name. If unspecified, the cluster name will be the same as the K8ssandraCluster\nCRD name.",
       "type": "string"
      },
      "config": {
       "description": "CassandraConfig contains configuration settings that are applied to cassandra.yaml, dse.yaml\nand the various jvm*.options files.",
       "properties": {
        "cassandraYaml": {
         "format": "textarea",
         "type": "string"
        },
        "dseYaml": {
         "format": "textarea",
         "type": "string"
        },
        "jvmOptions": {
         "properties": {
          "additionalJvm11ServerOptions": {
           "description": "Jvm11ServerOptions are additional options that will be passed on to the jvm11-server-options file.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "additionalJvm8ServerOptions": {
           "description": "Jvm8ServerOptions are additional options that will be passed on to the jvm8-server-options file.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "additionalJvmServerOptions": {
           "description": "JvmServerOptions are additional options that will be passed on to the jvm-server-options file.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "additionalOptions": {
           "description": "Additional, arbitrary JVM options which are written into the cassandra-env.sh file.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "cassandra_available_processors": {
           "description": "Available CPU processors.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.available_processors.",
           "type": "integer"
          },
          "cassandra_disable_auth_caches_remote_configuration": {
           "description": "Disable remote configuration via JMX of auth caches.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.disable_auth_caches_remote_configuration.",
           "type": "boolean"
          },
          "cassandra_expiration_date_overflow_policy": {
           "description": "Defines how to handle INSERT requests with TTL exceeding the maximum supported expiration\ndate.\nPossible values include `REJECT`, `CAP`, `CAP_NOWARN`.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.expiration_date_overflow_policy.",
           "type": "string"
          },
          "cassandra_force_3_0_protocol_version": {
           "description": "Disabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -Dcassandra.force_3_0_protocol_version=true.",
           "type": "boolean"
          },
          "cassandra_force_default_indexing_page_size": {
           "description": "Disable dynamic calculation of the page size used when indexing an entire partition (during\ninitial index build/rebuild).\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.force_default_indexing_page_size.",
           "type": "boolean"
          },
          "cassandra_max_hint_ttl_seconds": {
           "description": "Imposes an upper bound on hint lifetime below the normal min gc_grace_seconds.\nDisabled by default.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.maxHintTTL.",
           "type": "integer"
          },
          "cassandra_metrics_reporter_config_file": {
           "description": "Enable pluggable metrics reporter.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.metricsReporterConfigFile.\nTODO mountable directory",
           "type": "string"
          },
          "cassandra_ring_delay_ms": {
           "description": "Amount of time in milliseconds that a node waits before joining the ring.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.ring_delay_ms.",
           "type": "integer"
          },
          "cassandra_triggers_directory": {
           "description": "Default location for the trigger JARs.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.triggers_dir.\nTODO mountable directory",
           "type": "string"
          },
          "cassandra_write_survey": {
           "description": "For testing new compaction and compression strategies.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Dcassandra.write_survey.",
           "type": "boolean"
          },
          "debug_disable_contended_annotations": {
           "description": "Disable honoring user code @Contended annotations.\nEnabled by default.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -XX:-RestrictContended.",
           "type": "boolean"
          },
          "debug_enable_flight_recorder": {
           "description": "Enable Flight Recorder (Use in production is subject to Oracle licensing).\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -XX:+FlightRecorder.",
           "type": "boolean"
          },
          "debug_listen_remote_debuggers": {
           "description": "Listen for JVM remote debuggers on port 1414.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -agentlib:jdwp=transport=dt_socket,server=y,suspend=n,address=1414\".",
           "type": "boolean"
          },
          "debug_log_compilation": {
           "description": "Make Cassandra JVM log internal method compilation (developers only).\nDisabled by default.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -XX:+LogCompilation.",
           "type": "boolean"
          },
          "debug_non_safepoints": {
           "description": "Whether the compiler should generate the necessary metadata for the parts of the code not at\nsafe points as well. For use with Flight Recorder.\nEnabled by default.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -XX:+DebugNonSafepoints.",
           "type": "boolean"
          },
          "debug_preserve_frame_pointer": {
           "description": "Preserve Frame Pointer.\nEnabled by default.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -XX:+PreserveFramePointer.",
           "type": "boolean"
          },
          "debug_unlock_commercial_features": {
           "description": "Unlock commercial features.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -XX:+UnlockCommercialFeatures.",
           "type": "boolean"
          },
          "debug_unlock_diagnostic_vm_options": {
           "description": "Enabled by default.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -XX:+UnlockDiagnosticVMOptions.",
           "type": "boolean"
          },
          "gc": {
           "default": "G1GC",
           "description": "The name of the garbage collector to use. Depending on the Cassandra version, not all values\nare supported: Cassandra 3.11 supports only G1GC and CMS; Cassandra 4.0 supports G1GC, ZGC,\nShenandoah and Graal. This option will unlock the corresponding garbage collector with a\ndefault configuration; to further tune the GC settings, use the additional JVM options field.\nUse the special value Custom if you intend to use non-standard garbage collectors.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm11-server.options.",
           "enum": [
            "G1GC",
            "CMS",
            "ZGC",
            "Shenandoah",
            "Graal",
            "Custom"
           ],
           "type": "string"
          },
          "gc_cms_heap_size_young_generation": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "description": "Disabled by default. Can only be used when CMS garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -Xmn.",
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "gc_cms_initiating_occupancy_fraction": {
           "description": "Defaults to 75. Can only be used when CMS garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:CMSInitiatingOccupancyFraction.",
           "maximum": 100,
           "minimum": 0,
           "type": "integer"
          },
          "gc_cms_max_tenuring_threshold": {
           "description": "Defaults to 1. Can only be used when CMS garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:MaxTenuringThreshold.",
           "type": "integer"
          },
          "gc_cms_survivor_ratio": {
           "description": "Defaults to 8. Can only be used when CMS garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:SurvivorRatio.",
           "type": "integer"
          },
          "gc_cms_wait_duration_ms": {
           "description": "Defaults to 10000. Can only be used when CMS garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:CMSWaitDuration.",
           "type": "integer"
          },
          "gc_g1_conc_threads": {
           "description": "Concurrent GC Threads. Can only be used when G1 garbage collector is used.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm11-server.options.\nCorresponds to: -XX:ConcGCThreads.",
           "type": "integer"
          },
          "gc_g1_initiating_heap_occupancy_percent": {
           "description": "Initiating Heap Occupancy Percentage. Can only be used when G1 garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm11-server.options.\nCorresponds to: -XX:InitiatingHeapOccupancyPercent.",
           "maximum": 100,
           "minimum": 0,
           "type": "integer"
          },
          "gc_g1_max_gc_pause_ms": {
           "description": "G1GC Max GC Pause in milliseconds. Defaults to 500. Can only be used when G1 garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm11-server.options.\nCorresponds to: -XX:MaxGCPauseMillis.",
           "type": "integer"
          },
          "gc_g1_parallel_threads": {
           "description": "Parallel GC Threads. Can only be used when G1 garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm11-server.options.\nCorresponds to: -XX:ParallelGCThreads.",
           "type": "integer"
          },
          "gc_g1_rset_updating_pause_time_percent": {
           "description": "G1GC Updating Pause Time Percentage. Defaults to 5. Can only be used when G1 garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm11-server.options.\nCorresponds to: -XX:G1RSetUpdatingPauseTimePercent.",
           "maximum": 100,
           "minimum": 0,
           "type": "integer"
          },
          "gc_print_application_stopped_time": {
           "description": "Print GC Application Stopped Time.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:+PrintGCApplicationStoppedTime.",
           "type": "boolean"
          },
          "gc_print_date_stamps": {
           "description": "Print GC Date Stamps. Disabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:+PrintGCDateStamps.",
           "type": "boolean"
          },
          "gc_print_details": {
           "description": "Print GC details.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:+PrintGCDetails.",
           "type": "boolean"
          },
          "gc_print_flss_statistics": {
           "description": "Print FLSS Statistics.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:PrintFLSStatistics=1.",
           "type": "boolean"
          },
          "gc_print_heap": {
           "description": "Print Heap at GC.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:+PrintHeapAtGC.",
           "type": "boolean"
          },
          "gc_print_log_file_size": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "description": "Size of each log file.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:GCLogFileSize.",
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "gc_print_number_of_log_files": {
           "description": "Number of GC log files.\nDisabled by default. Can only be used when the G1 garbage collector is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:NumberOfGCLogFiles.",
           "type": "integer"
          },
          "gc_print_promotion_failure": {
           "description": "Print promotion failure.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:+PrintPromotionFailure.",
           "type": "boolean"
          },
          "gc_print_tenuring_distribution": {
           "description": "Print tenuring distribution.\nDefaults to false.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:+PrintTenuringDistribution.",
           "type": "boolean"
          },
          "gc_print_use_log_file": {
           "description": "Whether to print GC logs to /var/log/cassandra/gc.log.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -Xloggc:/var/log/cassandra/gc.log.",
           "type": "boolean"
          },
          "gc_print_use_log_file_rotation": {
           "description": "Use GC Log File Rotation.\nDisabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCorresponds to: -XX:+UseGCLogFileRotation.",
           "type": "boolean"
          },
          "heapNewGenSize": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "description": "Deprecated. Use gc_cms_heap_size_young_generation instead.\nValid for CMS garbage collector only + Cassandra 3.11.",
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "heapSize": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "description": "Deprecated. Use heap_initial_size and heap_max_size instead. If this field is defined,\nit applies to both max_heap_size and initial_heap_size.",
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "heap_initial_size": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "description": "Disabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Xms.",
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "heap_max_size": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "description": "Disabled by default.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nCorresponds to: -Xmx.",
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "jdk_allow_attach_self": {
           "description": "Allow the current VM to attach to itself.\nDefaults to true.\nCass Config Builder: supported for Cassandra 4.0 in jvm11-server.options.\nCorresponds to: -Djdk.attach.allowAttachSelf=true.",
           "type": "boolean"
          },
          "jmx_connection_type": {
           "description": "Cass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nPossible values for 3.11 include `local-no-auth`, `remote-no-auth`, and `remote-dse-unified-auth`. Defaults to `local-no-auth`.\nPossible values for 4.0 include `local-no-auth`, `remote-no-auth`. Defaults to `local-no-auth`.",
           "type": "string"
          },
          "jmx_port": {
           "description": "Disabled by default. Defaults to 7199.\nTODO Make Reaper aware of the JMX port if a non-default port is used.\nCass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.",
           "type": "integer"
          },
          "jmx_remote_ssl": {
           "description": "Cass Config Builder: supported for Cassandra 3.11 in jvm.options.\nCass Config Builder: supported for Cassandra 4.0 in jvm-server.options.\nDefaults to false.\nValid only when JmxConnectionType is \"remote-no-auth\", \"remote-dse-unified-auth\".",
           "type": "boolean"
          },
          "jmx_remote_ssl_opts": {
