component.schema:
{
 "description": "MedusaBackupSchedule is the Schema for the medusabackupschedules API",
 "properties": {
  "spec": {
   "description": "MedusaBackupScheduleSpec defines the desired state of MedusaBackupSchedule",
   "properties": {
    "backupSpec": {
     "description": "BackupSpec defines the MedusaBackup to be created for this job",
     "properties": {
      "backupType": {
       "default": "differential",
       "description": "The type of the backup: \"full\" or \"differential\"",
       "enum": [
        "differential",
        "full"
       ],
       "type": "string"
      },
      "cassandraDatacenter": {
       "description": "The name of the CassandraDatacenter to back up",
       "minLength": 1,
       "type": "string"
      }
     },
     "required": [
      "cassandraDatacenter"
     ],
     "type": "object"
    },
    "concurrencyPolicy": {
     "description": "Specifics if this backup task can be run concurrently with other active backup tasks. Valid values are:\n- \"Allow\": allows multiple Tasks to run concurrently on Cassandra cluster\n- \"Forbid\" (default): only a single task is executed at once\nThe \"Allow\" property is only valid if all the other active Tasks have \"Allow\" as well.",
     "type": "string"
    },
    "cronSchedule": {
     "description": "CronSchedule is a cronjob format schedule for backups. Overrides any easier methods of defining the schedule",
     "minLength": 1,
     "type": "string"
    },
    "disabled": {
     "description": "Disabled if set ensures this job is not scheduling anything",
     "type": "boolean"
    },
    "operationType": {
     "default": "backup",
     "description": "Specifies the type of operation to be performed",
     "enum": [
      "backup",
      "purge"
     ],
     "type": "string"
    }
   },
   "required": [
    "backupSpec",
    "cronSchedule"
   ],
   "type": "object"
  }
 },
 "title": "Medusa Backup Schedule",
 "type": "object"
}
