component.schema:
{
 "description": "ReplicatedSecret is the Schema for the replicatedsecrets API",
 "properties": {
  "spec": {
   "description": "ReplicatedSecretSpec defines the desired state of ReplicatedSecret",
   "properties": {
    "replicationTargets": {
     "description": "TargetContexts indicates the target clusters to which the secrets are replicated to. If empty, no clusters are targeted",
     "items": {
      "properties": {
       "addLabels": {
        "additionalProperties": {
         "type": "string"
        },
        "description": "AddLabels adds labels to the target secret.",
        "type": "object"
       },
       "dropLabels": {
        "description": "DropLabels defines the labels to be dropped from the secret before replication, this is sometimes neccessary to avoid infinite replication.",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "k8sContextName": {
        "description": "K8sContextName defines the target cluster name as set in the ClientConfig. If left empty, current cluster is assumed",
        "type": "string"
       },
       "namespace": {
        "description": "TODO Implement at some point\nNamespace to replicate the data to in the target cluster. If left empty, current namespace is used.",
        "type": "string"
       },
       "targetPrefix": {
        "description": "TargetPrefix is the prefix to be used for the replicated secret in the target cluster. If left empty, the same name is used\nas the original secret.",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "selector": {
     "description": "Selector defines which secrets are replicated. If left empty, all the secrets are replicated",
     "properties": {
      "matchExpressions": {
       "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
       "items": {
        "description": "A label selector requirement is a selector that contains values, a key, and an operator that\nrelates the key and values.",
        "properties": {
         "key": {
          "description": "key is the label key that the selector applies to.",
          "type": "string"
         },
         "operator": {
          "description": "operator represents a key's relationship to a set of values.\nValid operators are In, NotIn, Exists and DoesNotExist.",
          "type": "string"
         },
         "values": {
          "description": "values is an array of string values. If the operator is In or NotIn,\nthe values array must be non-empty. If the operator is Exists or DoesNotExist,\nthe values array must be empty. This array is replaced during a strategic\nmerge patch.",
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "required": [
         "key",
         "operator"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "matchLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels\nmap is equivalent to an element of matchExpressions, whose key field is \"key\", the\noperator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
       "type": "object"
      }
     },
     "type": "object",
     "x-kubernetes-map-type": "atomic"
    }
   },
   "type": "object"
  }
 },
 "title": "Replicated Secret",
 "type": "object"
}
