component.schema:
{
 "description": "MedusaConfiguration is the Schema for the medusaconfigurations API",
 "properties": {
  "spec": {
   "description": "MedusaConfigurationSpec defines the desired state of MedusaConfiguration",
   "properties": {
    "storageProperties": {
     "description": "StorageProperties defines the storage backend settings to use for the backups.",
     "properties": {
      "apiProfile": {
       "description": "AWS Profile to use for authentication.",
       "type": "string"
      },
      "backupGracePeriodInDays": {
       "description": "Age after which orphan sstables can be deleted from the storage backend.\nProtects from race conditions between purge and ongoing backups.\nDefaults to 10 days.",
       "type": "integer"
      },
      "bucketName": {
       "description": "The name of the bucket to use for the backups.",
       "type": "string"
      },
      "concurrentTransfers": {
       "default": 0,
       "description": "Number of concurrent uploads.\nHelps maximizing the speed of uploads but puts more pressure on the network.\nDefaults to 0.",
       "type": "integer"
      },
      "credentialsType": {
       "description": "Type of credentials to use for authentication.\nCan be \"role-based\", \"credential-based\" or empty.",
       "enum": [
        "role-based",
        "credential-based"
       ],
       "type": "string"
      },
      "host": {
       "description": "Host to connect to for the storage backend.",
       "type": "string"
      },
      "maxBackupAge": {
       "default": 0,
       "description": "Maximum backup age that the purge process should observe.",
       "type": "integer"
      },
      "maxBackupCount": {
       "default": 0,
       "description": "Maximum number of backups to keep (used by the purge process).\nDefault is unlimited.",
       "type": "integer"
      },
      "multiPartUploadThreshold": {
       "default": 104857600,
       "description": "File size over which cloud specific cli tools are used for transfer.\nDefaults to 100 MB.",
       "type": "integer"
      },
      "podStorage": {
       "description": "Pod storage settings for the local storage provider",
       "properties": {
        "accessModes": {
         "description": "Pod local storage access modes",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "size": {
         "anyOf": [
          {
           "type": "integer"
          },
          {
           "type": "string"
          }
         ],
         "default": "10Gi",
         "description": "Size of the pod's storage in bytes.\nDefaults to 10 GB.",
         "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
         "x-kubernetes-int-or-string": true
        },
        "storageClassName": {
         "description": "Storage class name to use for the pod's storage.",
         "type": "string"
        }
       },
       "type": "object"
      },
      "port": {
       "description": "Port to connect to for the storage backend.",
       "type": "integer"
      },
      "prefix": {
       "description": "Name of the top level folder in the backup bucket.\nIf empty, the cluster name will be used.",
       "type": "string"
      },
      "region": {
       "description": "Region of the storage bucket.\nDefaults to \"default\".",
       "type": "string"
      },
      "secure": {
       "description": "Whether to use SSL for the storage backend.",
       "type": "boolean"
      },
      "sslVerify": {
       "description": "When using SSL, whether to also verify the certificate.",
       "type": "boolean"
      },
      "storageProvider": {
       "description": "The storage backend to use for the backups.",
       "enum": [
        "google_storage",
        "azure_blobs",
        "s3",
        "s3_compatible",
        "s3_rgw",
        "ibm_storage"
       ],
       "type": "string"
      },
      "storageSecretRef": {
       "description": "Kubernetes Secret that stores the key file for the storage provider's API.\nIf using 'local' storage, this value is ignored.",
       "properties": {
        "name": {
         "description": "Name of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names\nTODO: Add other useful fields. apiVersion, kind, uid?",
         "type": "string"
        }
       },
       "type": "object",
       "x-kubernetes-map-type": "atomic"
      },
      "transferMaxBandwidth": {
       "default": "50MB/s",
       "description": "Max upload bandwidth in MB/s.\nDefaults to 50 MB/s.",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Medusa Configuration",
 "type": "object"
}
