                 },
                 "type": "array"
                }
               },
               "required": [
                "key",
                "operator"
               ],
               "type": "object"
              },
              "type": "array"
             }
            },
            "type": "object",
            "x-kubernetes-map-type": "atomic"
           },
           "type": "array"
          }
         },
         "required": [
          "nodeSelectorTerms"
         ],
         "type": "object",
