schema:
{
 "description": "RepositoryServer manages the lifecycle of Kopia Repository Server within a Pod",
 "properties": {
  "spec": {
   "description": "Spec defines the spec of repository server. It has all the details required to start the kopia repository server",
   "properties": {
    "repository": {
     "description": "Repository has the details required by the repository server to connect to kopia repository",
     "properties": {
      "cacheSizeSettings": {
       "description": "CacheSizeSettings are the metadata/content cache size details that can be used while establishing connection to the kopia repository",
       "properties": {
        "content": {
         "description": "Content size should be in specified in MB",
         "type": "integer"
        },
        "metadata": {
         "description": "Metadata size should be in specified in MB",
         "type": "integer"
        }
       },
       "type": "object"
      },
      "configuration": {
       "description": "Configuration can be used to specify the optional fields used for repository operations",
       "properties": {
        "cacheDirectory": {
         "description": "CacheDirectory is an optional field to specify kopia cache directory",
         "type": "string"
        },
        "configFilePath": {
         "description": "ConfigFilePath is an optional field to specify kopia config file path",
         "type": "string"
        },
        "logDirectory": {
         "description": "LogDirectory is an optional field to specify kopia log directory",
         "type": "string"
        }
       },
       "type": "object"
      },
      "hostname": {
       "description": "If specified, these values will be used by the controller to override default hostname when connecting to the repository from the server.",
       "type": "string"
      },
      "passwordSecretRef": {
       "description": "PasswordSecretRef has the password required to connect to kopia repository",
       "properties": {
        "name": {
         "description": "name is unique within a namespace to reference a secret resource.",
         "type": "string"
        },
        "namespace": {
         "description": "namespace defines the space within which the secret name must be unique.",
         "type": "string"
        }
       },
       "type": "object",
       "x-kubernetes-map-type": "atomic"
      },
      "rootPath": {
       "description": "Path for the repository, it will be a relative sub path within the path prefix specified in the location More info: https://kopia.io/docs/reference/command-line/common/#commands-to-manipulate-repository",
       "type": "string",
       "x-kubernetes-validations": [
        {
         "message": "Value is immutable",
         "rule": "self == oldSelf"
        }
       ]
      },
      "username": {
       "description": "If specified, these values will be used by the controller to override default username when connecting to the repository from the server.",
       "type": "string"
      }
     },
     "required": [
      "passwordSecretRef",
      "rootPath"
     ],
     "type": "object",
     "x-kubernetes-validations": [
      {
       "message": "rootPath field must not be allowed to be removed",
       "rule": "!has(oldSelf.rootPath) || has(self.rootPath)"
      }
     ]
    },
    "server": {
     "description": "Server has the details of all the secrets required to start the kopia repository server",
     "properties": {
      "adminSecretRef": {
       "description": "AdminSecretRef has the username and password required to start the kopia repository server",
       "properties": {
        "name": {
         "description": "name is unique within a namespace to reference a secret resource.",
         "type": "string"
        },
        "namespace": {
         "description": "namespace defines the space within which the secret name must be unique.",
         "type": "string"
        }
       },
       "type": "object",
       "x-kubernetes-map-type": "atomic",
       "x-kubernetes-validations": [
        {
         "message": "Value is immutable",
         "rule": "self == oldSelf"
        }
       ]
      },
      "tlsSecretRef": {
       "description": "TLSSecretRef has the certificates required for kopia repository client server connection",
       "properties": {
        "name": {
         "description": "name is unique within a namespace to reference a secret resource.",
         "type": "string"
        },
        "namespace": {
         "description": "namespace defines the space within which the secret name must be unique.",
         "type": "string"
        }
       },
       "type": "object",
       "x-kubernetes-map-type": "atomic",
       "x-kubernetes-validations": [
        {
         "message": "Value is immutable",
         "rule": "self == oldSelf"
        }
       ]
      },
      "userAccess": {
       "description": "UserAccess has the details of the user credentials required by client to connect to kopia repository server",
       "properties": {
        "userAccessSecretRef": {
         "description": "UserAccessSecretRef stores the list of hostname and passwords used by kopia clients to connect to kopia repository server",
         "properties": {
          "name": {
           "description": "name is unique within a namespace to reference a secret resource.",
           "type": "string"
          },
          "namespace": {
           "description": "namespace defines the space within which the secret name must be unique.",
           "type": "string"
          }
         },
         "type": "object",
         "x-kubernetes-map-type": "atomic"
        },
        "username": {
         "description": "Username is the user required by client to connect to kopia repository server",
         "type": "string"
        }
       },
       "required": [
        "userAccessSecretRef",
        "username"
       ],
       "type": "object"
      }
     },
     "required": [
      "adminSecretRef",
      "tlsSecretRef",
      "userAccess"
     ],
     "type": "object",
     "x-kubernetes-validations": [
      {
       "message": "adminSecretRef field must not be allowed to be removed",
       "rule": "!has(oldSelf.adminSecretRef) || has(self.adminSecretRef)"
      },
      {
       "message": "tlsSecretRef field must not be allowed to be removed",
       "rule": "!has(oldSelf.tlsSecretRef) || has(self.tlsSecretRef)"
      }
     ]
    },
    "storage": {
     "description": "Storage references the backend store where a repository already exists and the credential necessary to connect to the backend store",
     "properties": {
      "credentialSecretRef": {
       "description": "CredentialSecretRef stores the credentials required to connect to the object storage specified in `SecretRef` field",
       "properties": {
        "name": {
         "description": "name is unique within a namespace to reference a secret resource.",
         "type": "string"
        },
        "namespace": {
         "description": "namespace defines the space within which the secret name must be unique.",
         "type": "string"
        }
       },
       "type": "object",
       "x-kubernetes-map-type": "atomic"
      },
      "secretRef": {
       "description": "SecretRef has the details of the object storage (location) where the kopia would backup the data",
       "properties": {
        "name": {
         "description": "name is unique within a namespace to reference a secret resource.",
         "type": "string"
        },
        "namespace": {
         "description": "namespace defines the space within which the secret name must be unique.",
         "type": "string"
        }
       },
       "type": "object",
       "x-kubernetes-map-type": "atomic",
       "x-kubernetes-validations": [
        {
         "message": "Value is immutable",
         "rule": "self == oldSelf"
        }
       ]
      }
     },
     "required": [
      "credentialSecretRef",
      "secretRef"
     ],
     "type": "object",
     "x-kubernetes-validations": [
      {
       "message": "secretRef field must not be allowed to be removed",
       "rule": "!has(oldSelf.secretRef) || has(self.secretRef)"
      }
     ]
    }
   },
   "required": [
    "repository",
    "server",
    "storage"
   ],
   "type": "object"
  }
 },
 "required": [
  "spec"
 ],
 "title": "Repository Server",
 "type": "object"
}
