component.schema:
{
 "description": "Karmada enables declarative installation of karmada.",
 "properties": {
  "spec": {
   "description": "Spec defines the desired behavior of the Karmada.",
   "properties": {
    "components": {
     "description": "Components define all of karmada components.\nnot all of these components need to be installed.",
     "properties": {
      "etcd": {
       "description": "Etcd holds configuration for etcd.",
       "properties": {
        "external": {
         "description": "External describes how to connect to an external etcd cluster\nLocal and External are mutually exclusive",
         "properties": {
          "caData": {
           "description": "CAData is an SSL Certificate Authority file used to secure etcd communication.\nRequired if using a TLS connection.",
           "format": "byte",
           "type": "string"
          },
          "certData": {
           "description": "CertData is an SSL certification file used to secure etcd communication.\nRequired if using a TLS connection.",
           "format": "byte",
           "type": "string"
          },
          "endpoints": {
           "description": "Endpoints of etcd members. Required for ExternalEtcd.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "keyData": {
           "description": "KeyData is an SSL key file used to secure etcd communication.\nRequired if using a TLS connection.",
           "format": "byte",
           "type": "string"
          }
         },
         "required": [
          "caData",
          "certData",
          "endpoints",
          "keyData"
         ],
         "type": "object"
        },
        "local": {
         "description": "Local provides configuration knobs for configuring the built-in etcd instance\nLocal and External are mutually exclusive",
         "properties": {
          "annotations": {
           "additionalProperties": {
            "type": "string"
           },
           "description": "Annotations is an unstructured key value map stored with a resource that may be\nset by external tools to store and retrieve arbitrary metadata. They are not\nqueryable and should be preserved when modifying objects.\nMore info: http://kubernetes.io/docs/user-guide/annotations",
           "type": "object"
          },
          "imagePullPolicy": {
           "description": "ImagePullPolicy defines the policy for pulling the container image.\nIf not specified, it defaults to IfNotPresent.",
           "type": "string"
          },
          "imageRepository": {
           "description": "ImageRepository sets the container registry to pull images from.\nif not set, the ImageRepository defined in KarmadaSpec will be used instead.",
           "type": "string"
          },
          "imageTag": {
           "description": "ImageTag allows to specify a tag for the image.\nIn case this value is set, operator does not change automatically the version\nof the above components during upgrades.",
           "type": "string"
          },
          "labels": {
           "additionalProperties": {
            "type": "string"
           },
           "description": "Map of string keys and values that can be used to organize and categorize\n(scope and select) objects. May match selectors of replication controllers\nand services.\nMore info: http://kubernetes.io/docs/user-guide/labels",
           "type": "object"
          },
          "peerCertSANs": {
           "description": "PeerCertSANs sets extra Subject Alternative Names for the etcd peer signing cert.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "replicas": {
           "description": "Number of desired pods. This is a pointer to distinguish between explicit\nzero and not specified. Defaults to 1.",
           "format": "int32",
           "type": "integer"
          },
          "resources": {
           "description": "Compute Resources required by this component.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
           "properties": {
            "claims": {
             "description": "Claims lists the names of resources, defined in spec.resourceClaims,\nthat are used by this container.\n\n\nThis is an alpha field and requires enabling the\nDynamicResourceAllocation feature gate.\n\n\nThis field is immutable. It can only be set for containers.",
             "items": {
              "description": "ResourceClaim references one entry in PodSpec.ResourceClaims.",
              "properties": {
               "name": {
                "description": "Name must match the name of one entry in pod.spec.resourceClaims of\nthe Pod where this field is used. It makes that resource available\ninside a container.",
                "type": "string"
               }
              },
              "required": [
               "name"
              ],
              "type": "object"
             },
             "type": "array",
             "x-kubernetes-list-map-keys": [
              "name"
             ],
             "x-kubernetes-list-type": "map"
            },
            "limits": {
             "additionalProperties": {
              "anyOf": [
               {
                "type": "integer"
               },
               {
                "type": "string"
               }
              ],
              "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
              "x-kubernetes-int-or-string": true
             },
             "description": "Limits describes the maximum amount of compute resources allowed.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
             "type": "object"
            },
            "requests": {
             "additionalProperties": {
              "anyOf": [
               {
                "type": "integer"
               },
               {
                "type": "string"
               }
              ],
              "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
              "x-kubernetes-int-or-string": true
             },
             "description": "Requests describes the minimum amount of compute resources required.\nIf Requests is omitted for a container, it defaults to Limits if that is explicitly specified,\notherwise to an implementation-defined value. Requests cannot exceed Limits.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
             "type": "object"
            }
           },
           "type": "object"
          },
          "serverCertSANs": {
           "description": "ServerCertSANs sets extra Subject Alternative Names for the etcd server signing cert.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "volumeData": {
           "description": "VolumeData describes the settings of etcd data store.\nWe will support 3 modes: emptyDir, hostPath, PVC. default by hostPath.",
           "properties": {
            "emptyDir": {
             "description": "EmptyDir represents a temporary directory that shares a pod's lifetime.\nMore info: https://kubernetes.io/docs/concepts/storage/volumes#emptydir",
             "properties": {
              "medium": {
               "description": "medium represents what type of storage medium should back this directory.\nThe default is \"\" which means to use the node's default medium.\nMust be an empty string (default) or Memory.\nMore info: https://kubernetes.io/docs/concepts/storage/volumes#emptydir",
               "type": "string"
              },
              "sizeLimit": {
               "anyOf": [
                {
                 "type": "integer"
                },
                {
                 "type": "string"
                }
               ],
               "description": "sizeLimit is the total amount of local storage required for this EmptyDir volume.\nThe size limit is also applicable for memory medium.\nThe maximum usage on memory medium EmptyDir would be the minimum value between\nthe SizeLimit specified here and the sum of memory limits of all containers in a pod.\nThe default is nil which means that the limit is undefined.\nMore info: https://kubernetes.io/docs/concepts/storage/volumes#emptydir",
               "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
               "x-kubernetes-int-or-string": true
              }
             },
             "type": "object"
            },
            "hostPath": {
             "description": "HostPath represents a pre-existing file or directory on the host\nmachine that is directly exposed to the container. This is generally\nused for system agents or other privileged things that are allowed\nto see the host machine. Most containers will NOT need this.\nMore info: https://kubernetes.io/docs/concepts/storage/volumes#hostpath\n---\nTODO(jonesdl) We need to restrict who can use host directory mounts and who can/can not\nmount host directories as read/write.",
             "properties": {
              "path": {
               "description": "path of the directory on the host.\nIf the path is a symlink, it will follow the link to the real path.\nMore info: https://kubernetes.io/docs/concepts/storage/volumes#hostpath",
               "type": "string"
              },
              "type": {
               "description": "type for HostPath Volume\nDefaults to \"\"\nMore info: https://kubernetes.io/docs/concepts/storage/volumes#hostpath",
               "type": "string"
              }
             },
             "required": [
              "path"
             ],
             "type": "object"
            },
            "volumeClaim": {
             "description": "The specification for the PersistentVolumeClaim. The entire content is\ncopied unchanged into the PVC that gets created from this\ntemplate. The same fields as in a PersistentVolumeClaim\nare also valid here.",
             "properties": {
              "metadata": {
               "description": "May contain labels and annotations that will be copied into the PVC\nwhen creating it. No other fields are allowed and will be rejected during\nvalidation.",
               "properties": {
                "annotations": {
                 "additionalProperties": {
                  "type": "string"
                 },
                 "type": "object"
                },
                "finalizers": {
                 "items": {
                  "type": "string"
                 },
                 "type": "array"
                },
                "labels": {
                 "additionalProperties": {
                  "type": "string"
                 },
                 "type": "object"
                },
                "name": {
                 "type": "string"
                },
                "namespace": {
                 "type": "string"
                }
               },
               "type": "object"
              },
              "spec": {
               "description": "The specification for the PersistentVolumeClaim. The entire content is\ncopied unchanged into the PVC that gets created from this\ntemplate. The same fields as in a PersistentVolumeClaim\nare also valid here.",
               "properties": {
                "accessModes": {
                 "description": "accessModes contains the desired access modes the volume should have.\nMore info: https://kubernetes.io/docs/concepts/storage/persistent-volumes#access-modes-1",
                 "items": {
                  "type": "string"
                 },
                 "type": "array",
                 "x-kubernetes-list-type": "atomic"
                },
                "dataSource": {
                 "description": "dataSource field can be used to specify either:\n* An existing VolumeSnapshot object (snapshot.storage.k8s.io/VolumeSnapshot)\n* An existing PVC (PersistentVolumeClaim)\nIf the provisioner or an external controller can support the specified data source,\nit will create a new volume based on the contents of the specified data source.\nWhen the AnyVolumeDataSource feature gate is enabled, dataSource contents will be copied to dataSourceRef,\nand dataSourceRef contents will be copied to dataSource when dataSourceRef.namespace is not specified.\nIf the namespace is specified, then dataSourceRef will not be copied to dataSource.",
                 "properties": {
                  "apiGroup": {
                   "description": "APIGroup is the group for the resource being referenced.\nIf APIGroup is not specified, the specified Kind must be in the core API group.\nFor any other third-party types, APIGroup is required.",
                   "type": "string"
                  },
                  "kind": {
                   "description": "Kind is the type of resource being referenced",
                   "type": "string"
                  },
                  "name": {
                   "description": "Name is the name of resource being referenced",
                   "type": "string"
                  }
                 },
                 "required": [
                  "kind",
                  "name"
                 ],
                 "type": "object",
                 "x-kubernetes-map-type": "atomic"
                },
                "dataSourceRef": {
                 "description": "dataSourceRef specifies the object from which to populate the volume with data, if a non-empty\nvolume is desired. This may be any object from a non-empty API group (non\ncore object) or a PersistentVolumeClaim object.\nWhen this field is specified, volume binding will only succeed if the type of\nthe specified object matches some installed volume populator or dynamic\nprovisioner.\nThis field will replace the functionality of the dataSource field and as such\nif both fields are non-empty, they must have the same value. For backwards\ncompatibility, when namespace isn't specified in dataSourceRef,\nboth fields (dataSource and dataSourceRef) will be set to the same\nvalue automatically if one of them is empty and the other is non-empty.\nWhen namespace is specified in dataSourceRef,\ndataSource isn't set to the same value and must be empty.\nThere are three important differences between dataSource and dataSourceRef:\n* While dataSource only allows two specific types of objects, dataSourceRef\n  allows any non-core object, as well as PersistentVolumeClaim objects.\n* While dataSource ignores disallowed values (dropping them), dataSourceRef\n  preserves all values, and generates an error if a disallowed value is\n  specified.\n* While dataSource only allows local objects, dataSourceRef allows objects\n  in any namespaces.\n(Beta) Using this field requires the AnyVolumeDataSource feature gate to be enabled.\n(Alpha) Using the namespace field of dataSourceRef requires the CrossNamespaceVolumeDataSource feature gate to be enabled.",
                 "properties": {
                  "apiGroup": {
                   "description": "APIGroup is the group for the resource being referenced.\nIf APIGroup is not specified, the specified Kind must be in the core API group.\nFor any other third-party types, APIGroup is required.",
                   "type": "string"
                  },
                  "kind": {
                   "description": "Kind is the type of resource being referenced",
                   "type": "string"
                  },
                  "name": {
                   "description": "Name is the name of resource being referenced",
                   "type": "string"
                  },
                  "namespace": {
                   "description": "Namespace is the namespace of resource being referenced\nNote that when a namespace is specified, a gateway.networking.k8s.io/ReferenceGrant object is required in the referent namespace to allow that namespace's owner to accept the reference. See the ReferenceGrant documentation for details.\n(Alpha) This field requires the CrossNamespaceVolumeDataSource feature gate to be enabled.",
                   "type": "string"
                  }
                 },
                 "required": [
                  "kind",
                  "name"
                 ],
                 "type": "object"
                },
                "resources": {
                 "description": "resources represents the minimum resources the volume should have.\nIf RecoverVolumeExpansionFailure feature is enabled users are allowed to specify resource requirements\nthat are lower than previous value but must still be higher than capacity recorded in the\nstatus field of the claim.\nMore info: https://kubernetes.io/docs/concepts/storage/persistent-volumes#resources",
                 "properties": {
                  "limits": {
                   "additionalProperties": {
                    "anyOf": [
                     {
                      "type": "integer"
                     },
                     {
                      "type": "string"
                     }
                    ],
                    "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
                    "x-kubernetes-int-or-string": true
                   },
                   "description": "Limits describes the maximum amount of compute resources allowed.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
                   "type": "object"
                  },
                  "requests": {
                   "additionalProperties": {
                    "anyOf": [
                     {
                      "type": "integer"
                     },
                     {
                      "type": "string"
                     }
                    ],
                    "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
                    "x-kubernetes-int-or-string": true
                   },
                   "description": "Requests describes the minimum amount of compute resources required.\nIf Requests is omitted for a container, it defaults to Limits if that is explicitly specified,\notherwise to an implementation-defined value. Requests cannot exceed Limits.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
                   "type": "object"
                  }
                 },
                 "type": "object"
                },
                "selector": {
                 "description": "selector is a label query over volumes to consider for binding.",
                 "properties": {
                  "matchExpressions": {
                   "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
                   "items": {
                    "description": "A label selector requirement is a selector that contains values, a key, and an operator that\nrelates the key and values.",
                    "properties": {
                     "key": {
                      "description": "key is the label key that the selector applies to.",
                      "type": "string"
                     },
                     "operator": {
                      "description": "operator represents a key's relationship to a set of values.\nValid operators are In, NotIn, Exists and DoesNotExist.",
                      "type": "string"
                     },
                     "values": {
                      "description": "values is an array of string values. If the operator is In or NotIn,\nthe values array must be non-empty. If the operator is Exists or DoesNotExist,\nthe values array must be empty. This array is replaced during a strategic\nmerge patch.",
                      "items": {
                       "type": "string"
                      },
                      "type": "array",
                      "x-kubernetes-list-type": "atomic"
                     }
                    },
                    "required": [
                     "key",
                     "operator"
                    ],
                    "type": "object"
                   },
                   "type": "array",
                   "x-kubernetes-list-type": "atomic"
                  },
                  "matchLabels": {
                   "additionalProperties": {
                    "type": "string"
                   },
                   "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels\nmap is equivalent to an element of matchExpressions, whose key field is \"key\", the\noperator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
                   "type": "object"
                  }
                 },
                 "type": "object",
                 "x-kubernetes-map-type": "atomic"
                },
                "storageClassName": {
                 "description": "storageClassName is the name of the StorageClass required by the claim.\nMore info: https://kubernetes.io/docs/concepts/storage/persistent-volumes#class-1",
                 "type": "string"
                },
                "volumeAttributesClassName": {
                 "description": "volumeAttributesClassName may be used to set the VolumeAttributesClass used by this claim.\nIf specified, the CSI driver will create or update the volume with the attributes defined\nin the corresponding VolumeAttributesClass. This has a different purpose than storageClassName,\nit can be changed after the claim is created. An empty string value means that no VolumeAttributesClass\nwill be applied to the claim but it's not allowed to reset this field to empty string once it is set.\nIf unspecified and the PersistentVolumeClaim is unbound, the default VolumeAttributesClass\nwill be set by the persistentvolume controller if it exists.\nIf the resource referred to by volumeAttributesClass does not exist, this PersistentVolumeClaim will be\nset to a Pending state, as reflected by the modifyVolumeStatus field, until such as a resource\nexists.\nMore info: https://kubernetes.io/docs/concepts/storage/volume-attributes-classes/\n(Alpha) Using this field requires the VolumeAttributesClass feature gate to be enabled.",
                 "type": "string"
                },
                "volumeMode": {
                 "description": "volumeMode defines what type of volume is required by the claim.\nValue of Filesystem is implied when not included in claim spec.",
                 "type": "string"
                },
                "volumeName": {
                 "description": "volumeName is the binding reference to the PersistentVolume backing this claim.",
                 "type": "string"
                }
               },
               "type": "object"
              }
             },
             "required": [
              "spec"
             ],
             "type": "object"
            }
           },
           "type": "object"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
