schema:
{
 "description": "KeycloakClient is the Schema for the keycloak clients API.",
 "properties": {
  "spec": {
   "description": "KeycloakClientSpec defines the desired state of KeycloakClient.",
   "properties": {
    "advancedProtocolMappers": {
     "description": "AdvancedProtocolMappers is a flag to enable advanced protocol mappers.",
     "type": "boolean"
    },
    "attributes": {
     "additionalProperties": {
      "type": "string"
     },
     "default": {
      "post.logout.redirect.uris": "+"
     },
     "description": "Attributes is a map of client attributes.",
     "nullable": true,
     "type": "object"
    },
    "authorizationServicesEnabled": {
     "description": "ServiceAccountsEnabled enable/disable fine-grained authorization support for a client.",
     "type": "boolean"
    },
    "bearerOnly": {
     "description": "BearerOnly is a flag to enable bearer-only.",
     "type": "boolean"
    },
    "clientAuthenticatorType": {
     "default": "client-secret",
     "description": "ClientAuthenticatorType is a client authenticator type.",
     "type": "string"
    },
    "clientId": {
     "description": "ClientId is a unique keycloak client ID referenced in URI and tokens.",
     "type": "string"
    },
    "clientRoles": {
     "description": "ClientRoles is a list of client roles names assigned to client.",
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "consentRequired": {
     "description": "ConsentRequired is a flag to enable consent.",
     "type": "boolean"
    },
    "defaultClientScopes": {
     "description": "DefaultClientScopes is a list of default client scopes assigned to client.",
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "description": {
     "description": "Description is a client description.",
     "type": "string"
    },
    "directAccess": {
     "description": "DirectAccess is a flag to set client as direct access.",
     "type": "boolean"
    },
    "enabled": {
     "default": true,
     "description": "Enabled is a flag to enable client.",
     "type": "boolean"
    },
    "frontChannelLogout": {
     "description": "FrontChannelLogout is a flag to enable front channel logout.",
     "type": "boolean"
    },
    "fullScopeAllowed": {
     "default": true,
     "description": "FullScopeAllowed is a flag to enable full scope.",
     "type": "boolean"
    },
    "implicitFlowEnabled": {
     "description": "ImplicitFlowEnabled is a flag to enable support for OpenID Connect redirect based authentication without authorization code.",
     "type": "boolean"
    },
    "name": {
     "description": "Name is a client name.",
     "type": "string"
    },
    "protocol": {
     "description": "Protocol is a client protocol.",
     "nullable": true,
     "type": "string"
    },
    "protocolMappers": {
     "description": "ProtocolMappers is a list of protocol mappers assigned to client.",
     "items": {
      "properties": {
       "config": {
        "additionalProperties": {
         "type": "string"
        },
        "description": "Config is a map of protocol mapper configuration.",
        "nullable": true,
        "type": "object"
       },
       "name": {
        "description": "Name is a protocol mapper name.",
        "type": "string"
       },
       "protocol": {
        "description": "Protocol is a protocol name.",
        "type": "string"
       },
       "protocolMapper": {
        "description": "ProtocolMapper is a protocol mapper name.",
        "type": "string"
       }
      },
      "type": "object"
     },
     "nullable": true,
     "type": "array"
    },
    "public": {
     "description": "Public is a flag to set client as public.",
     "type": "boolean"
    },
    "realmRef": {
     "description": "RealmRef is reference to Realm custom resource.",
     "properties": {
      "kind": {
       "description": "Kind specifies the kind of the Keycloak resource.",
       "enum": [
        "KeycloakRealm",
        "ClusterKeycloakRealm"
       ],
       "type": "string"
      },
      "name": {
       "description": "Name specifies the name of the Keycloak resource.",
       "type": "string"
      }
     },
     "type": "object"
    },
    "realmRoles": {
     "description": "RealmRoles is a list of realm roles assigned to client.",
     "items": {
      "properties": {
       "composite": {
        "description": "Composite is a realm composite role name.",
        "type": "string"
       },
       "name": {
        "description": "Name is a realm role name.",
        "type": "string"
       }
      },
      "required": [
       "composite"
      ],
      "type": "object"
     },
     "nullable": true,
     "type": "array"
    },
    "reconciliationStrategy": {
     "description": "ReconciliationStrategy is a strategy to reconcile client.",
     "enum": [
      "full",
      "addOnly"
     ],
     "type": "string"
    },
    "redirectUris": {
     "description": "RedirectUris is a list of valid URI pattern a browser can redirect to after a successful login. Simple wildcards are allowed such as 'https://example.com/*'. Relative path can be specified too, such as /my/relative/path/*. Relative paths are relative to the client root URL. If not specified, spec.webUrl + \"/*\" will be used.",
     "example": [
      "https://example.com/*",
      "/my/relative/path/*"
     ],
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "secret": {
     "description": "Secret is kubernetes secret name where the client's secret will be stored. Secret should have the following format: $secretName:secretKey. If not specified, a client secret will be generated and stored in a secret with the name keycloak-client-{metadata.name}-secret. If keycloak client is public, secret property will be ignored.",
     "example": "$keycloak-secret:client_secret",
     "type": "string"
    },
    "serviceAccount": {
     "description": "ServiceAccount is a service account configuration.",
     "nullable": true,
     "properties": {
      "attributes": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Attributes is a map of service account attributes.",
       "nullable": true,
       "type": "object"
      },
      "clientRoles": {
       "description": "ClientRoles is a list of client roles assigned to service account.",
       "items": {
        "properties": {
         "clientId": {
          "description": "ClientID is a client ID.",
          "type": "string"
         },
         "roles": {
          "description": "Roles is a list of client roles names assigned to service account.",
          "items": {
           "type": "string"
          },
          "nullable": true,
          "type": "array"
         }
        },
        "required": [
         "clientId"
        ],
        "type": "object"
       },
       "nullable": true,
       "type": "array"
      },
      "enabled": {
       "description": "Enabled is a flag to enable service account.",
       "type": "boolean"
      },
      "realmRoles": {
       "description": "RealmRoles is a list of realm roles assigned to service account.",
       "items": {
        "type": "string"
       },
       "nullable": true,
       "type": "array"
      }
     },
     "type": "object"
    },
    "standardFlowEnabled": {
     "default": true,
     "description": "StandardFlowEnabled is a flag to enable standard flow.",
     "type": "boolean"
    },
    "surrogateAuthRequired": {
     "description": "SurrogateAuthRequired is a flag to enable surrogate auth.",
     "type": "boolean"
    },
    "targetRealm": {
     "description": "Deprecated: use RealmRef instead. TargetRealm is a realm name where client will be created. It has higher priority than RealmRef for backward compatibility. If both TargetRealm and RealmRef are specified, TargetRealm will be used for client creation.",
     "type": "string"
    },
    "webOrigins": {
     "description": "WebOrigins is a list of allowed CORS origins. To permit all origins of Valid Redirect URIs, add '+'. This does not include the '*' wildcard though. To permit all origins, explicitly add '*'. If not specified, the value from `WebUrl` is used",
     "example": [
      "https://example.com/*"
     ],
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "webUrl": {
     "description": "WebUrl is a client web url.",
     "type": "string"
    }
   },
   "required": [
    "clientId"
   ],
   "type": "object"
  }
 },
 "title": "Keycloak Client",
 "type": "object"
}
